INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1021, 3, 'Kalusteet, hyllyt', NULL, 'Tukeva kiinnitys ja korotettu reuna estävät tavaroiden putoamisen hyllyltä', '3,7,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (968, 3, 'Kalusteet, kaappi, etäisyys sivuseinästä', 'Syvyyden (A) vertailuarvona syvyys (B) 2. lähteestä. Kaappiin ulottumista helpottaa, kun ovi aukeaa yli 90 astetta. Avaamiseen tarvittava vapaa tila kaapin edessä 1300 mm.', 'Arvo A on vähintään 200 mm. Arvo B on vähintään 400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (564, 3, 'Kalusteet, ovi, avautuminen', NULL, 'Ovien avautumiskulma 180º, jolloin törmäysvaara pienenee.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (562, 3, 'Kalusteet, ovi, leveys', 'Ovileveys (C). Leveät ovet aiheuttavat törmäysvaaran. Törmäysvaara voidaan poistaa saranoimalla leveät ovet useampaan osaan taittuviksi tai käyttämällä liuku- ja rulo-ovia.', 'Arvo C on enintään 400 mm.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (765, 3, 'Kalusteet, ovi, rakenne', NULL, 'Oveen voidaan kiinnittää esim. lankakoreja ja koukkuja, jolloin tavarat ovat helposti ulottuvilla.', '8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (764, 3, 'Kalusteet, ovi, saranat', NULL, 'Saranoiden avautumiskulman on oltava 180 astetta, jolloin törmäysvaara pienenee. Saranaovien poisto on oltava helppoa, mutta ne eivät saa irrota paikoiltaan tahattomasta tönäisystä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (523, 3, 'Kalusteet, taso/pöytä, polvitilan korkeus', NULL, 'Polvitilan korkeus on 150 mm työtuolin korkeutta suurempi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (519, 3, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 450 mm. Arvo C on välillä 600 ... 650 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1242, 3, 'Ovi, kynnyskorkeus, asunto', 'Kynnyksen korkeus (C). Asunnoissa ja kulkuväylillä suositellaan kynnyksettömiä ovia.', 'Arvo C on enintään 20 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1064, 3, 'Valaistus, asuinhuone', NULL, 'Valaistusvoimakkuus asuinhuoneessa on 100 lx. Sopiva yleisvalaistuksen voimakkuus 150 -300 lx ( 2. lähde).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1065, 3, 'Valaistus, kohdevalaistus', NULL, 'Valaistusvoimakkuus työskentelyyn kirjoituspöydällä on 750 lx.  Sopiva  valaistusvoimakkuus lähityöskentelyssä 400 - 1000 lx (2. lähde).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (986, 4, 'Kalusteet, hyllyt', NULL, 'Hyllyjen on oltava ulosvedettäviä, pysäyttäjillä varustettuja ja sijoitettavissa halutulle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (521, 4, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (864, 4, 'Valaistus, asuinhuone', NULL, 'Valaistusvoimakkuus asuinhuoneessa on 150 lx. Sopiva yleisvalaistuksen voimakkuus 150 -300 lx ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (464, 4, 'Valaistus, kohdevalaistus', NULL, 'Valaistusvoimakkuus työskentelyyn kirjoituspöydällä on 1000 lx.  Sopiva  valaistusvoimakkuus lähityöskentelyssä 400 - 1000 lx (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (763, 5, 'Kalusteet', NULL, 'Kalusteiden paikkaa tai korkeutta voidaan säätää sähköisesti tai kiskokiinnityksellä.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1232, 2, 'Ovi, oviaukko, vapaa leveys, ulko-ovi', NULL, 'Asuntojen ulko- ja porrastaso-oviksi suositellaan leveydeltään 10 M-ovia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1042, 2, 'Valaistus, kulkuväylä sisällä', 'Valaistusvoimakkuus (C) eteisissä, auloissa ja käytävillä.', 'Arvo C on välillä 80 ... 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1021, 3, 'Kalusteet, hyllyt', NULL, 'Tukeva kiinnitys ja korotettu reuna estävät tavaroiden putoamisen hyllyltä', '3,7,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (968, 3, 'Kalusteet, kaappi, etäisyys sivuseinästä', 'Syvyyden (A) vertailuarvona syvyys (B) 2. lähteestä. Kaappiin ulottumista helpottaa, kun ovi aukeaa yli 90 astetta. Avaamiseen tarvittava vapaa tila kaapin edessä 1300 mm.', 'Arvo A on vähintään 200 mm. Arvo B on vähintään 400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (564, 3, 'Kalusteet, ovi, avautuminen', NULL, 'Ovien avautumiskulma 180º, jolloin törmäysvaara pienenee.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (562, 3, 'Kalusteet, ovi, leveys', 'Ovileveys (C). Leveät ovet aiheuttavat törmäysvaaran. Törmäysvaara voidaan poistaa saranoimalla leveät ovet useampaan osaan taittuviksi tai käyttämällä liuku- ja rulo-ovia.', 'Arvo C on enintään 400 mm.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (765, 3, 'Kalusteet, ovi, rakenne', NULL, 'Oveen voidaan kiinnittää esim. lankakoreja ja koukkuja, jolloin tavarat ovat helposti ulottuvilla.', '8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (764, 3, 'Kalusteet, ovi, saranat', NULL, 'Saranoiden avautumiskulman on oltava 180 astetta, jolloin törmäysvaara pienenee. Saranaovien poisto on oltava helppoa, mutta ne eivät saa irrota paikoiltaan tahattomasta tönäisystä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (580, 3, 'Kalusteet, siivousvälinekaappi', 'Kaapin leveys (A). Kaappi, josta sokkeli on poistettu pölynimurin liikuttelun helpottamiseksi.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (579, 3, 'Kalusteet, siivousvälinekaappi, silityslauta', NULL, 'Keittiön tai apukeittiön kaapistoon sijoitetaan silityskaappi, josta silityslauta voidaan kääntää ulos jalattomana ulokkeena halutulle korkeudelle. Tällöin sitä on helppo käyttää istuen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (852, 3, 'Ovi, aukipysymisaika', 'Aukipysymisaikaa (A) säätäessä otetaan huomioon hitaasti liikkuvat henkilöt. Mikäli ovi avataan painonappia tai painiketta käyttäen automaattisesti, riippuu tarvittava aika painikkeen ja oven välisestä etäisyydestä.', 'Arvo A on vähintään 25 s.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1242, 3, 'Ovi, kynnyskorkeus, asunto', 'Kynnyksen korkeus (C). Asunnoissa ja kulkuväylillä suositellaan kynnyksettömiä ovia.', 'Arvo C on enintään 20 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (702, 3, 'Seinä, varusteet, peiliseinä', NULL, 'Hahmottamisen ja häikäisyn takia ei peiliseiniä kulkuväylille.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1011, 3, 'Valaistus, eteinen', NULL, 'Valaistusvoimakkuus eteisessä on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (785, 3, 'Valaistus, sisäänkäynti', NULL, 'Valaistuksella tasoitetaan valaistuseroja sisä- ja ulkotilojen välillä ja estetään häikäistyminen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (458, 4, 'Eteinen, kalusteet, naulakko', 'Pyörätuolin käyttäjälle soveltuvan naulakon korkeus (C). Sokkelittomaan naulakkoon on helpompi ulottua pyörätuolista kuin sokkelilla varustettuun.', 'Arvo C on välillä 1100 ... 1200 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (986, 4, 'Kalusteet, hyllyt', NULL, 'Hyllyjen on oltava ulosvedettäviä, pysäyttäjillä varustettuja ja sijoitettavissa halutulle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1067, 4, 'Kalusteet, vaatekaappi', 'Vaatekaapin leveys (A). Leveä vaatekaappi parantaa ulottuvuutta. Jos kaappi on kapeampi, silloin hyllyjen ja tankojen tulisi olla ulosvedettäviä.', 'Arvo A on vähintään 800 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1010, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, asunto', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1400 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (453, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, avustaja', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 1400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1140, 4, 'Pyörätuolin pesutila, asunto', NULL, 'Käynti pesutilaan suoraan ulkoa tai pesutila sijoitetaan sisäänkäynnin lähelle esim. kuraeteiseen tai  WC-tilaan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (370, 4, 'Pyörätuolin pesutila, asunto, lattiakaivo', NULL, 'Irrallisella hiekkasuodattimella varustettu lattiakaivo WC:ssä pyörätuolin pesua varten, jos ei ole erillistä pesupaikkaa.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (793, 4, 'Valaistus, eteinen', NULL, 'Valaistusvoimakkuus eteisessä on 150 lx.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (763, 5, 'Kalusteet', NULL, 'Kalusteiden paikkaa tai korkeutta voidaan säätää sähköisesti tai kiskokiinnityksellä.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (515, 2, 'Keittiö, kalusteet, työtaso uunin vieressä, leveys', 'Uunin vieressä olevan laskutason leveys (A). Ellei uunin vieressä ole työtasoa, laskutaso voi olla kaapistosta ulosvedettävä aputaso, joka on samalla korkeudella avatun uuniluukun kanssa.', 'Arvo A on vähintään 400 mm.', '14,3,7,8,11,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (568, 2, 'Keittiö, laitteet, jääkaappi/pakastin, ovi', NULL, 'Magneettisalvalla varustettu ovi, joka avautuu ja sulkeutuu helposti ja kevyesti.', '8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (481, 2, 'Keittiö, tyyppi, II-keittiö, mitoitus, sisäänvedetty sokkeli', 'Kalusteryhmien välinen etäisyys (A).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (476, 2, 'Keittiö, tyyppi, mitoitus', NULL, 'Kalusteryhmien etäisyydet mitoitetaan niin, että tilaa on riittävästi apuvälineiden kanssa liikkumiseen. Jos (perhe)keittiö on samalla läpikulkutila, mitoituksessa on huomioitava ohituksen tilantarve.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (537, 2, 'Keittiö, vesikaluste, materiaali', NULL, 'Vesikaluste ei saa aiheuttaa allergista reaktiota (esim. kromi).', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (535, 2, 'Keittiö, vesikaluste, sekoittaja, vesiputki', NULL, 'Pitkä ja korkea hanan juoksuputki mahdollistaa astian täyttämisen pöytätasolla. Juoksuputken on käännyttävä pöytätason yläpuolelle.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1021, 3, 'Kalusteet, hyllyt', NULL, 'Tukeva kiinnitys ja korotettu reuna estävät tavaroiden putoamisen hyllyltä', '3,7,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (968, 3, 'Kalusteet, kaappi, etäisyys sivuseinästä', 'Syvyyden (A) vertailuarvona syvyys (B) 2. lähteestä. Kaappiin ulottumista helpottaa, kun ovi aukeaa yli 90 astetta. Avaamiseen tarvittava vapaa tila kaapin edessä 1300 mm.', 'Arvo A on vähintään 200 mm. Arvo B on vähintään 400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (564, 3, 'Kalusteet, ovi, avautuminen', NULL, 'Ovien avautumiskulma 180º, jolloin törmäysvaara pienenee.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (562, 3, 'Kalusteet, ovi, leveys', 'Ovileveys (C). Leveät ovet aiheuttavat törmäysvaaran. Törmäysvaara voidaan poistaa saranoimalla leveät ovet useampaan osaan taittuviksi tai käyttämällä liuku- ja rulo-ovia.', 'Arvo C on enintään 400 mm.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (765, 3, 'Kalusteet, ovi, rakenne', NULL, 'Oveen voidaan kiinnittää esim. lankakoreja ja koukkuja, jolloin tavarat ovat helposti ulottuvilla.', '8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (764, 3, 'Kalusteet, ovi, saranat', NULL, 'Saranoiden avautumiskulman on oltava 180 astetta, jolloin törmäysvaara pienenee. Saranaovien poisto on oltava helppoa, mutta ne eivät saa irrota paikoiltaan tahattomasta tönäisystä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (580, 3, 'Kalusteet, siivousvälinekaappi', 'Kaapin leveys (A). Kaappi, josta sokkeli on poistettu pölynimurin liikuttelun helpottamiseksi.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (579, 3, 'Kalusteet, siivousvälinekaappi, silityslauta', NULL, 'Keittiön tai apukeittiön kaapistoon sijoitetaan silityskaappi, josta silityslauta voidaan kääntää ulos jalattomana ulokkeena halutulle korkeudelle. Tällöin sitä on helppo käyttää istuen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (523, 3, 'Kalusteet, taso/pöytä, polvitilan korkeus', NULL, 'Polvitilan korkeus on 150 mm työtuolin korkeutta suurempi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (519, 3, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 450 mm. Arvo C on välillä 600 ... 650 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (528, 3, 'Keittiö, kalusteet, astianpesupöytä, allas', NULL, 'Pesu- ja huuhteluallas vierekkäin, jolloin niihin on helppo ulottua.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1019, 3, 'Keittiö, kalusteet, astianpesupöytä, altaan syvyys', 'Altaan syvyysmitta (A).', 'Astianpesualtaan normaali syvyys on 150 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1020, 3, 'Keittiö, kalusteet, astianpesupöytä, korkeus', 'Korkeusmitta (C).', 'Arvo C on välillä 850 ... 900 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (530, 3, 'Keittiö, kalusteet, astianpesupöytä, reunan korkeus', 'Reunan korkeusmitta (C). Korkea astianpesupöydän reuna, joka estää veden valumisen lattialle, hankaloittaa istuen työskentelevää.', 'Arvo C on enintään 10 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (529, 3, 'Keittiö, kalusteet, astianpesupöytä, tason leveys', 'Laskutason leveysmitta (C). Astianpesupöydän laskutason leveys riippuu ruokakunnan koosta ja työtason tarpeesta.Tukitanko pöydän etureunassa helpottaa liikkumista.', 'Arvo C on välillä 400 ... 600 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (554, 3, 'Keittiö, kalusteet, jätekaappi', NULL, 'Pöytätason alla oleva ulosvedettävä jätekaappi tai kiskoilla liikkuvat jätesäiliöt.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1277, 3, 'Keittiö, kalusteet, kaapit', NULL, 'Kaapit voidaan kiinnittää esim. kisko- ja koukkukiinnityksin, jolloin niiden korkeutta voidaan muuttaa asukkaan tarpeen mukaan.', '15,8,14,16,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1022, 3, 'Keittiö, kalusteet, seinäkaappi, korkeus työtason pinnasta', 'Yläkaappien asennuskorkeus (C).', 'Arvo C on välillä 370 ... 500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (488, 3, 'Keittiö, kalusteet, tukitanko', NULL, 'Tukitanko pöydän etureunassa helpottaa liikkumista.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1135, 3, 'Keittiö, kalusteet, työtaso lieden vieressä, leveys', 'Työtason leveys (A). Lieden molemmin puolin tarvitaan vapaata laskutilaa. Ainakin toinen laskutila on kuumuutta kestävää materiaalia.', 'Arvo A on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1182, 3, 'Keittiö, kalusteet, työtaso, korkeus', 'Työtason korkeus lattiasta (C).', 'Arvo C on välillä 750 ... 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1099, 3, 'Keittiö, kalusteet, työtaso, sijoitus', NULL, 'Keskeisimmän työtason paras sijotus on lieden ja astianpesupöydän välissä. Työtason suositeltava leveys on 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (969, 3, 'Keittiö, laitteet, etäisyys sivuseinästä', 'Etäisyyden (A) vertailuarvona etäisyys (B) 2. lähteestä. Jääkaappiin ym. ulottumista helpottaa, kun ovi aukeaa yli 90 astetta.', 'Arvo A on vähintään 200 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1033, 3, 'Keittiö, laitteet, jääkaappi/pakastin, hyllyt', NULL, 'Hyllyt on kiinnitetty tukevasti.', '3,7,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (506, 3, 'Keittiö, laitteet, liesi', NULL, 'Perinteisen lieden levyt ovat helpompi havaita kuin keraaminen taso.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (508, 3, 'Keittiö, laitteet, liesi, korkeus', NULL, 'Samalla korkeudella työtason kanssa, jotta astiat voidaan vetää liedeltä niitä nostamatta.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (574, 3, 'Keittiö, laitteet, säiliöpakastin', NULL, 'Säiliöpakastin ei sovi liikkumisesteisille ulottuvuusvaikeuksien takia. Se on hankala myös näkövammaisille, koska järjestystä on vaikea säilyttää.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1037, 3, 'Keittiö, laitteet, uuni, korkeus lattiasta', 'Uuninluukun alareunan korkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1200 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (516, 3, 'Keittiö, laitteet, uuninluukku', NULL, 'Turvallisessa uuninluukussa on turvalukko ja iso, käteensopiva vedin. Luukku on sivusaranoitu ja se aukeaa 180 astetta.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (487, 3, 'Keittiö, mitoitus', NULL, 'Säilytystilat jäsennetään pieniin yksiköihin.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1018, 3, 'Keittiö, mitoitus', NULL, 'Mitoituksessa on huomioitava yksilölliset vaatimukset ja apuvälineiden tilantarve.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1141, 3, 'Keittiö, pistorasia', NULL, 'Pienkoneiden käyttöä helpottaa lieden lähelle työtason etureunaan sijoitetut pistorasiat.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (477, 3, 'Keittiö, tyyppi, I-keittiö', NULL, 'I-keittiön pinta-ala ja etäisyydet työpisteiden välillä kasvavat suuriksi perhekeittiöissä. Tällöin keittiötyyppi aiheuttaa esineiden siirtelyä ja kuljettamista, mikä on hankalaa apuvälineen kanssa.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (533, 3, 'Keittiö, vesikaluste, sekoittaja', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1123, 3, 'Keittiö, vesikaluste, sekoittaja, sijoitus', NULL, 'Sekoittajaan on ulotuttava kurkottamatta. Ulottumisen helpottamiseksi käytetään tavallista pidempää vipua tai sijoitetaan sekoittaja pesualtaan etuosaan.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1242, 3, 'Ovi, kynnyskorkeus, asunto', 'Kynnyksen korkeus (C). Asunnoissa ja kulkuväylillä suositellaan kynnyksettömiä ovia.', 'Arvo C on enintään 20 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1039, 3, 'Valaistus, keittiö', NULL, 'Valaistusvoimakkuus keittiössä on 200 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1024, 3, 'Valaistus, keittiö, kohdevalaistus', NULL, 'Valaistusvoimakkuus keittiössä työtasoilla on 300 lx. Sopiva  valaistusvoimakkuus lähityöskentelyssä 400 - 1000 lx (2. lähde).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (986, 4, 'Kalusteet, hyllyt', NULL, 'Hyllyjen on oltava ulosvedettäviä, pysäyttäjillä varustettuja ja sijoitettavissa halutulle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (525, 4, 'Kalusteet, polvitilan vesijohdot', NULL, 'Polvitilassa olevat kuumavesiputket yms. asennetaan lähelle seinää ja suojataan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (550, 4, 'Kalusteet, pöytäkaappi, laatikosto', NULL, 'Osa pöytäkaapeista voidaan korvata liikuteltavilla laatikostoilla.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (521, 4, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (503, 4, 'Keittiö, kalusteet, aputaso', NULL, 'Aputaso on kalusteista ulosvedettävä levy, joka toimii lisätasona ruoan valmistuksessa ja astioiden siirtämisessä.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (505, 4, 'Keittiö, kalusteet, aputaso, korkeus', 'Korkeus lattiasta (A).', 'Aputason korkeus on vähintään 600 mm lattiasta tai enintään 50 mm työtason alapuolella.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1163, 4, 'Keittiö, kalusteet, aputaso, mitoitus', 'Pyörätuolia käyttävälle henkilölle suositeltavat aputason mitat. Mitat on esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 500 mm. Arvo B on vähintään 300 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (527, 4, 'Keittiö, kalusteet, astianpesupöytä, altaan syvyys', NULL, 'Suositeltava altaan syvyys on 125 mm, koska liian syvä allas vaikeuttaa astioiden käsittelyä. Pyörätuolilla pääsee lähemmäksi allasta, jos sen pohja on vino.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (517, 4, 'Keittiö, kalusteet, astianpesupöytä, korkeus', 'Korkeussmitta (C). Pesualtaiden alla siirrettävä laatikosto, jolloin pyörätuolin käyttäjä saa jalkatilaa.', 'Arvo C on välillä 800 ... 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (531, 4, 'Keittiö, kalusteet, astianpesupöytä, reunan leveys', 'Reunan leveysmitta (C). Leveä reuna astianpesualtaiden kohdalla vaikeuttaa altaaseen ulottumista.', 'Arvo C on enintään 75 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1309, 4, 'Keittiö, kalusteet, korkeussäädettävä', 'Liikkumisesteiselle suositellaan korkeussäädettäviä kalusteita, jotka voi säätää käyttäjälle sopivalle korkeudelle.', '', '15,14,16,11,15,14,16,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (555, 4, 'Keittiö, kalusteet, seinäkaappi, korkeus työtason pinnasta', 'Yläkaappien asennuskorkeus (C) määräytyy yksilöllisen ulottuvuuden mukaan.', 'Arvo C on välillä 250 ... 300 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1023, 4, 'Keittiö, kalusteet, työtaso, korkeus', 'Työtason korkeus lattiasta (C).', 'Arvo C on välillä 850 ... 900 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (495, 4, 'Keittiö, kalusteet, työtaso, mitoitus', 'Työtason mitat esitetty leveyden (A) ja korkeuden (C) avulla. Liikuntaesteisen keittiössä tarvitaan työtason alla polvitilaa.', 'Arvo A on vähintään 800 mm. Arvo C on välillä 700 ... 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (569, 4, 'Keittiö, laitteet, jääkaappi/pakastin, korkeus lattiasta', 'Sijoituskorkeus (C) lattiasta.', 'Jääkaappi/pakastimen sopiva sijoituskorkeus on 400 mm lattiasta. Kun jääkaapi sijoitetaan sokkelille, niin alimmatkin hyllyt ovat ulottuvilla.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (510, 4, 'Keittiö, laitteet, liesi, polvitila', 'Mitoitus esitetty leveyden (A) avulla. Polvitila on tasolieden vieressä tai osittain sen alla.', 'Arvo A on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (575, 4, 'Valaistus, keittiö', NULL, 'Valaistusvoimakkuus keittiössä on 300 lx. Sopiva yleisvalaistuksen voimakkuus 150 - 300 lx ( 2. lähde)', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (577, 4, 'Valaistus, keittiö, kohdevalaistus', NULL, 'Valaistusvoimakkuus keittiössä työtasoilla on 500 lx. Sopiva  valaistusvoimakkuus lähityöskentelyssä 400 - 1000 lx (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (763, 5, 'Kalusteet', NULL, 'Kalusteiden paikkaa tai korkeutta voidaan säätää sähköisesti tai kiskokiinnityksellä.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (560, 5, 'Keittiö, kalusteet, seinäkaappi, hyllyt', NULL, 'Tehdään ritilästä tai läpinäkyvästä materiaalista sisällön näkyvyyden vuoksi. Hyllyn yläpuolelle voi kiinnittää peilin, jotta hyllyn sisältö näkyy.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (489, 5, 'Keittiö, kalusteet, väri', NULL, 'Suositeltava keittiökalusteiden väritys on sellainen, jossa on tummat alakaapit, vaaleat työtasot ja vaaleat yläkaapit.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1032, 5, 'Keittiö, laitteet, astianpesukone, korkeus lattiasta', 'Sijoituskorkeus (A) lattiasta. Jos astianpesukone on sijoitettu liian alas, on astioiden nostaminen ja koneeseen laittaminen vaikeaa.', 'Arvo A on vähintään 400 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (538, 5, 'Keittiö, laitteet, astianpesukone, sijoitus', NULL, 'Astianpesukonetta ei voi sijoittaa astianpesualtaiden kohdalle, koska altaiden alla tarvitaan polvitilaa. Pyörätuolin käyttäjä ei ulotu täyttämään leveää konetta kokonaan samalta puolelta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1142, 5, 'Keittiö, laitteet, astianpesukone, tyyppi', NULL, 'Pöydälle sijoitettavaan astianpesukoneeseen on helppo ulottua.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (572, 5, 'Keittiö, laitteet, jääkaappi/pakastin', NULL, 'Yhdistelmäkaappi (pakastin ja jääkaappi päällekkäin) soveltuu huonosti liikuntaesteiselle, koska alimmat ja  ylimmät hyllyt ovat ulottumattomissa.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (570, 5, 'Keittiö, laitteet, jääkaappi/pakastin, hyllyt', NULL, 'Ulosvedettävät hyllyt parantavat ulottuvuutta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1034, 5, 'Keittiö, laitteet, liesi', NULL, 'Erillinen tasoliesi jättää polvitilaa työtason alle ja mahdollistaa astioiden siirtelyn työtasoa pitkin. Kaapistoon upotettu uuni on hyvällä työskentelykorkeudella.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1035, 5, 'Keittiö, laitteet, liesi, ajastin', NULL, 'Ajastimella varustettu liesi katkaisee levyjen virran ja estää tulipalovaaran.', '25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (512, 5, 'Keittiö, laitteet, uuni', NULL, 'Erillinen kaapistoon upotettu uuni on hyvällä työskentelykorkeudella ja vapauttaa polvitilaa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (513, 5, 'Keittiö, laitteet, uuni, korkeus lattiasta', 'Uuninluukun alareunan korkeus (C) lattiasta.', 'Arvo C on välillä 700 ... 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1036, 5, 'Keittiö, laitteet, uuni, lamppu', NULL, 'Uunin lampun tulisi olla sivulla tai ylhäällä häikäistymisen ehkäisemiseksi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (536, 5, 'Keittiö, säätimet', NULL, 'Säätimien numerot ja kirjaimet selkeitä ja isokokoisia, luettavissa myös sormin tunnustellen.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (192, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, kulman avartaminen', NULL, 'Kulkuväylän (leveys  900 mm) kääntymiskulmaa avarretaan niin, että kulman mitat ovat 1150 x 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (251, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, leveys', 'Kulkuväylän leveys (A) ja (B) kulkuväylän kääntyessä.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (833, 4, 'Liikkumisen tilantarve, kulkuväylä kääntyy, yhteenlaskettu leveys', 'Risteävien kulkuväylien a ja b yhteen laskettu leveys (A). Kulkusuunnan kääntyessä 90 astetta, a:n ja b:n leveys vähintään 900 mm.', 'Arvo A on vähintään 2300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (190, 4, 'Liikkumisen tilantarve, pyörätuolien kohdatessa', 'Kulkuväylän leveyden (A) vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1213, 1, 'Luiska, pituus', 'Luiska, jonka kaltevuus on 5 - 8 %, saa olla pituudeltaan (C) yhtäjaksoisena enintään kuusi metriä, jonka jälkeen tulee olla välitasanne.', 'Arvo C on enintään 6000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1217, 1, 'Luiska, pituuskaltevuus', NULL, 'Portaan kaltevuuden mukainen, yli yhden askelman pituinen ns. lastenvaunuluiska ei ole turvallinen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (275, 1, 'Luiska, pituuskaltevuus, ei välitasannetta', 'Ilman välitasanteita olevan luiskan kaltevuus (C). Vaikka luiska olisi loiva (5%), suositellaan välitasanteita korkeuseroille 480 mm, 960 mm jne.', 'Arvo C on enintään 5 %.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (276, 1, 'Luiska, pituuskaltevuus, välitasanne', 'Luiska saa olla kaltevuudeltaan enintään 8 % (1:12,5) ja pituudeltaan yhtäjaksoisena enintään kuusi metriä, jonka jälkeen kulkuväylällä tulee olla vaakasuora vähintään 2000 mm:n pituinen välitasanne.', '', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (319, 1, 'Luiska, välitasanne, pituus', 'Luiska saa olla kaltevuudeltaan enintään 8 % (1:12,5) ja pituudeltaan yhtäjaksoisena enintään kuusi metriä. Sen jälkeen kulkuväylällä tulee olla vaakasuora vähintään 2000 mm:n pituinen välitasanne (A).', 'Arvo A on vähintään 2000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1223, 1, 'Luiska/portaat, käsijohde', NULL, 'Käsijohde tai kädensija asennetaan kaikkiin portaisiin ja luiskiin, julkisissa tiloissa molemmin puolin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1224, 1, 'Luiska/portaat,käsijohde, pituus', NULL, 'Portaassa ja luiskassa käsijohde on asennettava koko pituudelle. Johteiden tulee jatkua yhtenäisinä myös välitasanteiden osuudella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (954, 2, 'Luiska, sijoitus', NULL, 'Luiska johtaa aina tasanteelta tasanteelle. Se ei saa lähteä esim. suoraan ovelta ilman tasannetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (274, 2, 'Luiska, sivukaltevuus', NULL, 'Luiskassa ei saa olla sivukaltevuutta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1218, 2, 'Luiska, tasoero', 'Luiskaa ei suositella ainoaksi kulkuväyläksi yli metrin tasoerolle (C).', 'Arvo C on enintään 1000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1214, 2, 'Luiska, välitasanne', NULL, 'Luiskan tulee olla suora kulkusuunnassa, käännöskohdissa tulee olla välitasanne.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1215, 2, 'Luiska, ylätasanne', 'Ylätasanteen syvyys (A), jotta ovi mahtuu aukeamaan ja tasanteella mahtuu kääntymään.', 'Arvo A on vähintään 1800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1027, 2, 'Painike/vedin, ovi, etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys (A) alasjohtavasta portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1042, 2, 'Valaistus, kulkuväylä sisällä', 'Valaistusvoimakkuus (C) eteisissä, auloissa ja käytävillä.', 'Arvo C on välillä 80 ... 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (895, 3, 'Kulkuväylä, pinta, väri', NULL, 'Kulkuväylän tai yksityiskohdan (esim. jalkalistan) väri- ja tummuuskontrasti auttaa suunnistautumista ja varoittaa vaarasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (915, 3, 'Kulkuväylä, pituuskaltevuus lepotasoilla ja kulkuväylän kääntyessä', 'Kaltevuusprosentti (C).Käännöskohdissa tasanne on vaakasuorassa ( 2. lähde).', 'Arvo C on enintään 2 %.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (736, 3, 'Kytkin/säädin, etäisyys alasjohtavasta portaasta tai luiskasta', 'Kytkimen tai säätimene etäisyys alasjohtavasta portaasta tai luiskasta (A).', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (192, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, kulman avartaminen', NULL, 'Kulkuväylän (leveys  900 mm) kääntymiskulmaa avarretaan niin, että kulman mitat ovat 1150 x 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (251, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, leveys', 'Kulkuväylän leveys (A) ja (B) kulkuväylän kääntyessä.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (312, 3, 'Luiska, leveys', 'Luiskan leveys (A) riippuu sijainnista ja käyttötarkoituksesta. Suositusmitta on kaksikaistaisen poistumistien leveys.', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (317, 3, 'Luiska, reuna, korkeus', 'Luiskassa tulee olla 50 mm:n suojareunus silloin, kun luiskan reuna ei ole maan tasossa tai se ei rajoitu seinään.', '', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1094, 3, 'Luiska, välitasanne, kulkuväylä kääntyy 90 astetta', 'Välitasanteen kääntymiskulmaa avarretaan niin, että kulman mitat ovat A x B. Suositusmitat 2. lähteestä.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1150 mm. Arvo B on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (956, 3, 'Valaistus, luiska, portaat, kohdevalaistus', 'Luiskan ja portaiden alun kohdevalaistus auttaa luiskan ja portaan havaitsemista.', 'Valaistusvoimakkuus portaissa ja luiskissa 20 - 50 lx. Kriittisissä kohteissa, kuten tasoerojen yhteydessä, valaistusvoimakkuus 400 - 500 lx (2. lähde).', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (833, 4, 'Liikkumisen tilantarve, kulkuväylä kääntyy, yhteenlaskettu leveys', 'Risteävien kulkuväylien a ja b yhteen laskettu leveys (A). Kulkusuunnan kääntyessä 90 astetta, a:n ja b:n leveys vähintään 900 mm.', 'Arvo A on vähintään 2300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (834, 4, 'Valaistus, kulkuväylä, pinta, häikäisy', NULL, 'Kulkuväylän pinta ei saa heijastaa keino- eikä päivänvaloa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (228, 4, 'Valaistus, kulkuväylä, sijoitus', NULL, 'Kulkuväylän hahmottamista helpottaa samansuuntainen yhtenäinen valaisinlinja.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (813, 4, 'Valaistus, yövalo', NULL, 'Yövalot esimerkiksi pistorasioissa makuuhuoneen ja wc:n kulkuväylällä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1021, 3, 'Kalusteet, hyllyt', NULL, 'Tukeva kiinnitys ja korotettu reuna estävät tavaroiden putoamisen hyllyltä', '3,7,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (968, 3, 'Kalusteet, kaappi, etäisyys sivuseinästä', 'Syvyyden (A) vertailuarvona syvyys (B) 2. lähteestä. Kaappiin ulottumista helpottaa, kun ovi aukeaa yli 90 astetta. Avaamiseen tarvittava vapaa tila kaapin edessä 1300 mm.', 'Arvo A on vähintään 200 mm. Arvo B on vähintään 400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (564, 3, 'Kalusteet, ovi, avautuminen', NULL, 'Ovien avautumiskulma 180º, jolloin törmäysvaara pienenee.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (562, 3, 'Kalusteet, ovi, leveys', 'Ovileveys (C). Leveät ovet aiheuttavat törmäysvaaran. Törmäysvaara voidaan poistaa saranoimalla leveät ovet useampaan osaan taittuviksi tai käyttämällä liuku- ja rulo-ovia.', 'Arvo C on enintään 400 mm.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (765, 3, 'Kalusteet, ovi, rakenne', NULL, 'Oveen voidaan kiinnittää esim. lankakoreja ja koukkuja, jolloin tavarat ovat helposti ulottuvilla.', '8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (764, 3, 'Kalusteet, ovi, saranat', NULL, 'Saranoiden avautumiskulman on oltava 180 astetta, jolloin törmäysvaara pienenee. Saranaovien poisto on oltava helppoa, mutta ne eivät saa irrota paikoiltaan tahattomasta tönäisystä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (523, 3, 'Kalusteet, taso/pöytä, polvitilan korkeus', NULL, 'Polvitilan korkeus on 150 mm työtuolin korkeutta suurempi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (519, 3, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 450 mm. Arvo C on välillä 600 ... 650 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1098, 3, 'Makuuhuone, mitoitus', 'Yleinen vaatimus makuuhuoneen mitoitukselle.', 'Tila mitoitetaan niin, että sen kalustamiseen on useita vaihtoehtoja.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1053, 3, 'Makuuhuone, tilantarve vuoteen vieressä', 'Rollaattorin tilantarve (A) vuoteen vieressä.', 'Arvo A on vähintään 1400 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1242, 3, 'Ovi, kynnyskorkeus, asunto', 'Kynnyksen korkeus (C). Asunnoissa ja kulkuväylillä suositellaan kynnyksettömiä ovia.', 'Arvo C on enintään 20 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1064, 3, 'Valaistus, asuinhuone', NULL, 'Valaistusvoimakkuus asuinhuoneessa on 100 lx. Sopiva yleisvalaistuksen voimakkuus 150 -300 lx ( 2. lähde).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1065, 3, 'Valaistus, kohdevalaistus', NULL, 'Valaistusvoimakkuus työskentelyyn kirjoituspöydällä on 750 lx.  Sopiva  valaistusvoimakkuus lähityöskentelyssä 400 - 1000 lx (2. lähde).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (986, 4, 'Kalusteet, hyllyt', NULL, 'Hyllyjen on oltava ulosvedettäviä, pysäyttäjillä varustettuja ja sijoitettavissa halutulle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (521, 4, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1067, 4, 'Kalusteet, vaatekaappi', 'Vaatekaapin leveys (A). Leveä vaatekaappi parantaa ulottuvuutta. Jos kaappi on kapeampi, silloin hyllyjen ja tankojen tulisi olla ulosvedettäviä.', 'Arvo A on vähintään 800 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (583, 4, 'Makuuhuone, mitoitus', 'Makuuhuoneen mitoitus, kun tilassa pitää päästä liikkumaan apuvälineen kanssa.', 'Tila mitoitetaan niin, että sen kalustamiseen on useita vaihtoehtoja. Ainakin yksi makuuhuone mitoitetaan riittävän väljäksi pyörätuolin tai rollaattorin käyttäjälle ja/tai avustajalle.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (582, 4, 'Makuuhuone, sijoitus', NULL, 'Makuuhuoneesta järjestetään mahdollisimman suora  yhteys wc- ja pesutiloihin ja mahdolliselle parvekkeelle tai terassille.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (586, 4, 'Makuuhuone, tilantarve vuoteen ohittamiseen', 'Vuoteen ohittamiseen pyörätuolilla tarvittava tilan leveys (A). Suositusarvo mahdollistaa esteettömän ohittamisen.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (584, 4, 'Makuuhuone, tilantarve vuoteen vieressä', 'Pyörätuolia käyttävän henkilön tilantarve (A x B) vuoteeseen siirryttäessä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (587, 4, 'Makuuhuone, tilantarve vuoteen vieressä, avustaja', 'Avustajan tarvitsema vapaa tilan leveys (A) vuoteen vieressä. Vertailuarvona leveys (B) 2. lähteestä. Vuoteen toisella puolella tarvittava tila (C) pyörätuolille.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 1200 mm.', '11,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (864, 4, 'Valaistus, asuinhuone', NULL, 'Valaistusvoimakkuus asuinhuoneessa on 150 lx. Sopiva yleisvalaistuksen voimakkuus 150 -300 lx ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (464, 4, 'Valaistus, kohdevalaistus', NULL, 'Valaistusvoimakkuus työskentelyyn kirjoituspöydällä on 1000 lx.  Sopiva  valaistusvoimakkuus lähityöskentelyssä 400 - 1000 lx (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (813, 4, 'Valaistus, yövalo', NULL, 'Yövalot esimerkiksi pistorasioissa makuuhuoneen ja wc:n kulkuväylällä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (763, 5, 'Kalusteet', NULL, 'Kalusteiden paikkaa tai korkeutta voidaan säätää sähköisesti tai kiskokiinnityksellä.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1301, 5, 'Makuuhuone, varusteet, henkilönosturi', NULL, 'Sängystä nousemisen apuvälineenä voidaan käyttää kattonosturia tai liikuteltavaa nosturia.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1193, 1, 'Sauna, ovi, avautuminen', NULL, 'Oven aukeamissuunta löylyhuoneesta katsottuna ulospäin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1287, 1, 'Sauna, ovi, vapaa leveys', 'Oven kulkuaukon leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1195, 1, 'WC, ovi, avautuminen', NULL, 'Wc:stä katsottuna wc-oven aukeamissuunta on ulospäin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1196, 1, 'WC-/peseytymistila, ovi, lukko', NULL, 'Lukko avattavissa myös ulkopuolelta ns. vahtimestarin avaimella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1027, 2, 'Painike/vedin, ovi, etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys (A) alasjohtavasta portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1242, 3, 'Ovi, kynnyskorkeus, asunto', 'Kynnyksen korkeus (C). Asunnoissa ja kulkuväylillä suositellaan kynnyksettömiä ovia.', 'Arvo C on enintään 20 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1246, 3, 'Ovi, turvallisuus', NULL, 'Oveen suositellaan ikkunaa 900-1800 mm:n korkeudelle törmäysvaaran estämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (619, 3, 'Pukuhuone, ovi, kynnys', NULL, 'Kynnyksiä olisi vältettävä saunatiloissa. Puku- ja pesuhuoneen välille voidaan tarvittaessa asentaa kasaan painuva kumikynnys.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (439, 3, 'Sauna, ovi', NULL, 'Törmäysvaaran vuoksi ei kokolasiovea. Ovessa vaakapuite tai havaintomerkkejä noin 1000 mm:n korkeudella.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (441, 3, 'Sauna, ovi, kynnys', NULL, 'Ei kynnystä.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1169, 3, 'Sauna, ovi, vedin', NULL, 'Puinen saunan oven vedin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (361, 3, 'WC-/peseytymistila, ovi, avaaminen', NULL, 'Ovi avattavissa ja lukittavissa yhdellä kädellä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (440, 4, 'Sauna, ovi, vapaa leveys', 'Oven kulkuaukon leveys (A) liikuntaesteisille suunnittellussa saunatilassa. Vertailuarvona asuntosaunan oven leveys (B) 2. lähteestä.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1232, 2, 'Ovi, oviaukko, vapaa leveys, ulko-ovi', NULL, 'Asuntojen ulko- ja porrastaso-oviksi suositellaan leveydeltään 10 M-ovia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1027, 2, 'Painike/vedin, ovi, etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys (A) alasjohtavasta portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (260, 2, 'Tuulikaappi, mitoitus, kääntöovi aukeaa poispäin', 'Tuulikaapin tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (784, 2, 'Valaistus, sisäänkäynti, ulko-ovi', 'Valaistusvoimakkuus (C) ulko-ovella.', 'Arvo C on välillä 80 ... 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (940, 3, 'Liikkumisen tilantarve, automaattiovi aukeaa kohti, ulkopyörätuoli', 'Tilantarve ulko-oven oven edessä  (A), silloin kun tasanteen syvyys on mitta (B). Vieressä alas johtava porras tai luiska. Jos oven edessä on alasjohtava porras, tarvitaan tilaa enemmän kuin 2000 mm.Oviaukon vapaa leveys 900 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 2700 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (852, 3, 'Ovi, aukipysymisaika', 'Aukipysymisaikaa (A) säätäessä otetaan huomioon hitaasti liikkuvat henkilöt. Mikäli ovi avataan painonappia tai painiketta käyttäen automaattisesti, riippuu tarvittava aika painikkeen ja oven välisestä etäisyydestä.', 'Arvo A on vähintään 25 s.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1242, 3, 'Ovi, kynnyskorkeus, asunto', 'Kynnyksen korkeus (C). Asunnoissa ja kulkuväylillä suositellaan kynnyksettömiä ovia.', 'Arvo C on enintään 20 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (681, 3, 'Parveke, ovi, aukipitolaite', NULL, 'Aukipitolaite esim. ovijarru estää tuulta paiskomasta ovea. Ovijarrun jäykkyyttä voidaan säätää niin, että ovi on kevyt avata, mutta pysyy silti auki.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (679, 3, 'Parveke, ovi, kynnyskorkeus', 'Kynnyskorkeus (C) saadaan vaatimuksen mukaiseksi esim. irrotettavan ritilän avulla. Parveketaso suositellaan kosteusteknisistä syistä sijoitettavaksi 80...120 mm kynnyksen yläpintaa alemmaksi.', 'Arvo C on enintään 20 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (680, 3, 'Parveke, ovi, kynnyspelti', NULL, 'Kynnyspelti tehdään luistamattomasta materiaalista esim. kuvioidusta alumiinista tai se varustetaan liukuesteellä. Kynnyspelti loivasti kalteva.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (273, 4, 'Autopaikka, autotalli, ovi', NULL, 'Ovessa automaattinen avauslaite, jota käytetään autosta kaukosäätimellä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (952, 4, 'Autopaikka, autotalli, ovi, leveys', 'Autotallin oven leveys (A).', 'Arvo A on vähintään 2500 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
