INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
