INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (259, 4, 'Tuulikaappi, mitoitus, kääntöovi aukeaa kohti', 'Tuulikaapin minimileveys (A) ja minimisyvyys(B).', 'Tuulikaapin syvyys vähintään 1500 mm + ovilehden leveys.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (682, 1, 'Parveke, kaide', NULL, 'Kaiteen suojaava osa tulisi olla läpinäkyvä (esim. pinnakaide) tai siinä tulisi olla matalalla sijaitsevia kurkistusaukkoja.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1221, 1, 'Parveke, kaide, korkeus lattiasta', 'Asunnon parvekkeen tai terassin kaiteen korkeus (A) putoamiskorkeudesta riippumatta. Suojaavan osan korkeus (B).', 'Arvo A on vähintään 1000 mm. Arvo B on vähintään 700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (519, 3, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 450 mm. Arvo C on välillä 600 ... 650 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (683, 3, 'Parveke, kaide, käsijohde', NULL, 'Parvekkeen kaiteessa on oltava käsijohde, josta saa tukevan otteen.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (684, 3, 'Parveke, kaide, lattian liittymäkohta', NULL, 'Liittymäkohdassa ei saa olla rakoa, johon apuväline esim. keppi voi jäädä kiinni.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (675, 3, 'Parveke, lattia, pinta', NULL, 'Parvekeen lattian on oltava kova, luistamaton, tasainen esim. betoni.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (676, 3, 'Parveke, lattia, ritilä', 'Apuvälineet esim. valkoinen keppi ei saa jäädä kiinni ritilän rakoihin, joiden maksimileveys on (C). Irrotettava ritilä madaltaa parvekkeen lattian ja kynnyksen korkeuseroa.', 'Arvo C on enintään 5 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (674, 3, 'Parveke, mitoitus', 'Tilantarve esitetty leveyden (A) ja syvyyden (B) avulla. Parvekken muoto vaikuttaa kalustettavuuteen. Mitoituksessa on huomioitava oven avautumisen, kalusteiden ja pyörätuolin kääntymisen tilantarve.', 'Arvo A on vähintään 2400 mm, mutta arvoksi suositellaan vähintään 2600 mm. Arvo B on vähintään 2100 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (681, 3, 'Parveke, ovi, aukipitolaite', NULL, 'Aukipitolaite esim. ovijarru estää tuulta paiskomasta ovea. Ovijarrun jäykkyyttä voidaan säätää niin, että ovi on kevyt avata, mutta pysyy silti auki.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (679, 3, 'Parveke, ovi, kynnyskorkeus', 'Kynnyskorkeus (C) saadaan vaatimuksen mukaiseksi esim. irrotettavan ritilän avulla. Parveketaso suositellaan kosteusteknisistä syistä sijoitettavaksi 80...120 mm kynnyksen yläpintaa alemmaksi.', 'Arvo C on enintään 20 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (680, 3, 'Parveke, ovi, kynnyspelti', NULL, 'Kynnyspelti tehdään luistamattomasta materiaalista esim. kuvioidusta alumiinista tai se varustetaan liukuesteellä. Kynnyspelti loivasti kalteva.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1068, 3, 'Pihakalusteet, penkki, käsinojat', NULL, 'Penkissä on oltava etureunaan ulottuvat käsinojat, joista saa tukea istuutuessa ja noustaessa ylös.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (521, 4, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1164, 4, 'Parveke, mitoitus, pinta-ala', 'Parvekkeen tai terassin suositeltava vähimmäispinta-ala (C).', 'Arvo C on välillä 6 ... 8 m2.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (234, 4, 'Pihakalusteet, penkki, tila pyörätuolille ja rollaattorille', 'Penkin vieressä tarvittavan tilan leveys (A) pyörätuolille ja rollaattorille, silloin kun henkilö istuu penkillä.', 'Arvo A on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (947, 4, 'Pihakalusteet, pöytä, korkeus', 'Pöydän korkeus (C) maanpinnasta.', 'Arvo C on välillä 750 ... 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (946, 4, 'Pihakalusteet, pöytä, polvitilan mitoitus', 'Mitat on esitetty leveyden (A), syvyyden (B) ja korkeuden  (C) avulla.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1196, 1, 'WC-/peseytymistila, ovi, lukko', NULL, 'Lukko avattavissa myös ulkopuolelta ns. vahtimestarin avaimella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1198, 1, 'WC-/peseytymistila, varusteet', NULL, 'Kerrostalossa, jossa edellytetään hissiä, asuinhuoneiston WC-/peseytymistilan on sovelluttava myös pyörätuolin käyttäjälle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (350, 2, 'Pesuallas, etäisyys seinästä', 'Pesualtaan etäisyys seinästä (A). Pesuallas asennetaan ulos seinästä polvitilan aikaansaamiseksi.', 'Arvo A on vähintään 200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (348, 2, 'Pesuallas, kiinnitys', 'Altaan kiinnitys kestää painon (A) ja altaaseen tukeutumisen.', 'Arvo A on vähintään 150 kg.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1162, 2, 'Suihku, käsisuihku, korkeus lattiasta', 'Suihkutangon asennuskorkeus (C). Suihkutangossa on korkeussäädettävä käsisuihku.', 'Arvo C on välillä 1000 ... 2000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1197, 2, 'Suihku, käsituki', NULL, 'Tukitanko tai -kahva sijoitetaan suihkutilaan ja kylpyammeen viereen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1021, 3, 'Kalusteet, hyllyt', NULL, 'Tukeva kiinnitys ja korotettu reuna estävät tavaroiden putoamisen hyllyltä', '3,7,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (968, 3, 'Kalusteet, kaappi, etäisyys sivuseinästä', 'Syvyyden (A) vertailuarvona syvyys (B) 2. lähteestä. Kaappiin ulottumista helpottaa, kun ovi aukeaa yli 90 astetta. Avaamiseen tarvittava vapaa tila kaapin edessä 1300 mm.', 'Arvo A on vähintään 200 mm. Arvo B on vähintään 400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (564, 3, 'Kalusteet, ovi, avautuminen', NULL, 'Ovien avautumiskulma 180º, jolloin törmäysvaara pienenee.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (562, 3, 'Kalusteet, ovi, leveys', 'Ovileveys (C). Leveät ovet aiheuttavat törmäysvaaran. Törmäysvaara voidaan poistaa saranoimalla leveät ovet useampaan osaan taittuviksi tai käyttämällä liuku- ja rulo-ovia.', 'Arvo C on enintään 400 mm.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (765, 3, 'Kalusteet, ovi, rakenne', NULL, 'Oveen voidaan kiinnittää esim. lankakoreja ja koukkuja, jolloin tavarat ovat helposti ulottuvilla.', '8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (764, 3, 'Kalusteet, ovi, saranat', NULL, 'Saranoiden avautumiskulman on oltava 180 astetta, jolloin törmäysvaara pienenee. Saranaovien poisto on oltava helppoa, mutta ne eivät saa irrota paikoiltaan tahattomasta tönäisystä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (346, 3, 'Pesuallas, laskutaso', NULL, 'Pesualtaan yhteydessä laskutaso, johon voi myös tukeutua.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (347, 3, 'Pesuallas, reuna', NULL, 'Altaan reunamuoto sellainen, johon on helppo tukeutua.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (357, 3, 'Pesuallas, sekoittaja', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (358, 3, 'Pesuallas, sekoittaja, sijoitus', NULL, 'Pesualtaan sekoittajaan on ulotuttava kurkottamatta. Ulottumisen helpottamiseksi voidaan käyttää tavallista pidempää vipua tai sijoittaa sekoittaja pesualtaan etuosaan.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1108, 3, 'Pesuallas, tyyppi, käsituki', NULL, 'Erikoisallas, jonka etureunassa on käsituki.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1107, 3, 'Pesuallas, vapaa tila altaan edessä', 'Rollaattorin käyttäjän tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1188, 3, 'Pesuallas, vesi- ja viemärijohdot', NULL, 'Vesi- ja viemäriliitännöissä on otettava huomioon pesualtaan mahdollinen myöhempi asennus uuteen korkeuteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (359, 3, 'Pesuallas, väri', NULL, 'Väri- tai tummuusero taustaa vasten auttaa altaan havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (338, 3, 'Suihku, istuin', NULL, 'Kiinteä seinälle kääntyvä ja korkeussäädettävä suihkuistuin.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (341, 3, 'Suihku, istuin, etäisyys sivuseinästä', 'Suihkuistuimen etäisyys seinästä (C).', 'Arvo C on välillä 700 ... 800 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1174, 3, 'Suihku, istuin, korkeus', 'Suihkuistuimen korkeus (A) lattiasta.', 'Istuin asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on 500 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (339, 3, 'Suihku, istuin, käsituet', NULL, 'Suihkuistuimen käsituet ovat suihkuun siirtymisessä välttämättömät. Ne ulottuvat istuimen etureunaan asti.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (342, 3, 'Suihku, istuin, poikkituen etäisyys', NULL, 'Poikkituen etäisyydeksi suihkutuolin etureunasta suositellaan 550 mm. Istuimen etäisyys sivuseinästä 700 - 800 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (344, 3, 'Suihku, istuin, vapaa tila edessä', 'Rollaattorin kääntämiseen tarvittava vapaa tila (A) suihkuistuimen edessä, kun istuinta lähestytään etuviistosta.', 'Arvo A on vähintään 1050 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (343, 3, 'Suihku, sekoittaja, etäisyys seinästä', NULL, 'Suihkusekoittaja sijoitetaan sivuseinälle suihkuistuimen etureunan kohdalle (n. 450 mm seinästä), jotta suihkussa istuttaessa ei tarvitse kurotella.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1136, 3, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus WC/peseytymistilassa on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1056, 3, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 200 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (627, 3, 'WC-/peseytymistila, käsituki, materiaali', NULL, 'Mattapintainen, muovipäällysteinen käsituki on suositeltavampi kuin metallipintainen. Metallipintainen käsijohde on liukas ja se saattaa häikäistä ja aiheuttaa allergiaa (nikkeli).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (690, 3, 'WC-/peseytymistila, lattia, kaltevuus', NULL, 'Peseytymistilan lattian suositeltava kaltevuus on 1,25 % (1:80), lattiakaivon ympärillä 2% (1:50).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1157, 3, 'WC-/peseytymistila, lattia, lattialämmitys', NULL, 'Lattialämmitys on suositeltava.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1156, 3, 'WC-/peseytymistila, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1158, 3, 'WC-/peseytymistila, lattiakaivo', NULL, 'Lattiakaivoa ei saa sijoittaa kulkuväylälle eikä paikkaan, jossa seisotaan (esim. suihkussa) liukastumisvaaran takia. Kannen on oltava tukeva ja pysyttävä hyvin paikallaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (361, 3, 'WC-/peseytymistila, ovi, avaaminen', NULL, 'Ovi avattavissa ja lukittavissa yhdellä kädellä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (369, 3, 'WC-/peseytymistila, varusteet, peili, yläreunan korkeus', 'Peilin yläreunan korkeus (C). Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo C on välillä 1700 ... 1800 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (986, 4, 'Kalusteet, hyllyt', NULL, 'Hyllyjen on oltava ulosvedettäviä, pysäyttäjillä varustettuja ja sijoitettavissa halutulle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (525, 4, 'Kalusteet, polvitilan vesijohdot', NULL, 'Polvitilassa olevat kuumavesiputket yms. asennetaan lähelle seinää ja suojataan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1202, 4, 'Pesuallas, etäisyys sivuseinästä', 'Normaalikokoisen pesualtaan (esim. 550 x 400 mm) keskilinjan etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (349, 4, 'Pesuallas, korkeus lattiasta', 'Korkeus (C) lattiasta. Pyörätuolin käyttäjälle soveltuvan pesualtaan asennuskorkeus. Pyörätuolilla pääsee lähemmäksi allasta, jos sen pohja on vino.', 'Arvo C on välillä 770 ... 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1103, 4, 'Pesuallas, korkeus lattiasta', 'Korkeus (C) lattiasta. Pesuallas asennetaan käyttäjän yksilölliselle korkeudelle.', 'Arvo C on välillä 700 ... 950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (355, 4, 'Pesuallas, muoto', NULL, 'Kovera etureuna helpottaa ulottumista ja kumartumista altaan ylle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (353, 4, 'Pesuallas, polvitila', 'Esitetty polvitilan leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1105, 4, 'Pesuallas, tyyppi, korkeussäädettävä allas', NULL, 'Käyttäjä voi säätää sähköisesti korkeussäädettävän altaan sopivalle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1106, 4, 'Pesuallas, tyyppi, siirrettävä allas', NULL, 'WC-istuimen vierellä sivusuunnassa kiskoilla siirrettävä allas, jolloin sitä käytetään WC-istuimella istuen. Istuimen vierestä vapautuu tilaa avustajalle, kun allas siirretään pois.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (351, 4, 'Pesuallas, vapaa tila altaan edessä', 'Vapaan tilan syvyys (A).', 'Arvo A on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1140, 4, 'Pyörätuolin pesutila, asunto', NULL, 'Käynti pesutilaan suoraan ulkoa tai pesutila sijoitetaan sisäänkäynnin lähelle esim. kuraeteiseen tai  WC-tilaan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (370, 4, 'Pyörätuolin pesutila, asunto, lattiakaivo', NULL, 'Irrallisella hiekkasuodattimella varustettu lattiakaivo WC:ssä pyörätuolin pesua varten, jos ei ole erillistä pesupaikkaa.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (340, 4, 'Suihku, istuin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1029, 4, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus on 150 lx. Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx. Wc-tiloissa esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (790, 4, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 300 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (368, 4, 'WC-/peseytymistila, varusteet, peili, alareunan korkeus', 'Pesualtaan yläpuolella oleva peilin alareunan korkeus (C) lattiasta riippuu pesualtaan korkeudesta.', 'Arvo C on välillä 800 ... 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (763, 5, 'Kalusteet', NULL, 'Kalusteiden paikkaa tai korkeutta voidaan säätää sähköisesti tai kiskokiinnityksellä.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (356, 5, 'Pesuallas, muoto', NULL, 'Allastasossa tai tasoon upotetussa altaassa selkeä reuna, jotta allas erottuu tasosta', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1104, 5, 'Pesuallas, tyyppi, käännettävä allas', 'Altaan leveys (A) ja syvyys (B). Allas, joka voidaan kääntää vinoon asentoon, soveltuu vinossa asennossa pyörätuolin käyttäjälle ja suorana muille käyttäjille. Polvitilaa lisää, jos se asennetaan 200 mm irti seinästä.', 'Arvo A on vähintään 670 mm. Arvo B on vähintään 430 mm, mutta arvoksi suositellaan vähintään 630 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1223, 1, 'Luiska/portaat, käsijohde', NULL, 'Käsijohde tai kädensija asennetaan kaikkiin portaisiin ja luiskiin, julkisissa tiloissa molemmin puolin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1224, 1, 'Luiska/portaat,käsijohde, pituus', NULL, 'Portaassa ja luiskassa käsijohde on asennettava koko pituudelle. Johteiden tulee jatkua yhtenäisinä myös välitasanteiden osuudella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (843, 1, 'Portaat, sijoitus', NULL, 'Portaat eivät saa aiheuttaa putoamis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1027, 2, 'Painike/vedin, ovi, etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys (A) alasjohtavasta portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (383, 2, 'Portaat, askelmat', NULL, 'Portaissa ei tulisi olla avonaisia tai yksittäisiä askelmia eikä ulkonevia askelmien etureunoja. Portaat, joissa on vähintään kolme nousua, on helpompi havaita.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (330, 2, 'Portaat, mitoitus, askelmat', 'Portaan nousun ja etenemän suhde määrää portaiden helppokulkuisuuden.', 'Mitoituksessa voidaan käyttää seuraavaa kaavaa: 2 * nousu + etenemä = 630 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1120, 2, 'Portaat, mitoitus, asunnon sisäinen, kaksivartinen', 'Normaalimitoitus esitetty portaikon koko leveyden (A), portaan etenemän (B) ja portaan nousun (C) avulla.', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2100 mm. Arvo B on vähintään 270 mm. Arvo C on enintään 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1114, 2, 'Portaat, mitoitus, asunnon sisäinen, yksivartinen', 'Normaalimitoitus esitetty portaikon leveyden (A), portaan etenemän (B) ja portaan nousun (C) avulla.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 270 mm. Arvo C on enintään 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1129, 2, 'Portaat, mitoitus, paarikuljetus', 'Paarien tilantarve kantajien liikkumavarat mukaanlukien esitettynä leveyden (A) ja pituuden (B) avulla.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 2400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1117, 2, 'Portaat, nousukulma, sisätilat', 'Portaiden nousukulman (C) yleinen vaihteluväli sisätiloissa.', 'Arvo C on välillä 25 ... 37 °.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (380, 2, 'Portaat, väri', NULL, 'Portaan väri- tai tummuuskontrasti seinän tai lattian kanssa auttaa portaiden erottamista seinä- tai lattiapinnasta. Porrasaskelmien etureunojen merkintä helpottaa askelmien erottamista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1042, 2, 'Valaistus, kulkuväylä sisällä', 'Valaistusvoimakkuus (C) eteisissä, auloissa ja käytävillä.', 'Arvo C on välillä 80 ... 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (736, 3, 'Kytkin/säädin, etäisyys alasjohtavasta portaasta tai luiskasta', 'Kytkimen tai säätimene etäisyys alasjohtavasta portaasta tai luiskasta (A).', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (381, 3, 'Portaat, liukueste', NULL, 'Liukuesteet porrasaskelmien etureunoissa ja niiden väriero portaan kanssa lisäävät portaan turvallisuutta.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1121, 3, 'Portaat, mitoitus, asunnon sisäinen, kaksivartinen', 'Helppokulkuisen portaan mitoitus on esitetty koko portaikon leveyden (A), portaan etenemän (B) ja portaan nousun (C) avulla.', 'Arvo A on vähintään 2600 mm, mutta arvoksi suositellaan vähintään 2700 mm. Arvo B on vähintään 300 mm. Arvo C on enintään 160 mm.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1115, 3, 'Portaat, mitoitus, asunnon sisäinen, yksivartinen', 'Helppokulkuisen portaan mitoitus on esitetty portaikon leveyden (A), portaan etenemän (B) ja portaan nousun (C) avulla.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 300 mm. Arvo C on enintään 160 mm.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1234, 3, 'Portaat, mitoitus, helppokulkuinen porras', 'Portaiden mitoitus henkilölle, jolla on jäykät nilkat. Mitoitus esitetty portaan etenemän (A) ja nousun (B) avulla.', 'Arvo A on vähintään 390 mm. Arvo C on enintään 120 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (384, 3, 'Portaat, tyyppi', NULL, 'Kierreporrasta ei saa käyttää.', '3,7,8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1128, 3, 'Portaat, välitasanne', NULL, 'Välitasanteita tulee olla vähintään yksi kerrosta kohti.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (956, 3, 'Valaistus, luiska, portaat, kohdevalaistus', 'Luiskan ja portaiden alun kohdevalaistus auttaa luiskan ja portaan havaitsemista.', 'Valaistusvoimakkuus portaissa ja luiskissa 20 - 50 lx. Kriittisissä kohteissa, kuten tasoerojen yhteydessä, valaistusvoimakkuus 400 - 500 lx (2. lähde).', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (834, 4, 'Valaistus, kulkuväylä, pinta, häikäisy', NULL, 'Kulkuväylän pinta ei saa heijastaa keino- eikä päivänvaloa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (228, 4, 'Valaistus, kulkuväylä, sijoitus', NULL, 'Kulkuväylän hahmottamista helpottaa samansuuntainen yhtenäinen valaisinlinja.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (813, 4, 'Valaistus, yövalo', NULL, 'Yövalot esimerkiksi pistorasioissa makuuhuoneen ja wc:n kulkuväylällä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1196, 1, 'WC-/peseytymistila, ovi, lukko', NULL, 'Lukko avattavissa myös ulkopuolelta ns. vahtimestarin avaimella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1198, 1, 'WC-/peseytymistila, varusteet', NULL, 'Kerrostalossa, jossa edellytetään hissiä, asuinhuoneiston WC-/peseytymistilan on sovelluttava myös pyörätuolin käyttäjälle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (968, 3, 'Kalusteet, kaappi, etäisyys sivuseinästä', 'Syvyyden (A) vertailuarvona syvyys (B) 2. lähteestä. Kaappiin ulottumista helpottaa, kun ovi aukeaa yli 90 astetta. Avaamiseen tarvittava vapaa tila kaapin edessä 1300 mm.', 'Arvo A on vähintään 200 mm. Arvo B on vähintään 400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (564, 3, 'Kalusteet, ovi, avautuminen', NULL, 'Ovien avautumiskulma 180º, jolloin törmäysvaara pienenee.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (562, 3, 'Kalusteet, ovi, leveys', 'Ovileveys (C). Leveät ovet aiheuttavat törmäysvaaran. Törmäysvaara voidaan poistaa saranoimalla leveät ovet useampaan osaan taittuviksi tai käyttämällä liuku- ja rulo-ovia.', 'Arvo C on enintään 400 mm.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (764, 3, 'Kalusteet, ovi, saranat', NULL, 'Saranoiden avautumiskulman on oltava 180 astetta, jolloin törmäysvaara pienenee. Saranaovien poisto on oltava helppoa, mutta ne eivät saa irrota paikoiltaan tahattomasta tönäisystä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1242, 3, 'Ovi, kynnyskorkeus, asunto', 'Kynnyksen korkeus (C). Asunnoissa ja kulkuväylillä suositellaan kynnyksettömiä ovia.', 'Arvo C on enintään 20 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1136, 3, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus WC/peseytymistilassa on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1056, 3, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 200 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (627, 3, 'WC-/peseytymistila, käsituki, materiaali', NULL, 'Mattapintainen, muovipäällysteinen käsituki on suositeltavampi kuin metallipintainen. Metallipintainen käsijohde on liukas ja se saattaa häikäistä ja aiheuttaa allergiaa (nikkeli).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1157, 3, 'WC-/peseytymistila, lattia, lattialämmitys', NULL, 'Lattialämmitys on suositeltava.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1156, 3, 'WC-/peseytymistila, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (361, 3, 'WC-/peseytymistila, ovi, avaaminen', NULL, 'Ovi avattavissa ja lukittavissa yhdellä kädellä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (369, 3, 'WC-/peseytymistila, varusteet, peili, yläreunan korkeus', 'Peilin yläreunan korkeus (C). Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo C on välillä 1700 ... 1800 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1029, 4, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus on 150 lx. Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx. Wc-tiloissa esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (790, 4, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 300 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (368, 4, 'WC-/peseytymistila, varusteet, peili, alareunan korkeus', 'Pesualtaan yläpuolella oleva peilin alareunan korkeus (C) lattiasta riippuu pesualtaan korkeudesta.', 'Arvo C on välillä 800 ... 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1021, 3, 'Kalusteet, hyllyt', NULL, 'Tukeva kiinnitys ja korotettu reuna estävät tavaroiden putoamisen hyllyltä', '3,7,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (968, 3, 'Kalusteet, kaappi, etäisyys sivuseinästä', 'Syvyyden (A) vertailuarvona syvyys (B) 2. lähteestä. Kaappiin ulottumista helpottaa, kun ovi aukeaa yli 90 astetta. Avaamiseen tarvittava vapaa tila kaapin edessä 1300 mm.', 'Arvo A on vähintään 200 mm. Arvo B on vähintään 400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (564, 3, 'Kalusteet, ovi, avautuminen', NULL, 'Ovien avautumiskulma 180º, jolloin törmäysvaara pienenee.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (562, 3, 'Kalusteet, ovi, leveys', 'Ovileveys (C). Leveät ovet aiheuttavat törmäysvaaran. Törmäysvaara voidaan poistaa saranoimalla leveät ovet useampaan osaan taittuviksi tai käyttämällä liuku- ja rulo-ovia.', 'Arvo C on enintään 400 mm.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (764, 3, 'Kalusteet, ovi, saranat', NULL, 'Saranoiden avautumiskulman on oltava 180 astetta, jolloin törmäysvaara pienenee. Saranaovien poisto on oltava helppoa, mutta ne eivät saa irrota paikoiltaan tahattomasta tönäisystä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (519, 3, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 450 mm. Arvo C on välillä 600 ... 650 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1242, 3, 'Ovi, kynnyskorkeus, asunto', 'Kynnyksen korkeus (C). Asunnoissa ja kulkuväylillä suositellaan kynnyksettömiä ovia.', 'Arvo C on enintään 20 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (466, 3, 'Ruokailutila', NULL, 'Mitoitetaan liikkumis- ja toimimisesteisten tarpeisiin; ruokailutila sijoitetaan lähellä ruoan valmistustilaa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1064, 3, 'Valaistus, asuinhuone', NULL, 'Valaistusvoimakkuus asuinhuoneessa on 100 lx. Sopiva yleisvalaistuksen voimakkuus 150 -300 lx ( 2. lähde).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1061, 3, 'Valaistus, ruokailutila, kohdevalaistus', NULL, 'Valaistusvoimakkuus ruokailutilassa ruokapöydässä on 200 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (986, 4, 'Kalusteet, hyllyt', NULL, 'Hyllyjen on oltava ulosvedettäviä, pysäyttäjillä varustettuja ja sijoitettavissa halutulle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (521, 4, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (471, 4, 'Ruokailutila, ruokapöytä, etäisyys kiintokalusteista', 'Ruokapöydän etäisyyden kiintokalusteista (A) vertailuarvona etäisyys (B) 2. lähteestä.', 'Arvo A on vähintään 1100 mm. Arvo B on vähintään 1400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (475, 4, 'Ruokailutila, ruokapöytä, syvyys', 'Ruokapöydän syvyys (A) silloin kun kaksi pyörätuolissa istuvaa henkilöä istuu vastakkain pöydän ääressä. Syvyys (B) silloin kun on yksi pyörätuolin käyttäjä, vertailuarvona syvyys (C) 2. lähteestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1050 mm. Arvo C on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (474, 4, 'Ruokailutila, ruokapöytä, vapaa ohitustila, pyörätuoli', 'Tilantarve (A) pöydän ääressä, jotta pyörätuolissa istuvan henkilön mahtuu ohittamaan.', 'Arvo A on vähintään 1300 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1116, 4, 'Ruokailutila, ruokapöytä, vapaa tila', 'Pyörätuolin vaatima leveys (A) ruokapöydän ääressä. Vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (962, 4, 'Ruokailutila, ruokapöytä, vapaa tila päädyssä', 'Vapaa tila esitetty alueena (A x B).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (472, 4, 'Ruokailutila, ruokapöytä, vapaa tila sivulla', 'Vapaa tila esitetty alueena (A x B).', 'Arvo A on vähintään 1100 mm. Arvo B on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (864, 4, 'Valaistus, asuinhuone', NULL, 'Valaistusvoimakkuus asuinhuoneessa on 150 lx. Sopiva yleisvalaistuksen voimakkuus 150 -300 lx ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (801, 4, 'Valaistus, ruokailutila, kohdevalaistus', NULL, 'Valaistusvoimakkuus ruokailutilassa ruokapöydässä on 300 lx.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (444, 1, 'Sauna, kiuas, suojaus', NULL, 'Kiuas suojataan suojakaiteilla tai -seinämillä palovammojen estämiseksi. Erityisesti suihkutuolia käyttävän henkilön jalat ovat vaarassa, joten suojaavaa rakennetta on jatkettava tavanomaista alemmaksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1194, 1, 'Sauna, lauteet, käsijohde', NULL, 'Käsijohde tai kädensija lauteille nousua varten.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1193, 1, 'Sauna, ovi, avautuminen', NULL, 'Oven aukeamissuunta löylyhuoneesta katsottuna ulospäin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1287, 1, 'Sauna, ovi, vapaa leveys', 'Oven kulkuaukon leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1198, 1, 'WC-/peseytymistila, varusteet', NULL, 'Kerrostalossa, jossa edellytetään hissiä, asuinhuoneiston WC-/peseytymistilan on sovelluttava myös pyörätuolin käyttäjälle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1246, 3, 'Ovi, turvallisuus', NULL, 'Oveen suositellaan ikkunaa 900-1800 mm:n korkeudelle törmäysvaaran estämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1166, 3, 'Sauna, ilmanvaihto, poistoilma', NULL, 'Löylyn painumista alas istumakorkeudelle tehostetaan, kun poistoilmaventtiili sijoitetaan kiukaan vastakkaiseen seinään, n. 200 mm:ä lattiasta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (443, 3, 'Sauna, ilmanvaihto, tuloilma', NULL, 'Löylyn painumista alas istumakorkeudelle tehostetaan, kun tuloilma otetaan ylhäältä 500 mm kiukaan yläpuolelta olevasta venttiilistä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1165, 3, 'Sauna, kiuas', NULL, 'Kiuas sijoitetaan ja ilmanvaihto järjestetään niin, että löylyä tulee myös alas.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1148, 3, 'Sauna, käsijohde', NULL, 'Puinen käsijohde, jossa ei saa olla kuumenevia metalliosia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1145, 3, 'Sauna, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1170, 3, 'Sauna, lauteet', NULL, 'Lauteet tehdään oksattomasta puusta. Lauteissa ei saa olla teräviä särmiä ja ulkonemia ja niiden etureuna pyöristetään. Näkyvissä ei saa olla kuumenevia metalliosia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (450, 3, 'Sauna, lauteet, istumakorkeus', NULL, 'Tavallinen lauteiden istumakorkeus on noin 450 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (451, 3, 'Sauna, lauteet, istumakorkeus', 'Sopiva istumakorkeus (C) henkilölle, jolla on jäykät polvet tai lonkat.', 'Arvo C on välillä 500 ... 550 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (447, 3, 'Sauna, lauteet, käsijohde', NULL, 'Lauteille nousemista varten käsijohteet molemmin puolin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (445, 3, 'Sauna, lauteet, pituus', 'Lauteiden pituus (A), jolloin lauteilla mahtuu makaamaan pitkällään.', 'Arvo A on vähintään 2000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1146, 3, 'Sauna, lauteet, portaat, mitoitus', 'Portaan leveyden (A) vaatimus.', 'Portaan leveys on 600 mm, silloin kun molemmin puolin on pidettävä kiinni käsijohteista.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1150, 3, 'Sauna, lauteet, portaat, mitoitus', 'Portaiden etenemä (A) ja nousu (C) henkilölle, jolla on jäykät polvet tai lonkat.', 'Arvo A on vähintään 390 mm. Arvo C on enintään 120 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1172, 3, 'Sauna, lauteet, portaat, mitoitus', 'Portaan etenemän (A) vaatimus.', 'Arvo A on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (446, 3, 'Sauna, lauteet, ylälauteen syvyys', 'Laudesyvyys (A). Lauteet suunnitellaan käyttäjäkohtaisesti. Suurempi laudesyvyys (B), jos lauteilla on voitava istua jalat suorana.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 1000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (439, 3, 'Sauna, ovi', NULL, 'Törmäysvaaran vuoksi ei kokolasiovea. Ovessa vaakapuite tai havaintomerkkejä noin 1000 mm:n korkeudella.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (441, 3, 'Sauna, ovi, kynnys', NULL, 'Ei kynnystä.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1169, 3, 'Sauna, ovi, vedin', NULL, 'Puinen saunan oven vedin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1136, 3, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus WC/peseytymistilassa on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (690, 3, 'WC-/peseytymistila, lattia, kaltevuus', NULL, 'Peseytymistilan lattian suositeltava kaltevuus on 1,25 % (1:80), lattiakaivon ympärillä 2% (1:50).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1157, 3, 'WC-/peseytymistila, lattia, lattialämmitys', NULL, 'Lattialämmitys on suositeltava.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1156, 3, 'WC-/peseytymistila, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1158, 3, 'WC-/peseytymistila, lattiakaivo', NULL, 'Lattiakaivoa ei saa sijoittaa kulkuväylälle eikä paikkaan, jossa seisotaan (esim. suihkussa) liukastumisvaaran takia. Kannen on oltava tukeva ja pysyttävä hyvin paikallaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1144, 4, 'Sauna, kiuas, seinäkiuas', NULL, 'Seinälle asennettava kiuas lisää lattiapinta-alaa.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (448, 4, 'Sauna, lauteet, istumakorkeus, alin laude', NULL, 'Suihkutuolia käyttävän henkilön on helpompi päästä lauteille, jos alin laude on suihkutuolin istuintasossa eli noin 500 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (449, 4, 'Sauna, lauteet, lauteiden korkeusero', 'Lauteiden korkeusero (C), jolloin lauteelta toiselle siirtyminen on käsivoimin mahdollista.', 'Arvo C on enintään 300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1147, 4, 'Sauna, lauteet, portaat, mitoitus', 'Portaan leveyden (A) vaatimus.', 'Portaan leveys on 900 mm, silloin kun apuna on kainalo- tai kyynärsauvat.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1149, 4, 'Sauna, mitoitus', NULL, 'Normaali laudekorkeus kasvattaa saunan pinta-alaa, koska liikuntaesteinen tarvitseee loivat portaat päästäkseen lauteille. Jos löyly saadaan istumakorkeudelle kiukaan sijoituksella, lauteina voidaan käyttää penkkejä eikä porrasmaisia lauteita.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (442, 4, 'Sauna, mitoitus, vapaa tila', 'Tilantarve esitetty leveyden (A) ja syvyyden (B) avulla. Löylyhuone suunnitellaan niin tilavaksi, että siellä mahtuu kääntymään suihkutuolilla.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (440, 4, 'Sauna, ovi, vapaa leveys', 'Oven kulkuaukon leveys (A) liikuntaesteisille suunnittellussa saunatilassa. Vertailuarvona asuntosaunan oven leveys (B) 2. lähteestä.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1029, 4, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus on 150 lx. Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx. Wc-tiloissa esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1143, 5, 'Sauna, kiuas, lattiaan upotettu kiuas', NULL, 'Kiuas upotetaan lattiaan niin, että kiuaskivet ovat noin lattiapinnan tasossa. Tällöin löylyä saadaan myös penkkien istumakorkeudella.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1233, 5, 'Sauna, kiuas, lattiaan upotettu kiuas, kiuassyvennys', NULL, 'Kiuassyvennyksen on oltava riittävän tilava, jotta se pystytään puhdistamaan.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1173, 5, 'Sauna, kiuas, lattiaan upotettu kiuas, lattiakaivo', NULL, 'Kiuassyvennyksessä pitää olla lattiakaivo.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1237, 5, 'Sauna, kiuas, lattiaan upotettu kiuas, ritilälattia', NULL, 'Siivouksen vuoksi saunan koko lattia on irrotettava ritilälattia, jonka alapuolella on varsinainen lattiakaivollinen lattiapinta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (252, 1, 'Sisäänkäynti, katos', NULL, 'Sisäänkäynnin porras, luiska ja ulko-ovi suojataan sateelta katoksella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1232, 2, 'Ovi, oviaukko, vapaa leveys, ulko-ovi', NULL, 'Asuntojen ulko- ja porrastaso-oviksi suositellaan leveydeltään 10 M-ovia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1008, 2, 'Sisäänkäynti, havaittavuus', NULL, 'Sisäänkäyntiä tulisi korostaa esim. katoksella, jotta sen pystyy erottamaan rakennuksessa.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (254, 2, 'Sisäänkäynti, tasanne, korkeus', 'Ulko-oven edessä olevan tasanteen tai ritilän korkeus (C) kulkuväylän pinnasta.', 'Arvo C on enintään 20 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1238, 2, 'Sisäänkäynti, tasanne, luiska', NULL, 'Ovi ei saa avautua luiskaa käyttävän henkilön tielle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (958, 2, 'Sisäänkäynti, tasoero, luiska', 'Tasoero (C) maanpinnan ja sisäänkäynnin lattian välillä.', 'Arvo C on enintään 500 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (260, 2, 'Tuulikaappi, mitoitus, kääntöovi aukeaa poispäin', 'Tuulikaapin tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1042, 2, 'Valaistus, kulkuväylä sisällä', 'Valaistusvoimakkuus (C) eteisissä, auloissa ja käytävillä.', 'Arvo C on välillä 80 ... 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (564, 3, 'Kalusteet, ovi, avautuminen', NULL, 'Ovien avautumiskulma 180º, jolloin törmäysvaara pienenee.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (562, 3, 'Kalusteet, ovi, leveys', 'Ovileveys (C). Leveät ovet aiheuttavat törmäysvaaran. Törmäysvaara voidaan poistaa saranoimalla leveät ovet useampaan osaan taittuviksi tai käyttämällä liuku- ja rulo-ovia.', 'Arvo C on enintään 400 mm.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (764, 3, 'Kalusteet, ovi, saranat', NULL, 'Saranoiden avautumiskulman on oltava 180 astetta, jolloin törmäysvaara pienenee. Saranaovien poisto on oltava helppoa, mutta ne eivät saa irrota paikoiltaan tahattomasta tönäisystä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (852, 3, 'Ovi, aukipysymisaika', 'Aukipysymisaikaa (A) säätäessä otetaan huomioon hitaasti liikkuvat henkilöt. Mikäli ovi avataan painonappia tai painiketta käyttäen automaattisesti, riippuu tarvittava aika painikkeen ja oven välisestä etäisyydestä.', 'Arvo A on vähintään 25 s.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1242, 3, 'Ovi, kynnyskorkeus, asunto', 'Kynnyksen korkeus (C). Asunnoissa ja kulkuväylillä suositellaan kynnyksettömiä ovia.', 'Arvo C on enintään 20 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (702, 3, 'Seinä, varusteet, peiliseinä', NULL, 'Hahmottamisen ja häikäisyn takia ei peiliseiniä kulkuväylille.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (253, 3, 'Sisäänkäynti, tasanne', NULL, 'Oven avautumistilan lisäksi tarvitaan tilaa pyörätuolin kääntymiseen ja oven avaamiseen ja sulkemiseen pyörätuolista käsin.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (255, 3, 'Sisäänkäynti, tasanne, kaltevuus', 'Ulko-oven edessä olevan tasanteen tai ritilän kaltevuus (C) kulkusuunnassa.', 'Arvo C on enintään 2 %.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (256, 3, 'Tuulikaappi', NULL, 'Tuulikaappi vaikeuttaa usein liikkumista. Ahtaat tuulikaapit painavine ovineen ovat vaikeakulkuisia heikkovoimaisille ja apuvälinein liikkuville.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (268, 3, 'Tuulikaappi, lattia, matto/ritilä', NULL, 'Tuulikaappiin ei sovellu paksu, pehmeä matto. Jos käytetään ritilää, se ei saa olla liukas ja sen rakojen enimmäisleveys on 5 mm. Tuulikaapissa olevan säleikön tai maton enimmäiskorkeus on 20 mm; hyvä ratkaisu on maton upotussyvennys.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1011, 3, 'Valaistus, eteinen', NULL, 'Valaistusvoimakkuus eteisessä on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (458, 4, 'Eteinen, kalusteet, naulakko', 'Pyörätuolin käyttäjälle soveltuvan naulakon korkeus (C). Sokkelittomaan naulakkoon on helpompi ulottua pyörätuolista kuin sokkelilla varustettuun.', 'Arvo C on välillä 1100 ... 1200 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1010, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, asunto', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1400 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (453, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, avustaja', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 1400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1140, 4, 'Pyörätuolin pesutila, asunto', NULL, 'Käynti pesutilaan suoraan ulkoa tai pesutila sijoitetaan sisäänkäynnin lähelle esim. kuraeteiseen tai  WC-tilaan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (370, 4, 'Pyörätuolin pesutila, asunto, lattiakaivo', NULL, 'Irrallisella hiekkasuodattimella varustettu lattiakaivo WC:ssä pyörätuolin pesua varten, jos ei ole erillistä pesupaikkaa.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (265, 4, 'Tuulikaappi, mitoitus, kääntyminen 90 astetta', 'Tuulikaapin  minimileveys (A) ja minimisyvyys(B), silloin kun ovet ovat eri seinillä ja tuulikaapissa joutuu kääntymään.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (259, 4, 'Tuulikaappi, mitoitus, kääntöovi aukeaa kohti', 'Tuulikaapin minimileveys (A) ja minimisyvyys(B).', 'Tuulikaapin syvyys vähintään 1500 mm + ovilehden leveys.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (266, 4, 'Tuulikaappi, mitoitus, kääntöovi aukeaa kohti', 'Avustettavan pyörätuolin käyttäjän tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 2400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1102, 4, 'Tuulikaappi, mitoitus, kääntöovi, ulkosähköpyörätuoli', 'Tuulikaapin syvyys (A) ulkosähköpyörätuolille.', 'Arvo A on vähintään 2400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (793, 4, 'Valaistus, eteinen', NULL, 'Valaistusvoimakkuus eteisessä on 150 lx.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (763, 5, 'Kalusteet', NULL, 'Kalusteiden paikkaa tai korkeutta voidaan säätää sähköisesti tai kiskokiinnityksellä.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1195, 1, 'WC, ovi, avautuminen', NULL, 'Wc:stä katsottuna wc-oven aukeamissuunta on ulospäin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1196, 1, 'WC-/peseytymistila, ovi, lukko', NULL, 'Lukko avattavissa myös ulkopuolelta ns. vahtimestarin avaimella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1198, 1, 'WC-/peseytymistila, varusteet', NULL, 'Kerrostalossa, jossa edellytetään hissiä, asuinhuoneiston WC-/peseytymistilan on sovelluttava myös pyörätuolin käyttäjälle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1296, 1, 'Yhteistilat, asunto', NULL, 'Asuntojen yhteisten tilojen tulee soveltua myös liikkumisesteisille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (350, 2, 'Pesuallas, etäisyys seinästä', 'Pesualtaan etäisyys seinästä (A). Pesuallas asennetaan ulos seinästä polvitilan aikaansaamiseksi.', 'Arvo A on vähintään 200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (348, 2, 'Pesuallas, kiinnitys', 'Altaan kiinnitys kestää painon (A) ja altaaseen tukeutumisen.', 'Arvo A on vähintään 150 kg.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1162, 2, 'Suihku, käsisuihku, korkeus lattiasta', 'Suihkutangon asennuskorkeus (C). Suihkutangossa on korkeussäädettävä käsisuihku.', 'Arvo C on välillä 1000 ... 2000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1197, 2, 'Suihku, käsituki', NULL, 'Tukitanko tai -kahva sijoitetaan suihkutilaan ja kylpyammeen viereen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (300, 2, 'WC-istuin, vapaa tila takana', 'Avustajan tilantarve (A) wc-istuimen takana. Vertailuarvona tilantarve (B) yhteistilojen wc:ssä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (968, 3, 'Kalusteet, kaappi, etäisyys sivuseinästä', 'Syvyyden (A) vertailuarvona syvyys (B) 2. lähteestä. Kaappiin ulottumista helpottaa, kun ovi aukeaa yli 90 astetta. Avaamiseen tarvittava vapaa tila kaapin edessä 1300 mm.', 'Arvo A on vähintään 200 mm. Arvo B on vähintään 400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (564, 3, 'Kalusteet, ovi, avautuminen', NULL, 'Ovien avautumiskulma 180º, jolloin törmäysvaara pienenee.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (562, 3, 'Kalusteet, ovi, leveys', 'Ovileveys (C). Leveät ovet aiheuttavat törmäysvaaran. Törmäysvaara voidaan poistaa saranoimalla leveät ovet useampaan osaan taittuviksi tai käyttämällä liuku- ja rulo-ovia.', 'Arvo C on enintään 400 mm.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (765, 3, 'Kalusteet, ovi, rakenne', NULL, 'Oveen voidaan kiinnittää esim. lankakoreja ja koukkuja, jolloin tavarat ovat helposti ulottuvilla.', '8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (764, 3, 'Kalusteet, ovi, saranat', NULL, 'Saranoiden avautumiskulman on oltava 180 astetta, jolloin törmäysvaara pienenee. Saranaovien poisto on oltava helppoa, mutta ne eivät saa irrota paikoiltaan tahattomasta tönäisystä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1243, 3, 'Käsituki, vinotuki', NULL, 'Vinotuki asennetaan wc-istuimen etulinjan kohdalle. Asennuskorkeus lattiasta 800 ja 1000 mm kts. kuva.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (346, 3, 'Pesuallas, laskutaso', NULL, 'Pesualtaan yhteydessä laskutaso, johon voi myös tukeutua.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (347, 3, 'Pesuallas, reuna', NULL, 'Altaan reunamuoto sellainen, johon on helppo tukeutua.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (357, 3, 'Pesuallas, sekoittaja', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (358, 3, 'Pesuallas, sekoittaja, sijoitus', NULL, 'Pesualtaan sekoittajaan on ulotuttava kurkottamatta. Ulottumisen helpottamiseksi voidaan käyttää tavallista pidempää vipua tai sijoittaa sekoittaja pesualtaan etuosaan.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1108, 3, 'Pesuallas, tyyppi, käsituki', NULL, 'Erikoisallas, jonka etureunassa on käsituki.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1286, 3, 'Pesuallas, tyyppi, säädettävä allas', NULL, 'Pesuallas voidaan säätää kiskojen avulla asukkaalle sopivalle korkeudelle.', '15,8,14,16,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1107, 3, 'Pesuallas, vapaa tila altaan edessä', 'Rollaattorin käyttäjän tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1188, 3, 'Pesuallas, vesi- ja viemärijohdot', NULL, 'Vesi- ja viemäriliitännöissä on otettava huomioon pesualtaan mahdollinen myöhempi asennus uuteen korkeuteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (359, 3, 'Pesuallas, väri', NULL, 'Väri- tai tummuusero taustaa vasten auttaa altaan havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (338, 3, 'Suihku, istuin', NULL, 'Kiinteä seinälle kääntyvä ja korkeussäädettävä suihkuistuin.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (341, 3, 'Suihku, istuin, etäisyys sivuseinästä', 'Suihkuistuimen etäisyys seinästä (C).', 'Arvo C on välillä 700 ... 800 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1174, 3, 'Suihku, istuin, korkeus', 'Suihkuistuimen korkeus (A) lattiasta.', 'Istuin asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on 500 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (339, 3, 'Suihku, istuin, käsituet', NULL, 'Suihkuistuimen käsituet ovat suihkuun siirtymisessä välttämättömät. Ne ulottuvat istuimen etureunaan asti.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (342, 3, 'Suihku, istuin, poikkituen etäisyys', NULL, 'Poikkituen etäisyydeksi suihkutuolin etureunasta suositellaan 550 mm. Istuimen etäisyys sivuseinästä 700 - 800 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (344, 3, 'Suihku, istuin, vapaa tila edessä', 'Rollaattorin kääntämiseen tarvittava vapaa tila (A) suihkuistuimen edessä, kun istuinta lähestytään etuviistosta.', 'Arvo A on vähintään 1050 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (343, 3, 'Suihku, sekoittaja, etäisyys seinästä', NULL, 'Suihkusekoittaja sijoitetaan sivuseinälle suihkuistuimen etureunan kohdalle (n. 450 mm seinästä), jotta suihkussa istuttaessa ei tarvitse kurotella.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1136, 3, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus WC/peseytymistilassa on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1056, 3, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 200 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (627, 3, 'WC-/peseytymistila, käsituki, materiaali', NULL, 'Mattapintainen, muovipäällysteinen käsituki on suositeltavampi kuin metallipintainen. Metallipintainen käsijohde on liukas ja se saattaa häikäistä ja aiheuttaa allergiaa (nikkeli).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (690, 3, 'WC-/peseytymistila, lattia, kaltevuus', NULL, 'Peseytymistilan lattian suositeltava kaltevuus on 1,25 % (1:80), lattiakaivon ympärillä 2% (1:50).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1157, 3, 'WC-/peseytymistila, lattia, lattialämmitys', NULL, 'Lattialämmitys on suositeltava.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1156, 3, 'WC-/peseytymistila, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1158, 3, 'WC-/peseytymistila, lattiakaivo', NULL, 'Lattiakaivoa ei saa sijoittaa kulkuväylälle eikä paikkaan, jossa seisotaan (esim. suihkussa) liukastumisvaaran takia. Kannen on oltava tukeva ja pysyttävä hyvin paikallaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1236, 3, 'Wc-/peseytymistila, mitoitus, allas viistottu', 'Wc-/peseytymistilan leveys 2100 mm ja syvyys 2400 mm. Wc -istuimen etäisyys takaseinästä 150 mm. Wc -istuimen keskilinjan etäisyys sivuseinästä 800 mm ja toisella puolella 1300 mm.Tila soveltuu rajoitetusti myös avustettavalle henkilölle.', '', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (361, 3, 'WC-/peseytymistila, ovi, avaaminen', NULL, 'Ovi avattavissa ja lukittavissa yhdellä kädellä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (369, 3, 'WC-/peseytymistila, varusteet, peili, yläreunan korkeus', 'Peilin yläreunan korkeus (C). Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo C on välillä 1700 ... 1800 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1203, 3, 'WC-istuin, etäisyys sivuseinästä', NULL, 'WC-istuimen keskilinjan etäisyys sivuseinästä on 400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (314, 3, 'WC-istuin, huuhtelu', NULL, 'Vähäisellä käsivoimalla, valokennolla tai pitkällä vivulla toimiva laite.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (315, 3, 'WC-istuin, kansi', NULL, 'Kansi kestää istumisen (150 kg) ja pysyy tukevasti paikoillaan.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (310, 3, 'WC-istuin, korkeus', NULL, 'WC-istuimen normaali korkeus on 450 mm. Istumakorkeutta voi muuttaa korkeussäädettävässä wc-istuimessa. Tavalliseen WC- istuimeen voidaan asentaa istuinrenkaan korotusosa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (313, 3, 'WC-istuin, korkeus', 'Sopiva istumakorkeus (C) henkilölle, jolla on jäykät polvet tai lonkat.', 'Arvo C on välillä 500 ... 550 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (321, 3, 'WC-istuin, käsisuihku', 'Käsisuihkun etäisyys (C) WC-istuimelta. WC-istuimelta on ulotuttava käsisuihkuun ja sen hanaan.', 'Arvo C on enintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (322, 3, 'WC-istuin, käsituet', NULL, 'Käsituet ovat käännettävissä tai nostettavissa pois avustajan tieltä ja niiden pitää salpautua sivulle tai ylös.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (329, 3, 'WC-istuin, käsituet, etäisyys toisistaan', 'Käsitukien välinen etäisyys (C).', 'Arvo C on välillä 550 ... 600 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (324, 3, 'WC-istuin, käsituet, korkeus lattiasta', 'Käsitukien korkeus (C) lattiasta. Vaihtoehtoisesti korkeussäädettävät käsituet.', 'Arvo C on välillä 750 ... 800 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (326, 3, 'WC-istuin, käsituet, pituus', NULL, 'Käsituet ulottuvat vähintään wc-istuimen etureunaan, mutta enintään 100 mm etureunan ohitse. Julkisissa WC-tiloissa enintään 200 mm etureunan ohitse.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1206, 3, 'WC-istuin, käsituki, poikkituki', NULL, 'Istuimen eteen sijoitettava poikkituki ulottuu 100 mm wc-istuimen keskilinjan ohi, jotta siihen voi tarttua kaksin käsin.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (334, 3, 'WC-istuin, paperirullateline', NULL, 'Paperirullaan on ulotuttava WC- istuimelta kurkottamatta. Paperirullatelineen voidaan kiinnittää käsitukeen.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (303, 3, 'WC-istuin, vapaa tila', 'Rollaattorin kääntämiseen tarvitava vapaa tila wc-istuimen edessä. Pesualtaan sijaitessa wc-istuimen edessä riittää mitta (B), jos rollaattorin pyörät ja etuosa mahtuvat osittain pesualtaan alle. Wc -istuimen etäisyys (C) sivuseinästä.', 'Arvo A on vähintään 1050 mm. Arvo B on vähintään 800 mm. Arvo C on välillä 600 ... 650 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (525, 4, 'Kalusteet, polvitilan vesijohdot', NULL, 'Polvitilassa olevat kuumavesiputket yms. asennetaan lähelle seinää ja suojataan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1202, 4, 'Pesuallas, etäisyys sivuseinästä', 'Normaalikokoisen pesualtaan (esim. 550 x 400 mm) keskilinjan etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (349, 4, 'Pesuallas, korkeus lattiasta', 'Korkeus (C) lattiasta. Pyörätuolin käyttäjälle soveltuvan pesualtaan asennuskorkeus. Pyörätuolilla pääsee lähemmäksi allasta, jos sen pohja on vino.', 'Arvo C on välillä 770 ... 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1103, 4, 'Pesuallas, korkeus lattiasta', 'Korkeus (C) lattiasta. Pesuallas asennetaan käyttäjän yksilölliselle korkeudelle.', 'Arvo C on välillä 700 ... 950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (355, 4, 'Pesuallas, muoto', NULL, 'Kovera etureuna helpottaa ulottumista ja kumartumista altaan ylle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (353, 4, 'Pesuallas, polvitila', 'Esitetty polvitilan leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1105, 4, 'Pesuallas, tyyppi, korkeussäädettävä allas', NULL, 'Käyttäjä voi säätää sähköisesti korkeussäädettävän altaan sopivalle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1106, 4, 'Pesuallas, tyyppi, siirrettävä allas', NULL, 'WC-istuimen vierellä sivusuunnassa kiskoilla siirrettävä allas, jolloin sitä käytetään WC-istuimella istuen. Istuimen vierestä vapautuu tilaa avustajalle, kun allas siirretään pois.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (351, 4, 'Pesuallas, vapaa tila altaan edessä', 'Vapaan tilan syvyys (A).', 'Arvo A on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1140, 4, 'Pyörätuolin pesutila, asunto', NULL, 'Käynti pesutilaan suoraan ulkoa tai pesutila sijoitetaan sisäänkäynnin lähelle esim. kuraeteiseen tai  WC-tilaan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (370, 4, 'Pyörätuolin pesutila, asunto, lattiakaivo', NULL, 'Irrallisella hiekkasuodattimella varustettu lattiakaivo WC:ssä pyörätuolin pesua varten, jos ei ole erillistä pesupaikkaa.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (340, 4, 'Suihku, istuin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1029, 4, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus on 150 lx. Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx. Wc-tiloissa esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (790, 4, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 300 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1235, 4, 'WC-/peseytymistila, mitoitus, allas nurkassa', 'Wc- tilan mitoitus esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1900 mm. Arvo B on vähintään 2400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (368, 4, 'WC-/peseytymistila, varusteet, peili, alareunan korkeus', 'Pesualtaan yläpuolella oleva peilin alareunan korkeus (C) lattiasta riippuu pesualtaan korkeudesta.', 'Arvo C on välillä 800 ... 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (311, 4, 'WC-istuin, korkeus', 'Wc-istuimen korkeus (C) on lähes sama kuin pyörätuolin istuimen korkeus. Jos käytetään reiällistä suihku/wc-tuolia, silloin wc-istuin on normaalin korkuinen.', 'Arvo C on välillä 480 ... 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (302, 4, 'WC-istuin, vapaa tila', 'Suoraan edestäpäin wc-istuimelle siirryttäessä tarvittavan tilan syvyys(A) ja leveys (B).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (308, 4, 'WC-istuin, vapaa tila', NULL, 'Vapaa tila istuimen edessä on vähintään 1050 mm. WC-istuimen keskilinjan etäisyys sivuseinästä on vähintään 650 - 700 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (356, 5, 'Pesuallas, muoto', NULL, 'Allastasossa tai tasoon upotetussa altaassa selkeä reuna, jotta allas erottuu tasosta', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1104, 5, 'Pesuallas, tyyppi, käännettävä allas', 'Altaan leveys (A) ja syvyys (B). Allas, joka voidaan kääntää vinoon asentoon, soveltuu vinossa asennossa pyörätuolin käyttäjälle ja suorana muille käyttäjille. Polvitilaa lisää, jos se asennetaan 200 mm irti seinästä.', 'Arvo A on vähintään 670 mm. Arvo B on vähintään 430 mm, mutta arvoksi suositellaan vähintään 630 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (316, 5, 'WC-istuin, kansi, väri', NULL, 'Väri tai tummuskontrasti istuinta vasten.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1204, 5, 'WC-istuin, tyyppi', NULL, 'Seinään kiinnitettävä wc-istuin voidaan asentaa yksilölliselle korkeudelle. Esim. rollaattorin pyörät ja etuosa mahtuvat osittain istuimen alle ja lisäävät kääntymistilaa. Vapaa lattiapinta helpottaa myös siivousta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1195, 1, 'WC, ovi, avautuminen', NULL, 'Wc:stä katsottuna wc-oven aukeamissuunta on ulospäin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1196, 1, 'WC-/peseytymistila, ovi, lukko', NULL, 'Lukko avattavissa myös ulkopuolelta ns. vahtimestarin avaimella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1198, 1, 'WC-/peseytymistila, varusteet', NULL, 'Kerrostalossa, jossa edellytetään hissiä, asuinhuoneiston WC-/peseytymistilan on sovelluttava myös pyörätuolin käyttäjälle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1296, 1, 'Yhteistilat, asunto', NULL, 'Asuntojen yhteisten tilojen tulee soveltua myös liikkumisesteisille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (350, 2, 'Pesuallas, etäisyys seinästä', 'Pesualtaan etäisyys seinästä (A). Pesuallas asennetaan ulos seinästä polvitilan aikaansaamiseksi.', 'Arvo A on vähintään 200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (348, 2, 'Pesuallas, kiinnitys', 'Altaan kiinnitys kestää painon (A) ja altaaseen tukeutumisen.', 'Arvo A on vähintään 150 kg.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (300, 2, 'WC-istuin, vapaa tila takana', 'Avustajan tilantarve (A) wc-istuimen takana. Vertailuarvona tilantarve (B) yhteistilojen wc:ssä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (968, 3, 'Kalusteet, kaappi, etäisyys sivuseinästä', 'Syvyyden (A) vertailuarvona syvyys (B) 2. lähteestä. Kaappiin ulottumista helpottaa, kun ovi aukeaa yli 90 astetta. Avaamiseen tarvittava vapaa tila kaapin edessä 1300 mm.', 'Arvo A on vähintään 200 mm. Arvo B on vähintään 400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (564, 3, 'Kalusteet, ovi, avautuminen', NULL, 'Ovien avautumiskulma 180º, jolloin törmäysvaara pienenee.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (562, 3, 'Kalusteet, ovi, leveys', 'Ovileveys (C). Leveät ovet aiheuttavat törmäysvaaran. Törmäysvaara voidaan poistaa saranoimalla leveät ovet useampaan osaan taittuviksi tai käyttämällä liuku- ja rulo-ovia.', 'Arvo C on enintään 400 mm.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (765, 3, 'Kalusteet, ovi, rakenne', NULL, 'Oveen voidaan kiinnittää esim. lankakoreja ja koukkuja, jolloin tavarat ovat helposti ulottuvilla.', '8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (764, 3, 'Kalusteet, ovi, saranat', NULL, 'Saranoiden avautumiskulman on oltava 180 astetta, jolloin törmäysvaara pienenee. Saranaovien poisto on oltava helppoa, mutta ne eivät saa irrota paikoiltaan tahattomasta tönäisystä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (580, 3, 'Kalusteet, siivousvälinekaappi', 'Kaapin leveys (A). Kaappi, josta sokkeli on poistettu pölynimurin liikuttelun helpottamiseksi.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (579, 3, 'Kalusteet, siivousvälinekaappi, silityslauta', NULL, 'Keittiön tai apukeittiön kaapistoon sijoitetaan silityskaappi, josta silityslauta voidaan kääntää ulos jalattomana ulokkeena halutulle korkeudelle. Tällöin sitä on helppo käyttää istuen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1243, 3, 'Käsituki, vinotuki', NULL, 'Vinotuki asennetaan wc-istuimen etulinjan kohdalle. Asennuskorkeus lattiasta 800 ja 1000 mm kts. kuva.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (346, 3, 'Pesuallas, laskutaso', NULL, 'Pesualtaan yhteydessä laskutaso, johon voi myös tukeutua.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (347, 3, 'Pesuallas, reuna', NULL, 'Altaan reunamuoto sellainen, johon on helppo tukeutua.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (357, 3, 'Pesuallas, sekoittaja', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (358, 3, 'Pesuallas, sekoittaja, sijoitus', NULL, 'Pesualtaan sekoittajaan on ulotuttava kurkottamatta. Ulottumisen helpottamiseksi voidaan käyttää tavallista pidempää vipua tai sijoittaa sekoittaja pesualtaan etuosaan.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1108, 3, 'Pesuallas, tyyppi, käsituki', NULL, 'Erikoisallas, jonka etureunassa on käsituki.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1286, 3, 'Pesuallas, tyyppi, säädettävä allas', NULL, 'Pesuallas voidaan säätää kiskojen avulla asukkaalle sopivalle korkeudelle.', '15,8,14,16,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1107, 3, 'Pesuallas, vapaa tila altaan edessä', 'Rollaattorin käyttäjän tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1188, 3, 'Pesuallas, vesi- ja viemärijohdot', NULL, 'Vesi- ja viemäriliitännöissä on otettava huomioon pesualtaan mahdollinen myöhempi asennus uuteen korkeuteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (359, 3, 'Pesuallas, väri', NULL, 'Väri- tai tummuusero taustaa vasten auttaa altaan havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1136, 3, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus WC/peseytymistilassa on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1056, 3, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 200 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (627, 3, 'WC-/peseytymistila, käsituki, materiaali', NULL, 'Mattapintainen, muovipäällysteinen käsituki on suositeltavampi kuin metallipintainen. Metallipintainen käsijohde on liukas ja se saattaa häikäistä ja aiheuttaa allergiaa (nikkeli).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (690, 3, 'WC-/peseytymistila, lattia, kaltevuus', NULL, 'Peseytymistilan lattian suositeltava kaltevuus on 1,25 % (1:80), lattiakaivon ympärillä 2% (1:50).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1157, 3, 'WC-/peseytymistila, lattia, lattialämmitys', NULL, 'Lattialämmitys on suositeltava.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1156, 3, 'WC-/peseytymistila, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1158, 3, 'WC-/peseytymistila, lattiakaivo', NULL, 'Lattiakaivoa ei saa sijoittaa kulkuväylälle eikä paikkaan, jossa seisotaan (esim. suihkussa) liukastumisvaaran takia. Kannen on oltava tukeva ja pysyttävä hyvin paikallaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (361, 3, 'WC-/peseytymistila, ovi, avaaminen', NULL, 'Ovi avattavissa ja lukittavissa yhdellä kädellä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (369, 3, 'WC-/peseytymistila, varusteet, peili, yläreunan korkeus', 'Peilin yläreunan korkeus (C). Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo C on välillä 1700 ... 1800 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1203, 3, 'WC-istuin, etäisyys sivuseinästä', NULL, 'WC-istuimen keskilinjan etäisyys sivuseinästä on 400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (314, 3, 'WC-istuin, huuhtelu', NULL, 'Vähäisellä käsivoimalla, valokennolla tai pitkällä vivulla toimiva laite.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (315, 3, 'WC-istuin, kansi', NULL, 'Kansi kestää istumisen (150 kg) ja pysyy tukevasti paikoillaan.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (310, 3, 'WC-istuin, korkeus', NULL, 'WC-istuimen normaali korkeus on 450 mm. Istumakorkeutta voi muuttaa korkeussäädettävässä wc-istuimessa. Tavalliseen WC- istuimeen voidaan asentaa istuinrenkaan korotusosa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (313, 3, 'WC-istuin, korkeus', 'Sopiva istumakorkeus (C) henkilölle, jolla on jäykät polvet tai lonkat.', 'Arvo C on välillä 500 ... 550 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (321, 3, 'WC-istuin, käsisuihku', 'Käsisuihkun etäisyys (C) WC-istuimelta. WC-istuimelta on ulotuttava käsisuihkuun ja sen hanaan.', 'Arvo C on enintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (322, 3, 'WC-istuin, käsituet', NULL, 'Käsituet ovat käännettävissä tai nostettavissa pois avustajan tieltä ja niiden pitää salpautua sivulle tai ylös.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (329, 3, 'WC-istuin, käsituet, etäisyys toisistaan', 'Käsitukien välinen etäisyys (C).', 'Arvo C on välillä 550 ... 600 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (324, 3, 'WC-istuin, käsituet, korkeus lattiasta', 'Käsitukien korkeus (C) lattiasta. Vaihtoehtoisesti korkeussäädettävät käsituet.', 'Arvo C on välillä 750 ... 800 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (326, 3, 'WC-istuin, käsituet, pituus', NULL, 'Käsituet ulottuvat vähintään wc-istuimen etureunaan, mutta enintään 100 mm etureunan ohitse. Julkisissa WC-tiloissa enintään 200 mm etureunan ohitse.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1206, 3, 'WC-istuin, käsituki, poikkituki', NULL, 'Istuimen eteen sijoitettava poikkituki ulottuu 100 mm wc-istuimen keskilinjan ohi, jotta siihen voi tarttua kaksin käsin.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (334, 3, 'WC-istuin, paperirullateline', NULL, 'Paperirullaan on ulotuttava WC- istuimelta kurkottamatta. Paperirullatelineen voidaan kiinnittää käsitukeen.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (303, 3, 'WC-istuin, vapaa tila', 'Rollaattorin kääntämiseen tarvitava vapaa tila wc-istuimen edessä. Pesualtaan sijaitessa wc-istuimen edessä riittää mitta (B), jos rollaattorin pyörät ja etuosa mahtuvat osittain pesualtaan alle. Wc -istuimen etäisyys (C) sivuseinästä.', 'Arvo A on vähintään 1050 mm. Arvo B on vähintään 800 mm. Arvo C on välillä 600 ... 650 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (525, 4, 'Kalusteet, polvitilan vesijohdot', NULL, 'Polvitilassa olevat kuumavesiputket yms. asennetaan lähelle seinää ja suojataan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1202, 4, 'Pesuallas, etäisyys sivuseinästä', 'Normaalikokoisen pesualtaan (esim. 550 x 400 mm) keskilinjan etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (349, 4, 'Pesuallas, korkeus lattiasta', 'Korkeus (C) lattiasta. Pyörätuolin käyttäjälle soveltuvan pesualtaan asennuskorkeus. Pyörätuolilla pääsee lähemmäksi allasta, jos sen pohja on vino.', 'Arvo C on välillä 770 ... 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1103, 4, 'Pesuallas, korkeus lattiasta', 'Korkeus (C) lattiasta. Pesuallas asennetaan käyttäjän yksilölliselle korkeudelle.', 'Arvo C on välillä 700 ... 950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (355, 4, 'Pesuallas, muoto', NULL, 'Kovera etureuna helpottaa ulottumista ja kumartumista altaan ylle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (353, 4, 'Pesuallas, polvitila', 'Esitetty polvitilan leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1105, 4, 'Pesuallas, tyyppi, korkeussäädettävä allas', NULL, 'Käyttäjä voi säätää sähköisesti korkeussäädettävän altaan sopivalle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1106, 4, 'Pesuallas, tyyppi, siirrettävä allas', NULL, 'WC-istuimen vierellä sivusuunnassa kiskoilla siirrettävä allas, jolloin sitä käytetään WC-istuimella istuen. Istuimen vierestä vapautuu tilaa avustajalle, kun allas siirretään pois.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (351, 4, 'Pesuallas, vapaa tila altaan edessä', 'Vapaan tilan syvyys (A).', 'Arvo A on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1140, 4, 'Pyörätuolin pesutila, asunto', NULL, 'Käynti pesutilaan suoraan ulkoa tai pesutila sijoitetaan sisäänkäynnin lähelle esim. kuraeteiseen tai  WC-tilaan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (370, 4, 'Pyörätuolin pesutila, asunto, lattiakaivo', NULL, 'Irrallisella hiekkasuodattimella varustettu lattiakaivo WC:ssä pyörätuolin pesua varten, jos ei ole erillistä pesupaikkaa.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1029, 4, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus on 150 lx. Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx. Wc-tiloissa esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (790, 4, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 300 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (368, 4, 'WC-/peseytymistila, varusteet, peili, alareunan korkeus', 'Pesualtaan yläpuolella oleva peilin alareunan korkeus (C) lattiasta riippuu pesualtaan korkeudesta.', 'Arvo C on välillä 800 ... 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (311, 4, 'WC-istuin, korkeus', 'Wc-istuimen korkeus (C) on lähes sama kuin pyörätuolin istuimen korkeus. Jos käytetään reiällistä suihku/wc-tuolia, silloin wc-istuin on normaalin korkuinen.', 'Arvo C on välillä 480 ... 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (302, 4, 'WC-istuin, vapaa tila', 'Suoraan edestäpäin wc-istuimelle siirryttäessä tarvittavan tilan syvyys(A) ja leveys (B).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (308, 4, 'WC-istuin, vapaa tila', NULL, 'Vapaa tila istuimen edessä on vähintään 1050 mm. WC-istuimen keskilinjan etäisyys sivuseinästä on vähintään 650 - 700 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (356, 5, 'Pesuallas, muoto', NULL, 'Allastasossa tai tasoon upotetussa altaassa selkeä reuna, jotta allas erottuu tasosta', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1104, 5, 'Pesuallas, tyyppi, käännettävä allas', 'Altaan leveys (A) ja syvyys (B). Allas, joka voidaan kääntää vinoon asentoon, soveltuu vinossa asennossa pyörätuolin käyttäjälle ja suorana muille käyttäjille. Polvitilaa lisää, jos se asennetaan 200 mm irti seinästä.', 'Arvo A on vähintään 670 mm. Arvo B on vähintään 430 mm, mutta arvoksi suositellaan vähintään 630 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (316, 5, 'WC-istuin, kansi, väri', NULL, 'Väri tai tummuskontrasti istuinta vasten.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1204, 5, 'WC-istuin, tyyppi', NULL, 'Seinään kiinnitettävä wc-istuin voidaan asentaa yksilölliselle korkeudelle. Esim. rollaattorin pyörät ja etuosa mahtuvat osittain istuimen alle ja lisäävät kääntymistilaa. Vapaa lattiapinta helpottaa myös siivousta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (179, 1, 'Kulkuväylä, turvallisuus', NULL, 'Ajoneuvoliikenne tontilla ei saisi ristetä jalankulkuliikenteen kanssa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (211, 1, 'Kulkuväylä, törmäysvaara', NULL, 'Kulkuväylällä ei saa olla törmäysvaaraa aiheuttavia rakenteita. Rakenteen törmäysvaara estetään esim. kaiteella ja siitä varoitetaan värillä tai materiaalilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1226, 1, 'Ovi, automaattiovi', NULL, 'Automaattioven avautuminen kulkuväylälle suunnitellaan ja toteutetaan niin, ettei törmäysvaaraa synny.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1227, 1, 'Ovi, automaattiovi, havaittavuus', NULL, 'Oven edustalle lattiaan tehdään tarvittaessa oven kääntymisaluetta kuvaava turvamerkintä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1229, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Kulkuväylältä hisseihin tai tasonvaihtojärjestelmiin sekä hallinto-, palvelu- ja liiketiloihin johtavien ovien vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (176, 2, 'Kulkuväylä, opastava, pääovi', NULL, 'Kulkuväylän, joka ohjaa pääovelle, on oltava esteetön ja helposti havaittava.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (187, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli julkisessa tilassa', 'Pyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1232, 2, 'Ovi, oviaukko, vapaa leveys, ulko-ovi', NULL, 'Asuntojen ulko- ja porrastaso-oviksi suositellaan leveydeltään 10 M-ovia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (232, 3, 'Kulkuväylä, kalusteet, sijoitus', NULL, 'Tontilla olevat kalusteet, kuten valaisinpylväät, opasteet, roskalaatikot, pyörätelineet ja penkit on sijoitettava kulkuväylän ulkopuolelle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (172, 3, 'Kulkuväylä, koordinaatisto', NULL, 'Kulkuväylien on oltava selkeässä koordinaatistossa ja yhtenäisiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (213, 3, 'Kulkuväylä, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton. Ulkona esim. asfaltti, betoni, kivituhka ja laatta.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1208, 3, 'Kulkuväylä, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (216, 3, 'Kulkuväylä, pinta, kuvio', NULL, 'Materiaalikontrasti ja kuviot auttaa suunnistautumista. Tummat kuviot vaalealla yksivärisellä pohjalla ovat selkeimpiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (217, 3, 'Kulkuväylä, pinta, kuvio, opastava', NULL, 'Opastava kuvio esim. pitkittäinen pyöristetty kohokuvio, jonka korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (895, 3, 'Kulkuväylä, pinta, väri', NULL, 'Kulkuväylän tai yksityiskohdan (esim. jalkalistan) väri- ja tummuuskontrasti auttaa suunnistautumista ja varoittaa vaarasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (171, 3, 'Kulkuväylä, pituus', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (177, 3, 'Kulkuväylä, päättyminen', NULL, 'Kulkureitti ei saa johtaa umpikujaan.', '3,7,11,14,15,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (693, 3, 'Lattia, pinta, kuvio', NULL, 'Lattiassa suuret, voimakasväriset kuviot haittaavat suunnistautumista ja tekevät yleisvaikutelman heikkonäköiselle sekavaksi. Heikkonäköinen voi erehtyä pitämään lattian tummaa kohtaa aukkona tai tasoerona lattiassa.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (692, 3, 'Lattia, pinta, kuvio, opastava', NULL, 'Kulkusuunnan suuntainen kuviointi helpottaa suunnistautumista.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (940, 3, 'Liikkumisen tilantarve, automaattiovi aukeaa kohti, ulkopyörätuoli', 'Tilantarve ulko-oven oven edessä  (A), silloin kun tasanteen syvyys on mitta (B). Vieressä alas johtava porras tai luiska. Jos oven edessä on alasjohtava porras, tarvitaan tilaa enemmän kuin 2000 mm.Oviaukon vapaa leveys 900 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 2700 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (192, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, kulman avartaminen', NULL, 'Kulkuväylän (leveys  900 mm) kääntymiskulmaa avarretaan niin, että kulman mitat ovat 1150 x 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (251, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, leveys', 'Kulkuväylän leveys (A) ja (B) kulkuväylän kääntyessä.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (718, 3, 'Opaste, korkeus lattiasta', 'Opasteen sijoituskorkeus (C), myös koho- ja syvennyskuvio-opasteet.', 'Arvo C on välillä 1400 ... 1600 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (717, 3, 'Opaste, materiaali', NULL, 'Pintamateriaali kiiltämätön ja heijastamaton, lasipintaisessa opasteessa heijastamaton lasi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (728, 3, 'Opaste, sijoitus, ovi', NULL, 'Huonetilan opasteet sijoitetaan oven aukeamispuolelle törmäysvaaran välttämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (719, 3, 'Opaste, teksti, kirjaintyyppi', NULL, 'Suurehko ja selvä, esim. Helvetica Medium.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (721, 3, 'Opaste, teksti, kirjaintyyppi, pienet kirjaimet', NULL, 'Pienellä kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (704, 3, 'Opaste, teksti, korkeus', 'Tekstin korkeus (C) opasteissa, jotka luetaan läheltä.', 'Arvo A on vähintään 15 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (705, 3, 'Opaste, teksti, korkeus, sijainti-tai suuntakilpi', 'Tekstin korkeus (A) sijaintia ja suuntaa osoittavissa opasteissa, jotka luetaan muutaman metrin etäisyydeltä.', 'Arvo C on välillä 25 ... 40 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (722, 3, 'Opaste, väri, kontrasti', NULL, 'Tekstin ja symbolin väri- tai tummuuskontrasti taustaa vasten. Parhaiten erottuu musta teksti valkoisella pohjalla.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (852, 3, 'Ovi, aukipysymisaika', 'Aukipysymisaikaa (A) säätäessä otetaan huomioon hitaasti liikkuvat henkilöt. Mikäli ovi avataan painonappia tai painiketta käyttäen automaattisesti, riippuu tarvittava aika painikkeen ja oven välisestä etäisyydestä.', 'Arvo A on vähintään 25 s.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (853, 3, 'Ovi, avaamis- ja säätölaitteet, painonappi, sijoitus', NULL, 'Automaattioven painonappi sijoitetaan siten, ettei aukeava ovi aiheuta törmäämisvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1246, 3, 'Ovi, turvallisuus', NULL, 'Oveen suositellaan ikkunaa 900-1800 mm:n korkeudelle törmäysvaaran estämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (231, 3, 'Pihakalusteet', NULL, 'Kalusteen on oltava tukeva ja paikallaan pysyvä ja siinä ei saa olla teräviä särmiä ja kulmia. Materiaali ei saa aiheuttaa allergiaa.', '3,7,8,20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (702, 3, 'Seinä, varusteet, peiliseinä', NULL, 'Hahmottamisen ja häikäisyn takia ei peiliseiniä kulkuväylille.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (819, 3, 'Valaistus, opaste, ulkopuolelta', NULL, 'Opasteen valaisu helpottaa sen löytymistä. Häikäisemätön valo, josta ei muodostu häiritseviä varjoja.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (785, 3, 'Valaistus, sisäänkäynti', NULL, 'Valaistuksella tasoitetaan valaistuseroja sisä- ja ulkotilojen välillä ja estetään häikäistyminen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (863, 3, 'Valaistus, yhteistilat, kulkuväylä', NULL, 'Valaistusvoimakkuus yhteistilojen eteisissä, auloissa, käytävissä, luiskissa ja portaissa on 200 lx.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1059, 3, 'Valaistus, yhteistilat, kulkuväylä', NULL, 'Valaistusvoimakkuus yhteistilojen eteisissä, auloissa, käytävissä, luiskissa ja portaissa on 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (458, 4, 'Eteinen, kalusteet, naulakko', 'Pyörätuolin käyttäjälle soveltuvan naulakon korkeus (C). Sokkelittomaan naulakkoon on helpompi ulottua pyörätuolista kuin sokkelilla varustettuun.', 'Arvo C on välillä 1100 ... 1200 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (236, 4, 'Kulkuväylä, kalusteet, kalustevyöhyke', NULL, 'Kalustevyöhyke tulisi erottaa kulkuväylästä poikkeavalla pintamateriaalilla ja/tai -värityksellä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (222, 4, 'Kulkuväylä, reuna', NULL, 'Suunnistautumista helpottaa kulkuväylän rajoittuminen selvästi poikkeavan tuntuiseen pintaan.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (223, 4, 'Kulkuväylä, reuna, kaide', NULL, 'Kulkuväylän reunalla kulkuesteaitana voi olla esim. avokaide, jossa on vaakajohteet sekä 900 mm:n (käsijohde) että 100 mm:n korkeudella.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1212, 4, 'Kulkuväylä, reuna, korkeus', NULL, 'Turvallisessa kulkuväylässä on korotetun reunuksen lisäksi kaide tai istutuksia. Tai reunus on riittävän korkea (noin 600 mm), ettei siitä aiheudu kompastumisvaaraa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (833, 4, 'Liikkumisen tilantarve, kulkuväylä kääntyy, yhteenlaskettu leveys', 'Risteävien kulkuväylien a ja b yhteen laskettu leveys (A). Kulkusuunnan kääntyessä 90 astetta, a:n ja b:n leveys vähintään 900 mm.', 'Arvo A on vähintään 2300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (190, 4, 'Liikkumisen tilantarve, pyörätuolien kohdatessa', 'Kulkuväylän leveyden (A) vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1010, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, asunto', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1400 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (453, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, avustaja', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 1400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (907, 4, 'Liikkumisen tilantarve, ulkosähköpyörätuolien kohdatessa', 'Tilantarve esitetty kulkuväylän leveyden (A) avulla.', 'Arvo A on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (731, 4, 'Opaste, sijoitus, kartat', NULL, 'Rakennuksessa suunnistautumista opastavat kohokartat sijoitetaan hyvin näkyville, esimerkiksi ulko-oven viereen.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (725, 4, 'Opaste, teksti, kohokirjain, korkeus', 'Kirjaimen korkeus pinnasta (A) ja kirjaimen korkeus (C). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo A on vähintään 1 mm. Arvo C on välillä 15 ... 40 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (716, 4, 'Opaste, teksti, korkeus, nimikilpi', 'Tekstin korkeus (C) nimikilvissä, jotka luetaan 1..3 m etäisyydeltä.', 'Arvo C on välillä 70 ... 100 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (726, 4, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', NULL, 'Pyörätuolin käyttäjän pistekirjoitusopasteen korkeus lattiasta on 1100 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (818, 4, 'Valaistus, opaste, sisäpuolelta', NULL, 'Sisältäpäin valaistussa opasteessa vaalea teksti tummalla pohjalla häikäisyn estämiseksi.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (657, 4, 'Yhteistilat, pyörätuolin pesutila, kerrostalo', NULL, 'Käynti pesutilaan, joka sijoitetaan pyörätuolin säilytystilan yhteyteen, suoraan ulkoa. Laitoskäyttöön tarkoitettu pyörien harjauslaite voi tulla kyseeseen, jos on useita pyörätuolin käyttäjiä.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (658, 4, 'Yhteistilat, pyörätuolin pesutila, lattiakaivo', NULL, 'Varustettu hiekanerottimella tai vastaavalla laitteella.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1137, 4, 'Yhteistilat, pyörätuolin pesutila, mitoitus', 'Pesutilan tilantarve esitetty syvyyden (A) ja leveyden (B) avulla.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1089, 5, 'Liikkumisen tilantarve, opaskoiran kanssa', 'Opaskoiraa käyttävän henkilön tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 1100 mm.', '3');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (724, 5, 'Opaste, kieli', NULL, 'Opasteisiin soveltuu selkokieli, jota ymmärtävät kaikki. Erityisesti sitä tarvitsevat kehitys- ja monivammaiset sekä esim. viittomakieltä äidinkielenään käyttävät.', '1,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (730, 5, 'Opaste, rakennuksen opastusjärjestelmä', NULL, 'Hälytysäänen ohella myös visuaalinen viesti ja synteettinen puheviesti.', '1,3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1179, 5, 'Opaste, teksti, kohokirjain, poikkileikkaus', NULL, 'Kohokirjaimen poikkileikkaus on kolmioimainen, jonka yläsärmää pitkin sormea voi liuuttaa. Yläsärmä ei saa olla terävä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1043, 5, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', 'Opasteen korkeus (C) lattiasta.', 'Arvo C on välillä 1300 ... 1400 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1180, 5, 'Opaste, teksti, syvennyskirjain, korkeus', NULL, 'Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta. Kirjaimen syvyys tai korkeus pinnasta 1 mm ja kirjaimen korkeus 30-60 mm. Kirjaimen korkeus 15-40 mm (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1181, 5, 'Opaste, teksti, syvennyskirjain, leveys', 'Kirjaimen leveys eli syvennysuran leveys (A). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo C on välillä 6 ... 10 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (744, 5, 'Ovi, avaamis- ja säätölaitteet, ulko-ovi', NULL, 'Raskaat ovet joko automaattisesti avautuvia tai kaukosäätimellä avattavia.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (699, 5, 'Seinä ja katto, akustiikka', NULL, 'Kuulovammaiselle pidetään yleensä lyhyttä jälkikaiunta-aikaa sopivana. Tarvittava vaimennus asunnossa saadaan käyttämällä sisustuksessa mattoja, verhoja yms. vaimentavia tekstiilejä.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1028, 1, 'Hissi, henkilöhissi, mitoitus', 'Hissin mitoitus esitetty ovisivun leveyden (A) ja korin syvyyden (B) avulla.', 'Arvo A on vähintään 1100 mm. Arvo B on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 2100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1257, 1, 'Hissi, muu nosto ja siirtolaite, kerroskorkeus', NULL, 'Toiminnallisesti toisiinsa yhteydessä olevien kerrostasojen välillä tulee olla hissi tai muu nostolaite, ellei yhteyttä ole mahdollista järjestää toisiinsa liittyvillä luiskilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (401, 1, 'Hissi, oviaukko, mitoitus', 'Mitoitus esitetty leveyden (A) ja korkeuden (B) avulla.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 2100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1249, 1, 'Hissi, yhteistilat', NULL, 'Hissivaatimus kerrostalossa riippuu kerrosten lukumäärästä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1229, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Kulkuväylältä hisseihin tai tasonvaihtojärjestelmiin sekä hallinto-, palvelu- ja liiketiloihin johtavien ovien vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (397, 2, 'Hissi, henkilöhissi, mitoitus', 'Mitoitus esitetty ovisivun leveyden (A) ja korin syvyyden (B) avulla.Suositusmitta (2. lähde) mahdollistaa pyörätuolin kääntymisen.', 'Arvo A on vähintään 1340 mm, mutta arvoksi suositellaan vähintään 1600 mm. Arvo B on vähintään 1400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (410, 2, 'Hissi, hälytysjärjestelmä', NULL, 'Hississä on oltava hälytysjärjestelmä, jossa on puheyhteys palvelukeskukseen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (412, 2, 'Hissi, hälytysjärjestelmä, etäisyys sivuseinästä', 'Hälytyslaitteiston etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (411, 2, 'Hissi, hälytysjärjestelmä, korkeus korin lattiasta', 'Hälytyslaitteiston korkeus (C) lattiasta.', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1264, 2, 'Hissi, kaita/minihissi, ovi, syvyys', NULL, '', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1260, 2, 'Hissi, kaita/minihissi, sijoitus', NULL, '', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1259, 2, 'Hissi, kaita/minihissi, sijoitus porrashuoneessa', NULL, '', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1258, 2, 'Hissi, kaitahissi, mitoitus', 'Mitat annettu hissikorin sisäleveyden (A) ja sisäsyvyyden (B) avulla. Kuilun ulkoleveys 1000 mm ja ulkosyvyys 2160 mm. Oven vapaa leveys (C).', '', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1262, 2, 'Hissi, kaitahissi, vapaa tila tulotasanteella', 'Tilantarve hissin oven edessä esitetty syvyyden (A) ja leveyden (B) avulla. Leveys (B) on etäisyys portaan reunasta hissin keskilinjaan.', '', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1263, 2, 'Hissi, kaitahissi, vapaa tila tulotasanteella', 'Tilantarve hissin oven edessä esitetty syvyyden (A) ja leveyden (B) avulla. Leveys (B) on etäisyys portaan reunasta hissin keskilinjaan.', '', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (437, 2, 'Hissi, kerrosmerkintä', NULL, 'Hissin oven eteen lattiaan ja vastakkaiselle seinälle merkitään suurikokoinen kerrosta osoittava numero', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1251, 2, 'Hissi, käsijohde', NULL, 'Korin yhdellä seinällä on oltava käsijohde.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (427, 2, 'Hissi, lattia', NULL, 'Hissin lattian on oltava märkänäkin luistamaton.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1252, 2, 'Hissi, lukumäärä, porrashuone', NULL, 'Vanhustentaloihin suositellaan vähintään kaksi hissiä porrashuonetta kohti. Vanhustentaloihin suositellaan aina 13 henkilön hissiä. Osa hisseistä mitoitetaan paarikuljetukseen soveltuvaksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1261, 2, 'Hissi, minihissi, mitoitus', 'Mitoitus esitetty hissikorin vapaan sisäleveyden (A) ja sisäsyvyyden (B) avulla. Oven vapaa leveys (C).', '', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1265, 2, 'Hissi, muu nosto- ja siirtolaite, porrashissi, mitoitus', NULL, '', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (844, 2, 'Hissi, muu nosto- ja siirtolaite, pystyhissi, mitoitus', 'Mitoitus esitetty kulkuaukkosivun leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 1600 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (424, 2, 'Hissi, painike, ulosmenopainike', NULL, 'Ulosmenopainike erottuu selkeästi vihreällä värillä ja se on korkeammalla kuin muut painikkeet.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (425, 2, 'Hissi, painikejärjestys', NULL, 'Painikejärjestyksen on oltava selkeä ja looginen, esim. kaksirivinen painikepaneeli voi olla epälooginen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1012, 2, 'Hissi, painikepaneeli, korkeus lattiasta', 'Hallintalaitteiston sijoituskorkeus (C).', 'Arvo C on välillä 1300 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1254, 2, 'Hissi, porrashuone', NULL, 'Erityisesti liikkumisesteisille suunnitellussa rakennuksessa tulee portaiden yhteydessä aina olla hissi.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1255, 2, 'Hissi, porrashuone', NULL, 'Kerrostaloissa, joihin ei määräysten mukaan tarvitse rakentaa hissiä, suositellaan varautumaan hissin rakentamiseen myöhemmin joko porrashuoneeseen tai rakennuksen ulkopuolelle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1256, 2, 'Hissi, sijoitus porrashuoneessa', NULL, 'Hissikuilua ei sijoiteta makuuhuoneiden vastaisille seinille. Asuinhuoneiden vastaisille seinille kuilujen sijoittamista on vältettävä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1253, 2, 'Hissi, valinta', NULL, 'Asuinkerrostalon hissin valintaan vaikuttaa erityisesti rakennuksen kerrosluku, asukasmäärä, hissin toiminnalliset ja suorituskykyvaatimukset ja viranomaismääräykset.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (404, 2, 'Hissi, vapaa tila pysähdystasolla', NULL, 'Vapaa kääntymistila hissin oven edessä vähintään 1500 x 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1250, 2, 'Hissi, vapaa tila pysähdystasolla, julkinen rakennus', NULL, 'Tilantarve hissin oven edessä on syvyyssuunnassa 1,5 x  korin syvyys. Jos vastapäätä alaspäin johtavat portaat tai luiska, syvyys min. 2000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1030, 2, 'Hissi, vapaa tila pysähdystasolla, julkinen rakennus, hissit vastakkain', NULL, 'Tilantarve hissin oven edessä on syvyyssuunnassa 2 x  korin syvyys. Mitoituksen perusteena tungostilanne.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (187, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli julkisessa tilassa', 'Pyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1027, 2, 'Painike/vedin, ovi, etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys (A) alasjohtavasta portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1031, 3, 'Hissi, henkilö/tavarahissi, mitoitus', 'Mitoitus esitetty kulkuaukkosivun leveyden (A) ja syvyyden (B) avulla. Mitoitus mahdollistaa pyörätuolilla kääntymisen.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 2400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (416, 3, 'Hissi, istuin', NULL, 'Hissiin asennetaan ylöskäännettävä istuin.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (417, 3, 'Hissi, istuin, mitoitus', 'Istuimen mitat esitetty leveyden (A) ja syvyyden (C) avulla.', 'Arvo A on vähintään 400 mm. Arvo C on välillä 300 ... 400 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (418, 3, 'Hissi, istuin, sijoitus', 'Istuimen reunan etäisyys (A) sivuseinästä. Istumakorkeus on noin 500 mm.', 'Arvo A on vähintään 200 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1086, 3, 'Hissi, muu nosto- ja siirtolaite', NULL, 'Toiminnallisesti toisiinsa liittyvien kerrostasojen välillä tulee olla pyörätuolin käyttäjälle soveltuva hissi tai muu tasonvaihtojärjestelmä, ellei luiska ole mahdollinen.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (398, 3, 'Hissi, muu nosto- ja siirtolaite, mitoitus', 'Mitoitus esitetty kulkuaukkosivun (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1600 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1266, 3, 'Hissi, muu nosto- ja siirtolaite, porrastuolihissi, mitoitus', NULL, '', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (422, 3, 'Hissi, painike,  halkaisija', NULL, 'Arvo A on vähintään 25 mm.', '16,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (420, 3, 'Hissi, painike, kirjaimen tai numeron korkeus', 'Hissipainikkeen sormin tunnusteltavan kirjaimen tai numeron korkeus (A) painikkeen pinnasta.', 'Arvo A on vähintään 1 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (421, 3, 'Hissi, painike, kirjaimen tai numeron väri', NULL, 'Kirjaimella tai numerolla  väri- tai tummuuskontrasti taustaa vasten.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (406, 3, 'Hissi, painike, kutsupainike pysähdystasolla', NULL, 'Painikkeen on oltava taustapinnastaan koholla, väri- tai tummuuskontrastiltaan erottuva ja alaspainettuna valaistu.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (407, 3, 'Hissi, painikepaneeli, korkeus lattiasta', 'Hissin tai muun nostolaitteen pyörätuolin käyttäjälle soveltuvan hallintalaitteiston sijoituskorkeus (C).', 'Arvo C on välillä 900 ... 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (408, 3, 'Hissi, painikepaneeli, kulma', NULL, 'Pyöratuolin käyttäjälle tarkoitetun painikepaneelin asennuskulma on 45 astetta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (423, 3, 'Hissi, painikepaneeli, painikkeiden etäisyys', 'Painikkeiden riittävä etäisyys (A) auttaa niiden erottamista toisistaan.', 'Arvo A on vähintään 10 mm.', '3,7,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (428, 3, 'Hissi, peili, alareunan korkeus', 'Peilin alareunan korkeus (C) lattiasta.', 'Arvo C on välillä 300 ... 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (430, 3, 'Hissi, peili, leveys', NULL, 'Peili on leveydeltään hissin takaseinän levyinen.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (431, 3, 'Hissi, peili, sijoitus', NULL, 'Peili sijoitetaan hissin takaseinälle, jolloin se auttaa pyörätuolin ohjaamista.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (429, 3, 'Hissi, peili, yläreunan korkeus', 'Peilin yläreunan korkeus (A) lattiasta.', 'Arvo A on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (405, 3, 'Hissi, vapaa tila pysähdystasolla, pyörätuoli', 'Tilantarve oven edessä esitetty syvyyden (A) ja leveyden (B) avulla.Syvyys (C) silloin kun edessä on alasjohtava porras tai luiska.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 1500 mm. Arvo C on vähintään 2000 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (217, 3, 'Kulkuväylä, pinta, kuvio, opastava', NULL, 'Opastava kuvio esim. pitkittäinen pyöristetty kohokuvio, jonka korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (736, 3, 'Kytkin/säädin, etäisyys alasjohtavasta portaasta tai luiskasta', 'Kytkimen tai säätimene etäisyys alasjohtavasta portaasta tai luiskasta (A).', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (940, 3, 'Liikkumisen tilantarve, automaattiovi aukeaa kohti, ulkopyörätuoli', 'Tilantarve ulko-oven oven edessä  (A), silloin kun tasanteen syvyys on mitta (B). Vieressä alas johtava porras tai luiska. Jos oven edessä on alasjohtava porras, tarvitaan tilaa enemmän kuin 2000 mm.Oviaukon vapaa leveys 900 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 2700 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1013, 3, 'Valaistus, hissi', NULL, 'Valaistusvoimakkuus hississä on 200 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1269, 4, 'Hissi, hälytysjärjestelmä', NULL, 'Hissin hälytysjärjestelmään tulee liittää myös visuaalinen kahdensuuntainen järjestelmä, joka kertoo myös, että lähetetty hälytys on mennyt perille.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (860, 4, 'Hissi, muu nosto- ja siirtolaite, tasoero', NULL, 'Jos tasoero toiminnallisesti toisiinsa yhteydessä olevien kerrostasojen välillä on suurempi kuin 1000 mm, suositellaan hissiä tai muuta tasonvaihtolaitteistoa luiskan sijasta.', '11,14,15,20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (409, 4, 'Hissi, painike, havaittavuus', NULL, 'Painikkeen on erotuttava painikepaneelista väriltään ja sen oltava alaspainettuna valaistu.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (419, 4, 'Hissi, painike, kirjaimen tai numeron koko', 'Painikkeen kohokirjaimen tai -numeron on oltava riittävän iso.', 'Arvo A on vähintään 15 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (432, 4, 'Hissi, äänimerkki', NULL, 'Hississä on oltava pysähdystasolle saapumisesta ilmoittava synteettinen ääni-ilmaisin.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (190, 4, 'Liikkumisen tilantarve, pyörätuolien kohdatessa', 'Kulkuväylän leveyden (A) vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (907, 4, 'Liikkumisen tilantarve, ulkosähköpyörätuolien kohdatessa', 'Tilantarve esitetty kulkuväylän leveyden (A) avulla.', 'Arvo A on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (848, 4, 'Valaistus, hissi', NULL, 'Valaistusvoimakkuus hississä on 300 lx.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (957, 4, 'Valaistus, hissi, kohdevalaistus', NULL, 'Hissin oven ja painikepaneelin kohdevalaistus auttaa niiden havaitsemista.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1089, 5, 'Liikkumisen tilantarve, opaskoiran kanssa', 'Opaskoiraa käyttävän henkilön tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 1100 mm.', '3');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (816, 2, 'Valaistus, yhteistilat', 'Yhteistilojen, jätetilojen ja varastojen yleisvalaistusvaatimus.', 'Valaistusvoimakkuus varastotiloissa on 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (839, 3, 'Kytkin/säädin, valopainike, sijoitus', NULL, 'Valopainike sijoitetaan kulkureitin varrelle helposti havaittavaan ja ulottuvissa olevaan paikkaan.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (668, 3, 'Yhteistilat, jätetila, sijoitus', 'Jätetilan etäisyys (C) sisäänkäynnistä. Jätetila sijoitetaan pääkulkureitin varrelle.', 'Arvo C on enintään 50 m.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (669, 3, 'Yhteistilat, jätetila, säiliöt', NULL, 'Käytettävissä myös heikoilla voimilla ja pyörätuolissa istuen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (670, 3, 'Yhteistilat, jätetila, säiliöt, lajittelu', NULL, 'Eri laatuisten jätteiden säiliöiden merkintä selvästi.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1229, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Kulkuväylältä hisseihin tai tasonvaihtojärjestelmiin sekä hallinto-, palvelu- ja liiketiloihin johtavien ovien vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (515, 2, 'Keittiö, kalusteet, työtaso uunin vieressä, leveys', 'Uunin vieressä olevan laskutason leveys (A). Ellei uunin vieressä ole työtasoa, laskutaso voi olla kaapistosta ulosvedettävä aputaso, joka on samalla korkeudella avatun uuniluukun kanssa.', 'Arvo A on vähintään 400 mm.', '14,3,7,8,11,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (481, 2, 'Keittiö, tyyppi, II-keittiö, mitoitus, sisäänvedetty sokkeli', 'Kalusteryhmien välinen etäisyys (A).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (476, 2, 'Keittiö, tyyppi, mitoitus', NULL, 'Kalusteryhmien etäisyydet mitoitetaan niin, että tilaa on riittävästi apuvälineiden kanssa liikkumiseen. Jos (perhe)keittiö on samalla läpikulkutila, mitoituksessa on huomioitava ohituksen tilantarve.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (537, 2, 'Keittiö, vesikaluste, materiaali', NULL, 'Vesikaluste ei saa aiheuttaa allergista reaktiota (esim. kromi).', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (535, 2, 'Keittiö, vesikaluste, sekoittaja, vesiputki', NULL, 'Pitkä ja korkea hanan juoksuputki mahdollistaa astian täyttämisen pöytätasolla. Juoksuputken on käännyttävä pöytätason yläpuolelle.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (187, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli julkisessa tilassa', 'Pyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (523, 3, 'Kalusteet, taso/pöytä, polvitilan korkeus', NULL, 'Polvitilan korkeus on 150 mm työtuolin korkeutta suurempi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (519, 3, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 450 mm. Arvo C on välillä 600 ... 650 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1019, 3, 'Keittiö, kalusteet, astianpesupöytä, altaan syvyys', 'Altaan syvyysmitta (A).', 'Astianpesualtaan normaali syvyys on 150 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1020, 3, 'Keittiö, kalusteet, astianpesupöytä, korkeus', 'Korkeusmitta (C).', 'Arvo C on välillä 850 ... 900 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (554, 3, 'Keittiö, kalusteet, jätekaappi', NULL, 'Pöytätason alla oleva ulosvedettävä jätekaappi tai kiskoilla liikkuvat jätesäiliöt.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1022, 3, 'Keittiö, kalusteet, seinäkaappi, korkeus työtason pinnasta', 'Yläkaappien asennuskorkeus (C).', 'Arvo C on välillä 370 ... 500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (488, 3, 'Keittiö, kalusteet, tukitanko', NULL, 'Tukitanko pöydän etureunassa helpottaa liikkumista.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1135, 3, 'Keittiö, kalusteet, työtaso lieden vieressä, leveys', 'Työtason leveys (A). Lieden molemmin puolin tarvitaan vapaata laskutilaa. Ainakin toinen laskutila on kuumuutta kestävää materiaalia.', 'Arvo A on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1182, 3, 'Keittiö, kalusteet, työtaso, korkeus', 'Työtason korkeus lattiasta (C).', 'Arvo C on välillä 750 ... 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1099, 3, 'Keittiö, kalusteet, työtaso, sijoitus', NULL, 'Keskeisimmän työtason paras sijotus on lieden ja astianpesupöydän välissä. Työtason suositeltava leveys on 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (508, 3, 'Keittiö, laitteet, liesi, korkeus', NULL, 'Samalla korkeudella työtason kanssa, jotta astiat voidaan vetää liedeltä niitä nostamatta.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (574, 3, 'Keittiö, laitteet, säiliöpakastin', NULL, 'Säiliöpakastin ei sovi liikkumisesteisille ulottuvuusvaikeuksien takia. Se on hankala myös näkövammaisille, koska järjestystä on vaikea säilyttää.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1037, 3, 'Keittiö, laitteet, uuni, korkeus lattiasta', 'Uuninluukun alareunan korkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1200 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (516, 3, 'Keittiö, laitteet, uuninluukku', NULL, 'Turvallisessa uuninluukussa on turvalukko ja iso, käteensopiva vedin. Luukku on sivusaranoitu ja se aukeaa 180 astetta.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1018, 3, 'Keittiö, mitoitus', NULL, 'Mitoituksessa on huomioitava yksilölliset vaatimukset ja apuvälineiden tilantarve.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1141, 3, 'Keittiö, pistorasia', NULL, 'Pienkoneiden käyttöä helpottaa lieden lähelle työtason etureunaan sijoitetut pistorasiat.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (477, 3, 'Keittiö, tyyppi, I-keittiö', NULL, 'I-keittiön pinta-ala ja etäisyydet työpisteiden välillä kasvavat suuriksi perhekeittiöissä. Tällöin keittiötyyppi aiheuttaa esineiden siirtelyä ja kuljettamista, mikä on hankalaa apuvälineen kanssa.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (533, 3, 'Keittiö, vesikaluste, sekoittaja', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1123, 3, 'Keittiö, vesikaluste, sekoittaja, sijoitus', NULL, 'Sekoittajaan on ulotuttava kurkottamatta. Ulottumisen helpottamiseksi käytetään tavallista pidempää vipua tai sijoitetaan sekoittaja pesualtaan etuosaan.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1246, 3, 'Ovi, turvallisuus', NULL, 'Oveen suositellaan ikkunaa 900-1800 mm:n korkeudelle törmäysvaaran estämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1039, 3, 'Valaistus, keittiö', NULL, 'Valaistusvoimakkuus keittiössä on 200 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1024, 3, 'Valaistus, keittiö, kohdevalaistus', NULL, 'Valaistusvoimakkuus keittiössä työtasoilla on 300 lx. Sopiva  valaistusvoimakkuus lähityöskentelyssä 400 - 1000 lx (2. lähde).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (525, 4, 'Kalusteet, polvitilan vesijohdot', NULL, 'Polvitilassa olevat kuumavesiputket yms. asennetaan lähelle seinää ja suojataan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (521, 4, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (517, 4, 'Keittiö, kalusteet, astianpesupöytä, korkeus', 'Korkeussmitta (C). Pesualtaiden alla siirrettävä laatikosto, jolloin pyörätuolin käyttäjä saa jalkatilaa.', 'Arvo C on välillä 800 ... 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (555, 4, 'Keittiö, kalusteet, seinäkaappi, korkeus työtason pinnasta', 'Yläkaappien asennuskorkeus (C) määräytyy yksilöllisen ulottuvuuden mukaan.', 'Arvo C on välillä 250 ... 300 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1023, 4, 'Keittiö, kalusteet, työtaso, korkeus', 'Työtason korkeus lattiasta (C).', 'Arvo C on välillä 850 ... 900 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (495, 4, 'Keittiö, kalusteet, työtaso, mitoitus', 'Työtason mitat esitetty leveyden (A) ja korkeuden (C) avulla. Liikuntaesteisen keittiössä tarvitaan työtason alla polvitilaa.', 'Arvo A on vähintään 800 mm. Arvo C on välillä 700 ... 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (569, 4, 'Keittiö, laitteet, jääkaappi/pakastin, korkeus lattiasta', 'Sijoituskorkeus (C) lattiasta.', 'Jääkaappi/pakastimen sopiva sijoituskorkeus on 400 mm lattiasta. Kun jääkaapi sijoitetaan sokkelille, niin alimmatkin hyllyt ovat ulottuvilla.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (510, 4, 'Keittiö, laitteet, liesi, polvitila', 'Mitoitus esitetty leveyden (A) avulla. Polvitila on tasolieden vieressä tai osittain sen alla.', 'Arvo A on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (575, 4, 'Valaistus, keittiö', NULL, 'Valaistusvoimakkuus keittiössä on 300 lx. Sopiva yleisvalaistuksen voimakkuus 150 - 300 lx ( 2. lähde)', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (577, 4, 'Valaistus, keittiö, kohdevalaistus', NULL, 'Valaistusvoimakkuus keittiössä työtasoilla on 500 lx. Sopiva  valaistusvoimakkuus lähityöskentelyssä 400 - 1000 lx (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (489, 5, 'Keittiö, kalusteet, väri', NULL, 'Suositeltava keittiökalusteiden väritys on sellainen, jossa on tummat alakaapit, vaaleat työtasot ja vaaleat yläkaapit.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1032, 5, 'Keittiö, laitteet, astianpesukone, korkeus lattiasta', 'Sijoituskorkeus (A) lattiasta. Jos astianpesukone on sijoitettu liian alas, on astioiden nostaminen ja koneeseen laittaminen vaikeaa.', 'Arvo A on vähintään 400 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (538, 5, 'Keittiö, laitteet, astianpesukone, sijoitus', NULL, 'Astianpesukonetta ei voi sijoittaa astianpesualtaiden kohdalle, koska altaiden alla tarvitaan polvitilaa. Pyörätuolin käyttäjä ei ulotu täyttämään leveää konetta kokonaan samalta puolelta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1034, 5, 'Keittiö, laitteet, liesi', NULL, 'Erillinen tasoliesi jättää polvitilaa työtason alle ja mahdollistaa astioiden siirtelyn työtasoa pitkin. Kaapistoon upotettu uuni on hyvällä työskentelykorkeudella.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (512, 5, 'Keittiö, laitteet, uuni', NULL, 'Erillinen kaapistoon upotettu uuni on hyvällä työskentelykorkeudella ja vapauttaa polvitilaa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (513, 5, 'Keittiö, laitteet, uuni, korkeus lattiasta', 'Uuninluukun alareunan korkeus (C) lattiasta.', 'Arvo C on välillä 700 ... 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (536, 5, 'Keittiö, säätimet', NULL, 'Säätimien numerot ja kirjaimet selkeitä ja isokokoisia, luettavissa myös sormin tunnustellen.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1229, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Kulkuväylältä hisseihin tai tasonvaihtojärjestelmiin sekä hallinto-, palvelu- ja liiketiloihin johtavien ovien vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (187, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli julkisessa tilassa', 'Pyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (693, 3, 'Lattia, pinta, kuvio', NULL, 'Lattiassa suuret, voimakasväriset kuviot haittaavat suunnistautumista ja tekevät yleisvaikutelman heikkonäköiselle sekavaksi. Heikkonäköinen voi erehtyä pitämään lattian tummaa kohtaa aukkona tai tasoerona lattiassa.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (692, 3, 'Lattia, pinta, kuvio, opastava', NULL, 'Kulkusuunnan suuntainen kuviointi helpottaa suunnistautumista.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (192, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, kulman avartaminen', NULL, 'Kulkuväylän (leveys  900 mm) kääntymiskulmaa avarretaan niin, että kulman mitat ovat 1150 x 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (251, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, leveys', 'Kulkuväylän leveys (A) ja (B) kulkuväylän kääntyessä.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1246, 3, 'Ovi, turvallisuus', NULL, 'Oveen suositellaan ikkunaa 900-1800 mm:n korkeudelle törmäysvaaran estämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (702, 3, 'Seinä, varusteet, peiliseinä', NULL, 'Hahmottamisen ja häikäisyn takia ei peiliseiniä kulkuväylille.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (819, 3, 'Valaistus, opaste, ulkopuolelta', NULL, 'Opasteen valaisu helpottaa sen löytymistä. Häikäisemätön valo, josta ei muodostu häiritseviä varjoja.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (833, 4, 'Liikkumisen tilantarve, kulkuväylä kääntyy, yhteenlaskettu leveys', 'Risteävien kulkuväylien a ja b yhteen laskettu leveys (A). Kulkusuunnan kääntyessä 90 astetta, a:n ja b:n leveys vähintään 900 mm.', 'Arvo A on vähintään 2300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (190, 4, 'Liikkumisen tilantarve, pyörätuolien kohdatessa', 'Kulkuväylän leveyden (A) vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (907, 4, 'Liikkumisen tilantarve, ulkosähköpyörätuolien kohdatessa', 'Tilantarve esitetty kulkuväylän leveyden (A) avulla.', 'Arvo A on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1268, 4, 'Opaste, äänensiirtojärjestelmä, julkinen tila', NULL, 'T-merkki julkisissa tiloissa kertoo kuulolaitteen käyttäjälle induktiosilmukan käyttömahdollisuudesta.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (471, 4, 'Ruokailutila, ruokapöytä, etäisyys kiintokalusteista', 'Ruokapöydän etäisyyden kiintokalusteista (A) vertailuarvona etäisyys (B) 2. lähteestä.', 'Arvo A on vähintään 1100 mm. Arvo B on vähintään 1400 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (475, 4, 'Ruokailutila, ruokapöytä, syvyys', 'Ruokapöydän syvyys (A) silloin kun kaksi pyörätuolissa istuvaa henkilöä istuu vastakkain pöydän ääressä. Syvyys (B) silloin kun on yksi pyörätuolin käyttäjä, vertailuarvona syvyys (C) 2. lähteestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1050 mm. Arvo C on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (474, 4, 'Ruokailutila, ruokapöytä, vapaa ohitustila, pyörätuoli', 'Tilantarve (A) pöydän ääressä, jotta pyörätuolissa istuvan henkilön mahtuu ohittamaan.', 'Arvo A on vähintään 1300 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1116, 4, 'Ruokailutila, ruokapöytä, vapaa tila', 'Pyörätuolin vaatima leveys (A) ruokapöydän ääressä. Vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (962, 4, 'Ruokailutila, ruokapöytä, vapaa tila päädyssä', 'Vapaa tila esitetty alueena (A x B).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (472, 4, 'Ruokailutila, ruokapöytä, vapaa tila sivulla', 'Vapaa tila esitetty alueena (A x B).', 'Arvo A on vähintään 1100 mm. Arvo B on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (818, 4, 'Valaistus, opaste, sisäpuolelta', NULL, 'Sisältäpäin valaistussa opasteessa vaalea teksti tummalla pohjalla häikäisyn estämiseksi.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1089, 5, 'Liikkumisen tilantarve, opaskoiran kanssa', 'Opaskoiraa käyttävän henkilön tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 1100 mm.', '3');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (699, 5, 'Seinä ja katto, akustiikka', NULL, 'Kuulovammaiselle pidetään yleensä lyhyttä jälkikaiunta-aikaa sopivana. Tarvittava vaimennus asunnossa saadaan käyttämällä sisustuksessa mattoja, verhoja yms. vaimentavia tekstiilejä.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (187, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli julkisessa tilassa', 'Pyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (940, 3, 'Liikkumisen tilantarve, automaattiovi aukeaa kohti, ulkopyörätuoli', 'Tilantarve ulko-oven oven edessä  (A), silloin kun tasanteen syvyys on mitta (B). Vieressä alas johtava porras tai luiska. Jos oven edessä on alasjohtava porras, tarvitaan tilaa enemmän kuin 2000 mm.Oviaukon vapaa leveys 900 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 2700 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (192, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, kulman avartaminen', NULL, 'Kulkuväylän (leveys  900 mm) kääntymiskulmaa avarretaan niin, että kulman mitat ovat 1150 x 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (251, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, leveys', 'Kulkuväylän leveys (A) ja (B) kulkuväylän kääntyessä.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (833, 4, 'Liikkumisen tilantarve, kulkuväylä kääntyy, yhteenlaskettu leveys', 'Risteävien kulkuväylien a ja b yhteen laskettu leveys (A). Kulkusuunnan kääntyessä 90 astetta, a:n ja b:n leveys vähintään 900 mm.', 'Arvo A on vähintään 2300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (190, 4, 'Liikkumisen tilantarve, pyörätuolien kohdatessa', 'Kulkuväylän leveyden (A) vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (907, 4, 'Liikkumisen tilantarve, ulkosähköpyörätuolien kohdatessa', 'Tilantarve esitetty kulkuväylän leveyden (A) avulla.', 'Arvo A on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1089, 5, 'Liikkumisen tilantarve, opaskoiran kanssa', 'Opaskoiraa käyttävän henkilön tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 1100 mm.', '3');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (816, 2, 'Valaistus, yhteistilat', 'Yhteistilojen, jätetilojen ja varastojen yleisvalaistusvaatimus.', 'Valaistusvoimakkuus varastotiloissa on 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (839, 3, 'Kytkin/säädin, valopainike, sijoitus', NULL, 'Valopainike sijoitetaan kulkureitin varrelle helposti havaittavaan ja ulottuvissa olevaan paikkaan.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (693, 3, 'Lattia, pinta, kuvio', NULL, 'Lattiassa suuret, voimakasväriset kuviot haittaavat suunnistautumista ja tekevät yleisvaikutelman heikkonäköiselle sekavaksi. Heikkonäköinen voi erehtyä pitämään lattian tummaa kohtaa aukkona tai tasoerona lattiassa.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (692, 3, 'Lattia, pinta, kuvio, opastava', NULL, 'Kulkusuunnan suuntainen kuviointi helpottaa suunnistautumista.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1152, 3, 'Yhteistilat, varasto, liikkumisvälineet', NULL, 'Mitoituksessa on huomioitava asukkaiden ikärakenne; mitä enemmän talossa asuu ikäihmisiä, sitä enemmän tarvitaan varastotilaa apuvälineille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (645, 3, 'Yhteistilat, varasto, sijoitus', NULL, 'Sijoitetaan sisäänkäynnin lähelle. Kulkuväylä lyhyt ja helppokulkuinen. Varastoon johtavalla kulkuväylällä ei saa olla portaita, korkeita kynnyksiä tai muita esteitä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1010, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, asunto', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1400 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (453, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, avustaja', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 1400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (657, 4, 'Yhteistilat, pyörätuolin pesutila, kerrostalo', NULL, 'Käynti pesutilaan, joka sijoitetaan pyörätuolin säilytystilan yhteyteen, suoraan ulkoa. Laitoskäyttöön tarkoitettu pyörien harjauslaite voi tulla kyseeseen, jos on useita pyörätuolin käyttäjiä.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (658, 4, 'Yhteistilat, pyörätuolin pesutila, lattiakaivo', NULL, 'Varustettu hiekanerottimella tai vastaavalla laitteella.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1137, 4, 'Yhteistilat, pyörätuolin pesutila, mitoitus', 'Pesutilan tilantarve esitetty syvyyden (A) ja leveyden (B) avulla.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1139, 4, 'Yhteistilat, pyörätuolivarasto, ilmanvaihto', NULL, 'Hyvä tuuletus akkujen lataamisessa syntyvän vetykaasun poistamiseksi.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (654, 4, 'Yhteistilat, pyörätuolivarasto, kulkuväylä', 'Pyörätuolivarastoon johtavan kulkuväylän leveys (A).', 'Arvo A on vähintään 1600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (656, 4, 'Yhteistilat, pyörätuolivarasto, lämpötila', 'Lämpötila (C).', 'Arvo C on välillä 10 ... 25 astetta C.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (652, 4, 'Yhteistilat, pyörätuolivarasto, mitoitus', 'Pyörätuolin säilytystilan tilantarve esitetty leveyden (A) ja syvyyden (B) avulla. Tilaan  mahtuu kaksi pyörätuolia vierekkäin, jolloin voidaan siirtyä sisällä käytettävään pyörätuoliin.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 1400 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (653, 4, 'Yhteistilat, pyörätuolivarasto, mitoitus, avustaja', 'Pyörätuolin säilytystilan tilantarve esitetty leveyden (A) ja syvyyden (B) avulla. Tilaan  mahtuu kaksi pyörätuolia vierekkäin, jolloin voidaan siirtyä sisällä käytettävään pyörätuoliin.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 1400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (655, 4, 'Yhteistilat, pyörätuolivarasto, pistorasia', NULL, 'Säilytystilan varusteina on maadoitettu pistorasia sähköpyörätuolin akkujen lataamista varten.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (649, 4, 'Yhteistilat, varasto, liikkumisvälineet', NULL, 'Lämmitetyssä lastenvaunu- tai ulkoiluvälinevarastossa voidaan säilyttää myös sähkökäyttöisiä pyörätuoleja ja vanhusten kävelytukia. Esteetön pääsy ja sijainti sisäänkäynnin lähellä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (651, 4, 'Yhteistilat, varasto, liikkumisvälineet, ilmanvaihto', NULL, 'Säilytystilassa, jossa ladataan sähköpyörätuolien akut, on oltava riittävä ilmanvaihto.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (650, 4, 'Yhteistilat, varasto, liikkumisvälineet, mitoitus, pinta-ala', 'Pinta-ala (A).', 'Arvo A on vähintään 10 m2.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (646, 4, 'Yhteistilat, varasto, mitoitus', NULL, 'Varaston ja sen kulkuväylien on oltava tavanomaista tilavampia, jotta siellä pystyy liikkumaan pyörätuolilla.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (386, 1, 'Käsijohde, luiskat ja portaat, julkinen tila', NULL, 'Luiskat ja portaat varustetaan molemmin puolin yhtenäisillä käsijohteilla. Leveissä portaissa suositellaan portaan keskellä käsijohdetta 2400 mm:n välein.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1222, 1, 'Käsijohde, pituus, julkinen tila', 'Käsijohde ulotetaan luiskien ja portaiden alkamis- ja päättymiskohtien ohi. Käsijohteen ulokkeen pituus (A).', 'Arvo A on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1213, 1, 'Luiska, pituus', 'Luiska, jonka kaltevuus on 5 - 8 %, saa olla pituudeltaan (C) yhtäjaksoisena enintään kuusi metriä, jonka jälkeen tulee olla välitasanne.', 'Arvo C on enintään 6000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1217, 1, 'Luiska, pituuskaltevuus', NULL, 'Portaan kaltevuuden mukainen, yli yhden askelman pituinen ns. lastenvaunuluiska ei ole turvallinen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (275, 1, 'Luiska, pituuskaltevuus, ei välitasannetta', 'Ilman välitasanteita olevan luiskan kaltevuus (C). Vaikka luiska olisi loiva (5%), suositellaan välitasanteita korkeuseroille 480 mm, 960 mm jne.', 'Arvo C on enintään 5 %.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (276, 1, 'Luiska, pituuskaltevuus, välitasanne', 'Luiska saa olla kaltevuudeltaan enintään 8 % (1:12,5) ja pituudeltaan yhtäjaksoisena enintään kuusi metriä, jonka jälkeen kulkuväylällä tulee olla vaakasuora vähintään 2000 mm:n pituinen välitasanne.', '', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (319, 1, 'Luiska, välitasanne, pituus', 'Luiska saa olla kaltevuudeltaan enintään 8 % (1:12,5) ja pituudeltaan yhtäjaksoisena enintään kuusi metriä. Sen jälkeen kulkuväylällä tulee olla vaakasuora vähintään 2000 mm:n pituinen välitasanne (A).', 'Arvo A on vähintään 2000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1223, 1, 'Luiska/portaat, käsijohde', NULL, 'Käsijohde tai kädensija asennetaan kaikkiin portaisiin ja luiskiin, julkisissa tiloissa molemmin puolin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1224, 1, 'Luiska/portaat,käsijohde, pituus', NULL, 'Portaassa ja luiskassa käsijohde on asennettava koko pituudelle. Johteiden tulee jatkua yhtenäisinä myös välitasanteiden osuudella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1087, 2, 'Käsijohde, luiskat ja portaat, korkeus lattiasta, julkinen tila', NULL, 'Kahden päällekkäisen käsijohteen sopivat korkeudet ovat noin 900 ja 700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (954, 2, 'Luiska, sijoitus', NULL, 'Luiska johtaa aina tasanteelta tasanteelle. Se ei saa lähteä esim. suoraan ovelta ilman tasannetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (274, 2, 'Luiska, sivukaltevuus', NULL, 'Luiskassa ei saa olla sivukaltevuutta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1218, 2, 'Luiska, tasoero', 'Luiskaa ei suositella ainoaksi kulkuväyläksi yli metrin tasoerolle (C).', 'Arvo C on enintään 1000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1214, 2, 'Luiska, välitasanne', NULL, 'Luiskan tulee olla suora kulkusuunnassa, käännöskohdissa tulee olla välitasanne.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1215, 2, 'Luiska, ylätasanne', 'Ylätasanteen syvyys (A), jotta ovi mahtuu aukeamaan ja tasanteella mahtuu kääntymään.', 'Arvo A on vähintään 1800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1027, 2, 'Painike/vedin, ovi, etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys (A) alasjohtavasta portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (213, 3, 'Kulkuväylä, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton. Ulkona esim. asfaltti, betoni, kivituhka ja laatta.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1208, 3, 'Kulkuväylä, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (216, 3, 'Kulkuväylä, pinta, kuvio', NULL, 'Materiaalikontrasti ja kuviot auttaa suunnistautumista. Tummat kuviot vaalealla yksivärisellä pohjalla ovat selkeimpiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (217, 3, 'Kulkuväylä, pinta, kuvio, opastava', NULL, 'Opastava kuvio esim. pitkittäinen pyöristetty kohokuvio, jonka korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (895, 3, 'Kulkuväylä, pinta, väri', NULL, 'Kulkuväylän tai yksityiskohdan (esim. jalkalistan) väri- ja tummuuskontrasti auttaa suunnistautumista ja varoittaa vaarasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (915, 3, 'Kulkuväylä, pituuskaltevuus lepotasoilla ja kulkuväylän kääntyessä', 'Kaltevuusprosentti (C).Käännöskohdissa tasanne on vaakasuorassa ( 2. lähde).', 'Arvo C on enintään 2 %.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (736, 3, 'Kytkin/säädin, etäisyys alasjohtavasta portaasta tai luiskasta', 'Kytkimen tai säätimene etäisyys alasjohtavasta portaasta tai luiskasta (A).', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (192, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, kulman avartaminen', NULL, 'Kulkuväylän (leveys  900 mm) kääntymiskulmaa avarretaan niin, että kulman mitat ovat 1150 x 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (251, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, leveys', 'Kulkuväylän leveys (A) ja (B) kulkuväylän kääntyessä.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (312, 3, 'Luiska, leveys', 'Luiskan leveys (A) riippuu sijainnista ja käyttötarkoituksesta. Suositusmitta on kaksikaistaisen poistumistien leveys.', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (317, 3, 'Luiska, reuna, korkeus', 'Luiskassa tulee olla 50 mm:n suojareunus silloin, kun luiskan reuna ei ole maan tasossa tai se ei rajoitu seinään.', '', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1094, 3, 'Luiska, välitasanne, kulkuväylä kääntyy 90 astetta', 'Välitasanteen kääntymiskulmaa avarretaan niin, että kulman mitat ovat A x B. Suositusmitat 2. lähteestä.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1150 mm. Arvo B on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (956, 3, 'Valaistus, luiska, portaat, kohdevalaistus', 'Luiskan ja portaiden alun kohdevalaistus auttaa luiskan ja portaan havaitsemista.', 'Valaistusvoimakkuus portaissa ja luiskissa 20 - 50 lx. Kriittisissä kohteissa, kuten tasoerojen yhteydessä, valaistusvoimakkuus 400 - 500 lx (2. lähde).', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (863, 3, 'Valaistus, yhteistilat, kulkuväylä', NULL, 'Valaistusvoimakkuus yhteistilojen eteisissä, auloissa, käytävissä, luiskissa ja portaissa on 200 lx.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1059, 3, 'Valaistus, yhteistilat, kulkuväylä', NULL, 'Valaistusvoimakkuus yhteistilojen eteisissä, auloissa, käytävissä, luiskissa ja portaissa on 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (833, 4, 'Liikkumisen tilantarve, kulkuväylä kääntyy, yhteenlaskettu leveys', 'Risteävien kulkuväylien a ja b yhteen laskettu leveys (A). Kulkusuunnan kääntyessä 90 astetta, a:n ja b:n leveys vähintään 900 mm.', 'Arvo A on vähintään 2300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (834, 4, 'Valaistus, kulkuväylä, pinta, häikäisy', NULL, 'Kulkuväylän pinta ei saa heijastaa keino- eikä päivänvaloa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (228, 4, 'Valaistus, kulkuväylä, sijoitus', NULL, 'Kulkuväylän hahmottamista helpottaa samansuuntainen yhtenäinen valaisinlinja.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (438, 1, 'WC-/peseytymistila, opaste, julkinen tila', NULL, 'Julkisissa tiloissa liikkumisesteisen WC merkitään ISA-opasteella ja kuvalla, josta selviää kummalla puolella wc-istuinta on vapaa tila pyörätuolia varten.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (272, 2, 'Autopaikka, opaste', NULL, 'Liikkumisesteisten autopaikat pitää merkitä kansainvälisellä ISA-tunnuksella.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (176, 2, 'Kulkuväylä, opastava, pääovi', NULL, 'Kulkuväylän, joka ohjaa pääovelle, on oltava esteetön ja helposti havaittava.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (217, 3, 'Kulkuväylä, pinta, kuvio, opastava', NULL, 'Opastava kuvio esim. pitkittäinen pyöristetty kohokuvio, jonka korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (692, 3, 'Lattia, pinta, kuvio, opastava', NULL, 'Kulkusuunnan suuntainen kuviointi helpottaa suunnistautumista.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (718, 3, 'Opaste, korkeus lattiasta', 'Opasteen sijoituskorkeus (C), myös koho- ja syvennyskuvio-opasteet.', 'Arvo C on välillä 1400 ... 1600 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (717, 3, 'Opaste, materiaali', NULL, 'Pintamateriaali kiiltämätön ja heijastamaton, lasipintaisessa opasteessa heijastamaton lasi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (728, 3, 'Opaste, sijoitus, ovi', NULL, 'Huonetilan opasteet sijoitetaan oven aukeamispuolelle törmäysvaaran välttämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (720, 3, 'Opaste, teksti', NULL, 'Isoilla kirjaimilla kirjoitetaan alkukirjaimet, lyhyet sanat sekä koho- ja syvennyskirjaimet. Pienillä kirjaimilla kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (719, 3, 'Opaste, teksti, kirjaintyyppi', NULL, 'Suurehko ja selvä, esim. Helvetica Medium.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (721, 3, 'Opaste, teksti, kirjaintyyppi, pienet kirjaimet', NULL, 'Pienellä kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (704, 3, 'Opaste, teksti, korkeus', 'Tekstin korkeus (C) opasteissa, jotka luetaan läheltä.', 'Arvo A on vähintään 15 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (705, 3, 'Opaste, teksti, korkeus, sijainti-tai suuntakilpi', 'Tekstin korkeus (A) sijaintia ja suuntaa osoittavissa opasteissa, jotka luetaan muutaman metrin etäisyydeltä.', 'Arvo C on välillä 25 ... 40 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (722, 3, 'Opaste, väri, kontrasti', NULL, 'Tekstin ja symbolin väri- tai tummuuskontrasti taustaa vasten. Parhaiten erottuu musta teksti valkoisella pohjalla.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (819, 3, 'Valaistus, opaste, ulkopuolelta', NULL, 'Opasteen valaisu helpottaa sen löytymistä. Häikäisemätön valo, josta ei muodostu häiritseviä varjoja.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (731, 4, 'Opaste, sijoitus, kartat', NULL, 'Rakennuksessa suunnistautumista opastavat kohokartat sijoitetaan hyvin näkyville, esimerkiksi ulko-oven viereen.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (725, 4, 'Opaste, teksti, kohokirjain, korkeus', 'Kirjaimen korkeus pinnasta (A) ja kirjaimen korkeus (C). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo A on vähintään 1 mm. Arvo C on välillä 15 ... 40 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (716, 4, 'Opaste, teksti, korkeus, nimikilpi', 'Tekstin korkeus (C) nimikilvissä, jotka luetaan 1..3 m etäisyydeltä.', 'Arvo C on välillä 70 ... 100 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (726, 4, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', NULL, 'Pyörätuolin käyttäjän pistekirjoitusopasteen korkeus lattiasta on 1100 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1268, 4, 'Opaste, äänensiirtojärjestelmä, julkinen tila', NULL, 'T-merkki julkisissa tiloissa kertoo kuulolaitteen käyttäjälle induktiosilmukan käyttömahdollisuudesta.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (818, 4, 'Valaistus, opaste, sisäpuolelta', NULL, 'Sisältäpäin valaistussa opasteessa vaalea teksti tummalla pohjalla häikäisyn estämiseksi.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (724, 5, 'Opaste, kieli', NULL, 'Opasteisiin soveltuu selkokieli, jota ymmärtävät kaikki. Erityisesti sitä tarvitsevat kehitys- ja monivammaiset sekä esim. viittomakieltä äidinkielenään käyttävät.', '1,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (729, 5, 'Opaste, merkinantolaite', NULL, 'Äänimerkkien lisäksi esim. ovipuhelimiin ja merkinantolaitteisiin suositellaan myös visuaalisia ja taktuaalisia eli tuntoaistiin perustuvia merkkejä.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (730, 5, 'Opaste, rakennuksen opastusjärjestelmä', NULL, 'Hälytysäänen ohella myös visuaalinen viesti ja synteettinen puheviesti.', '1,3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1179, 5, 'Opaste, teksti, kohokirjain, poikkileikkaus', NULL, 'Kohokirjaimen poikkileikkaus on kolmioimainen, jonka yläsärmää pitkin sormea voi liuuttaa. Yläsärmä ei saa olla terävä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1043, 5, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', 'Opasteen korkeus (C) lattiasta.', 'Arvo C on välillä 1300 ... 1400 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1180, 5, 'Opaste, teksti, syvennyskirjain, korkeus', NULL, 'Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta. Kirjaimen syvyys tai korkeus pinnasta 1 mm ja kirjaimen korkeus 30-60 mm. Kirjaimen korkeus 15-40 mm (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1181, 5, 'Opaste, teksti, syvennyskirjain, leveys', 'Kirjaimen leveys eli syvennysuran leveys (A). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo C on välillä 6 ... 10 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1227, 1, 'Ovi, automaattiovi, havaittavuus', NULL, 'Oven edustalle lattiaan tehdään tarvittaessa oven kääntymisaluetta kuvaava turvamerkintä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1229, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Kulkuväylältä hisseihin tai tasonvaihtojärjestelmiin sekä hallinto-, palvelu- ja liiketiloihin johtavien ovien vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1193, 1, 'Sauna, ovi, avautuminen', NULL, 'Oven aukeamissuunta löylyhuoneesta katsottuna ulospäin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1287, 1, 'Sauna, ovi, vapaa leveys', 'Oven kulkuaukon leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1195, 1, 'WC, ovi, avautuminen', NULL, 'Wc:stä katsottuna wc-oven aukeamissuunta on ulospäin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1196, 1, 'WC-/peseytymistila, ovi, lukko', NULL, 'Lukko avattavissa myös ulkopuolelta ns. vahtimestarin avaimella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (363, 1, 'WC-/peseytymistila, ovi, vapaa leveys, julkinen tila', NULL, 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1027, 2, 'Painike/vedin, ovi, etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys (A) alasjohtavasta portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (940, 3, 'Liikkumisen tilantarve, automaattiovi aukeaa kohti, ulkopyörätuoli', 'Tilantarve ulko-oven oven edessä  (A), silloin kun tasanteen syvyys on mitta (B). Vieressä alas johtava porras tai luiska. Jos oven edessä on alasjohtava porras, tarvitaan tilaa enemmän kuin 2000 mm.Oviaukon vapaa leveys 900 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 2700 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (728, 3, 'Opaste, sijoitus, ovi', NULL, 'Huonetilan opasteet sijoitetaan oven aukeamispuolelle törmäysvaaran välttämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (852, 3, 'Ovi, aukipysymisaika', 'Aukipysymisaikaa (A) säätäessä otetaan huomioon hitaasti liikkuvat henkilöt. Mikäli ovi avataan painonappia tai painiketta käyttäen automaattisesti, riippuu tarvittava aika painikkeen ja oven välisestä etäisyydestä.', 'Arvo A on vähintään 25 s.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (745, 3, 'Ovi, avaamis- ja säätölaitteet, koodi', NULL, 'Avaamis- ja säätölaitteissa ei tule olla numerokoodia.', '3,7,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (853, 3, 'Ovi, avaamis- ja säätölaitteet, painonappi, sijoitus', NULL, 'Automaattioven painonappi sijoitetaan siten, ettei aukeava ovi aiheuta törmäämisvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1246, 3, 'Ovi, turvallisuus', NULL, 'Oveen suositellaan ikkunaa 900-1800 mm:n korkeudelle törmäysvaaran estämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (619, 3, 'Pukuhuone, ovi, kynnys', NULL, 'Kynnyksiä olisi vältettävä saunatiloissa. Puku- ja pesuhuoneen välille voidaan tarvittaessa asentaa kasaan painuva kumikynnys.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (439, 3, 'Sauna, ovi', NULL, 'Törmäysvaaran vuoksi ei kokolasiovea. Ovessa vaakapuite tai havaintomerkkejä noin 1000 mm:n korkeudella.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (441, 3, 'Sauna, ovi, kynnys', NULL, 'Ei kynnystä.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1169, 3, 'Sauna, ovi, vedin', NULL, 'Puinen saunan oven vedin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (361, 3, 'WC-/peseytymistila, ovi, avaaminen', NULL, 'Ovi avattavissa ja lukittavissa yhdellä kädellä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (440, 4, 'Sauna, ovi, vapaa leveys', 'Oven kulkuaukon leveys (A) liikuntaesteisille suunnittellussa saunatilassa. Vertailuarvona asuntosaunan oven leveys (B) 2. lähteestä.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (744, 5, 'Ovi, avaamis- ja säätölaitteet, ulko-ovi', NULL, 'Raskaat ovet joko automaattisesti avautuvia tai kaukosäätimellä avattavia.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1226, 1, 'Ovi, automaattiovi', NULL, 'Automaattioven avautuminen kulkuväylälle suunnitellaan ja toteutetaan niin, ettei törmäysvaaraa synny.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1227, 1, 'Ovi, automaattiovi, havaittavuus', NULL, 'Oven edustalle lattiaan tehdään tarvittaessa oven kääntymisaluetta kuvaava turvamerkintä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (176, 2, 'Kulkuväylä, opastava, pääovi', NULL, 'Kulkuväylän, joka ohjaa pääovelle, on oltava esteetön ja helposti havaittava.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1232, 2, 'Ovi, oviaukko, vapaa leveys, ulko-ovi', NULL, 'Asuntojen ulko- ja porrastaso-oviksi suositellaan leveydeltään 10 M-ovia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1027, 2, 'Painike/vedin, ovi, etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys (A) alasjohtavasta portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (260, 2, 'Tuulikaappi, mitoitus, kääntöovi aukeaa poispäin', 'Tuulikaapin tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (784, 2, 'Valaistus, sisäänkäynti, ulko-ovi', 'Valaistusvoimakkuus (C) ulko-ovella.', 'Arvo C on välillä 80 ... 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (736, 3, 'Kytkin/säädin, etäisyys alasjohtavasta portaasta tai luiskasta', 'Kytkimen tai säätimene etäisyys alasjohtavasta portaasta tai luiskasta (A).', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (749, 3, 'Laitteet, kulunvalvontalaite', 'Kulunvalvontalaitteen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (940, 3, 'Liikkumisen tilantarve, automaattiovi aukeaa kohti, ulkopyörätuoli', 'Tilantarve ulko-oven oven edessä  (A), silloin kun tasanteen syvyys on mitta (B). Vieressä alas johtava porras tai luiska. Jos oven edessä on alasjohtava porras, tarvitaan tilaa enemmän kuin 2000 mm.Oviaukon vapaa leveys 900 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 2700 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (728, 3, 'Opaste, sijoitus, ovi', NULL, 'Huonetilan opasteet sijoitetaan oven aukeamispuolelle törmäysvaaran välttämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (852, 3, 'Ovi, aukipysymisaika', 'Aukipysymisaikaa (A) säätäessä otetaan huomioon hitaasti liikkuvat henkilöt. Mikäli ovi avataan painonappia tai painiketta käyttäen automaattisesti, riippuu tarvittava aika painikkeen ja oven välisestä etäisyydestä.', 'Arvo A on vähintään 25 s.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (745, 3, 'Ovi, avaamis- ja säätölaitteet, koodi', NULL, 'Avaamis- ja säätölaitteissa ei tule olla numerokoodia.', '3,7,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (853, 3, 'Ovi, avaamis- ja säätölaitteet, painonappi, sijoitus', NULL, 'Automaattioven painonappi sijoitetaan siten, ettei aukeava ovi aiheuta törmäämisvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (681, 3, 'Parveke, ovi, aukipitolaite', NULL, 'Aukipitolaite esim. ovijarru estää tuulta paiskomasta ovea. Ovijarrun jäykkyyttä voidaan säätää niin, että ovi on kevyt avata, mutta pysyy silti auki.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (679, 3, 'Parveke, ovi, kynnyskorkeus', 'Kynnyskorkeus (C) saadaan vaatimuksen mukaiseksi esim. irrotettavan ritilän avulla. Parveketaso suositellaan kosteusteknisistä syistä sijoitettavaksi 80...120 mm kynnyksen yläpintaa alemmaksi.', 'Arvo C on enintään 20 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (680, 3, 'Parveke, ovi, kynnyspelti', NULL, 'Kynnyspelti tehdään luistamattomasta materiaalista esim. kuvioidusta alumiinista tai se varustetaan liukuesteellä. Kynnyspelti loivasti kalteva.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (269, 3, 'Tuulikaappi, automaattiovi', NULL, 'Automaattiovien molempien ovien on toimittava samalla periaatteella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (273, 4, 'Autopaikka, autotalli, ovi', NULL, 'Ovessa automaattinen avauslaite, jota käytetään autosta kaukosäätimellä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (952, 4, 'Autopaikka, autotalli, ovi, leveys', 'Autotallin oven leveys (A).', 'Arvo A on vähintään 2500 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (261, 4, 'Tuulikaappi, mitoitus, automaattiovi', 'Tuulikaapin minimileveys (A) ja minimisyvyys (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (263, 4, 'Tuulikaappi, mitoitus, automaattiovi, pyörätuolien kohtaaminen', 'Tuulikaapin oviaukon vapaa leveys (A)  kahden pyörätuolin ohittamiseen. Tuulikaapin syvyys (B).', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 2400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (259, 4, 'Tuulikaappi, mitoitus, kääntöovi aukeaa kohti', 'Tuulikaapin minimileveys (A) ja minimisyvyys(B).', 'Tuulikaapin syvyys vähintään 1500 mm + ovilehden leveys.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (744, 5, 'Ovi, avaamis- ja säätölaitteet, ulko-ovi', NULL, 'Raskaat ovet joko automaattisesti avautuvia tai kaukosäätimellä avattavia.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1229, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Kulkuväylältä hisseihin tai tasonvaihtojärjestelmiin sekä hallinto-, palvelu- ja liiketiloihin johtavien ovien vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (682, 1, 'Parveke, kaide', NULL, 'Kaiteen suojaava osa tulisi olla läpinäkyvä (esim. pinnakaide) tai siinä tulisi olla matalalla sijaitsevia kurkistusaukkoja.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1221, 1, 'Parveke, kaide, korkeus lattiasta', 'Asunnon parvekkeen tai terassin kaiteen korkeus (A) putoamiskorkeudesta riippumatta. Suojaavan osan korkeus (B).', 'Arvo A on vähintään 1000 mm. Arvo B on vähintään 700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (187, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli julkisessa tilassa', 'Pyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1232, 2, 'Ovi, oviaukko, vapaa leveys, ulko-ovi', NULL, 'Asuntojen ulko- ja porrastaso-oviksi suositellaan leveydeltään 10 M-ovia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (519, 3, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 450 mm. Arvo C on välillä 600 ... 650 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (683, 3, 'Parveke, kaide, käsijohde', NULL, 'Parvekkeen kaiteessa on oltava käsijohde, josta saa tukevan otteen.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (684, 3, 'Parveke, kaide, lattian liittymäkohta', NULL, 'Liittymäkohdassa ei saa olla rakoa, johon apuväline esim. keppi voi jäädä kiinni.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (675, 3, 'Parveke, lattia, pinta', NULL, 'Parvekeen lattian on oltava kova, luistamaton, tasainen esim. betoni.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (676, 3, 'Parveke, lattia, ritilä', 'Apuvälineet esim. valkoinen keppi ei saa jäädä kiinni ritilän rakoihin, joiden maksimileveys on (C). Irrotettava ritilä madaltaa parvekkeen lattian ja kynnyksen korkeuseroa.', 'Arvo C on enintään 5 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (674, 3, 'Parveke, mitoitus', 'Tilantarve esitetty leveyden (A) ja syvyyden (B) avulla. Parvekken muoto vaikuttaa kalustettavuuteen. Mitoituksessa on huomioitava oven avautumisen, kalusteiden ja pyörätuolin kääntymisen tilantarve.', 'Arvo A on vähintään 2400 mm, mutta arvoksi suositellaan vähintään 2600 mm. Arvo B on vähintään 2100 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (681, 3, 'Parveke, ovi, aukipitolaite', NULL, 'Aukipitolaite esim. ovijarru estää tuulta paiskomasta ovea. Ovijarrun jäykkyyttä voidaan säätää niin, että ovi on kevyt avata, mutta pysyy silti auki.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (679, 3, 'Parveke, ovi, kynnyskorkeus', 'Kynnyskorkeus (C) saadaan vaatimuksen mukaiseksi esim. irrotettavan ritilän avulla. Parveketaso suositellaan kosteusteknisistä syistä sijoitettavaksi 80...120 mm kynnyksen yläpintaa alemmaksi.', 'Arvo C on enintään 20 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (680, 3, 'Parveke, ovi, kynnyspelti', NULL, 'Kynnyspelti tehdään luistamattomasta materiaalista esim. kuvioidusta alumiinista tai se varustetaan liukuesteellä. Kynnyspelti loivasti kalteva.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1068, 3, 'Pihakalusteet, penkki, käsinojat', NULL, 'Penkissä on oltava etureunaan ulottuvat käsinojat, joista saa tukea istuutuessa ja noustaessa ylös.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (521, 4, 'Kalusteet, taso/pöytä, polvitilan mitat', 'Polvitilan mitat esitetty leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1164, 4, 'Parveke, mitoitus, pinta-ala', 'Parvekkeen tai terassin suositeltava vähimmäispinta-ala (C).', 'Arvo C on välillä 6 ... 8 m2.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (234, 4, 'Pihakalusteet, penkki, tila pyörätuolille ja rollaattorille', 'Penkin vieressä tarvittavan tilan leveys (A) pyörätuolille ja rollaattorille, silloin kun henkilö istuu penkillä.', 'Arvo A on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (947, 4, 'Pihakalusteet, pöytä, korkeus', 'Pöydän korkeus (C) maanpinnasta.', 'Arvo C on välillä 750 ... 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (946, 4, 'Pihakalusteet, pöytä, polvitilan mitoitus', 'Mitat on esitetty leveyden (A), syvyyden (B) ja korkeuden  (C) avulla.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (363, 1, 'WC-/peseytymistila, ovi, vapaa leveys, julkinen tila', NULL, 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1199, 1, 'WC-/peseytymistila, sijoitus, julkinen tila', NULL, 'Tiloihin tulee olla pääsy suoraan aulasta tai käytävästä. Mikäli WC- ja pesutiloja on erikseen naisille ja miehille, kuhunkin sijoitetaan liikkumisesteiselle soveltuva WC- ja pesutila.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1198, 1, 'WC-/peseytymistila, varusteet', NULL, 'Kerrostalossa, jossa edellytetään hissiä, asuinhuoneiston WC-/peseytymistilan on sovelluttava myös pyörätuolin käyttäjälle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1246, 3, 'Ovi, turvallisuus', NULL, 'Oveen suositellaan ikkunaa 900-1800 mm:n korkeudelle törmäysvaaran estämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1136, 3, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus WC/peseytymistilassa on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (690, 3, 'WC-/peseytymistila, lattia, kaltevuus', NULL, 'Peseytymistilan lattian suositeltava kaltevuus on 1,25 % (1:80), lattiakaivon ympärillä 2% (1:50).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1157, 3, 'WC-/peseytymistila, lattia, lattialämmitys', NULL, 'Lattialämmitys on suositeltava.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1156, 3, 'WC-/peseytymistila, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1158, 3, 'WC-/peseytymistila, lattiakaivo', NULL, 'Lattiakaivoa ei saa sijoittaa kulkuväylälle eikä paikkaan, jossa seisotaan (esim. suihkussa) liukastumisvaaran takia. Kannen on oltava tukeva ja pysyttävä hyvin paikallaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (365, 3, 'Yhteistilat, WC-/peseytymistila, varusteet', NULL, 'Pesualtaan tai suihkun läheisyyteen asennetaan hylly esim. silmälaseille, korkeus lattiasta 900 mm. Saippuatelineen yms. asennuskorkeus samoin 900 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1029, 4, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus on 150 lx. Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx. Wc-tiloissa esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (816, 2, 'Valaistus, yhteistilat', 'Yhteistilojen, jätetilojen ja varastojen yleisvalaistusvaatimus.', 'Valaistusvoimakkuus varastotiloissa on 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (662, 2, 'Yhteistilat, pesula, laitteet, sijoitus', NULL, 'Ulottumista helpottaa, jos kone sijoitetaan sokkelin päälle.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (839, 3, 'Kytkin/säädin, valopainike, sijoitus', NULL, 'Valopainike sijoitetaan kulkureitin varrelle helposti havaittavaan ja ulottuvissa olevaan paikkaan.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (693, 3, 'Lattia, pinta, kuvio', NULL, 'Lattiassa suuret, voimakasväriset kuviot haittaavat suunnistautumista ja tekevät yleisvaikutelman heikkonäköiselle sekavaksi. Heikkonäköinen voi erehtyä pitämään lattian tummaa kohtaa aukkona tai tasoerona lattiassa.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (692, 3, 'Lattia, pinta, kuvio, opastava', NULL, 'Kulkusuunnan suuntainen kuviointi helpottaa suunnistautumista.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1158, 3, 'WC-/peseytymistila, lattiakaivo', NULL, 'Lattiakaivoa ei saa sijoittaa kulkuväylälle eikä paikkaan, jossa seisotaan (esim. suihkussa) liukastumisvaaran takia. Kannen on oltava tukeva ja pysyttävä hyvin paikallaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1190, 3, 'Yhteistilat, pesula, laitteet, pesukone', NULL, 'Pesulaan suositellaan myös pikkupyykkiä varten pesukoneita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (659, 3, 'Yhteistilat, pesula, sijoitus', NULL, 'Hissi- ja porrasyhteyksien välittömässä läheisyydessä, ensisijaisesti maantasokerroksessa. Näköyhteys oleskelupihalle ja porrashuoneen sisäänkäyntiin on suotavaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (664, 3, 'Yhteistilat, pyykinkuivaus', NULL, 'Pesulassa on joko kuivauskaappi tai -rumpu tai pesulan yhteydessä on erillinen kuivaushuone. Mahdollisuus pyykin kuivaamiseen ulkona.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (667, 3, 'Yhteistilat, pyykinkuivaus, telineet, alusta', NULL, 'Pyykinkuivaus- ja tomutustelineet sijoitetaan alustalle, joka on vaakasuora ja kovapintainen.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (663, 4, 'Yhteistilat, pesula, laitteet, vapaa tila', 'Avaamispuolen leveysmitta (A). Vertailuarvo (B) 2. lähteestä. Oviaukkoa voidaan käyttää hyväksi vapaana tilana.', 'Arvo A on vähintään 500 mm. Arvo B on vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (660, 4, 'Yhteistilat, pesula, mitoitus', NULL, 'Mitoituksessa otetaan huomioon pyörätuolin liikkumisen ja kääntymisen tilantarve.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (665, 4, 'Yhteistilat, pyykinkuivaus, telineet', NULL, 'Pyykinkuivaus- ja tomutustelineet asennetaan eri korkeuksille, niin että niihin ulottuu myös lyhytkasvuinen henkilö ja pyörätuolin käyttäjä. Pyykin ripustaminen kuivumaan mahdollista myös avustajan kanssa.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1089, 5, 'Liikkumisen tilantarve, opaskoiran kanssa', 'Opaskoiraa käyttävän henkilön tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 1100 mm.', '3');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (179, 1, 'Kulkuväylä, turvallisuus', NULL, 'Ajoneuvoliikenne tontilla ei saisi ristetä jalankulkuliikenteen kanssa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (211, 1, 'Kulkuväylä, törmäysvaara', NULL, 'Kulkuväylällä ei saa olla törmäysvaaraa aiheuttavia rakenteita. Rakenteen törmäysvaara estetään esim. kaiteella ja siitä varoitetaan värillä tai materiaalilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1226, 1, 'Ovi, automaattiovi', NULL, 'Automaattioven avautuminen kulkuväylälle suunnitellaan ja toteutetaan niin, ettei törmäysvaaraa synny.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1227, 1, 'Ovi, automaattiovi, havaittavuus', NULL, 'Oven edustalle lattiaan tehdään tarvittaessa oven kääntymisaluetta kuvaava turvamerkintä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (176, 2, 'Kulkuväylä, opastava, pääovi', NULL, 'Kulkuväylän, joka ohjaa pääovelle, on oltava esteetön ja helposti havaittava.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (187, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli julkisessa tilassa', 'Pyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1232, 2, 'Ovi, oviaukko, vapaa leveys, ulko-ovi', NULL, 'Asuntojen ulko- ja porrastaso-oviksi suositellaan leveydeltään 10 M-ovia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (232, 3, 'Kulkuväylä, kalusteet, sijoitus', NULL, 'Tontilla olevat kalusteet, kuten valaisinpylväät, opasteet, roskalaatikot, pyörätelineet ja penkit on sijoitettava kulkuväylän ulkopuolelle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (172, 3, 'Kulkuväylä, koordinaatisto', NULL, 'Kulkuväylien on oltava selkeässä koordinaatistossa ja yhtenäisiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (213, 3, 'Kulkuväylä, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton. Ulkona esim. asfaltti, betoni, kivituhka ja laatta.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1208, 3, 'Kulkuväylä, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (216, 3, 'Kulkuväylä, pinta, kuvio', NULL, 'Materiaalikontrasti ja kuviot auttaa suunnistautumista. Tummat kuviot vaalealla yksivärisellä pohjalla ovat selkeimpiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (217, 3, 'Kulkuväylä, pinta, kuvio, opastava', NULL, 'Opastava kuvio esim. pitkittäinen pyöristetty kohokuvio, jonka korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (895, 3, 'Kulkuväylä, pinta, väri', NULL, 'Kulkuväylän tai yksityiskohdan (esim. jalkalistan) väri- ja tummuuskontrasti auttaa suunnistautumista ja varoittaa vaarasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (171, 3, 'Kulkuväylä, pituus', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (177, 3, 'Kulkuväylä, päättyminen', NULL, 'Kulkureitti ei saa johtaa umpikujaan.', '3,7,11,14,15,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (736, 3, 'Kytkin/säädin, etäisyys alasjohtavasta portaasta tai luiskasta', 'Kytkimen tai säätimene etäisyys alasjohtavasta portaasta tai luiskasta (A).', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (840, 3, 'Kytkin/säädin, valopainike, havaittavuus', NULL, 'Painikkeen merkkivalon lisäksi painikkeen väri- tai tummuuskontrasti taustaa vasten auttaa sen havaitsemista.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (839, 3, 'Kytkin/säädin, valopainike, sijoitus', NULL, 'Valopainike sijoitetaan kulkureitin varrelle helposti havaittavaan ja ulottuvissa olevaan paikkaan.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (749, 3, 'Laitteet, kulunvalvontalaite', 'Kulunvalvontalaitteen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (940, 3, 'Liikkumisen tilantarve, automaattiovi aukeaa kohti, ulkopyörätuoli', 'Tilantarve ulko-oven oven edessä  (A), silloin kun tasanteen syvyys on mitta (B). Vieressä alas johtava porras tai luiska. Jos oven edessä on alasjohtava porras, tarvitaan tilaa enemmän kuin 2000 mm.Oviaukon vapaa leveys 900 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 2700 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (192, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, kulman avartaminen', NULL, 'Kulkuväylän (leveys  900 mm) kääntymiskulmaa avarretaan niin, että kulman mitat ovat 1150 x 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (251, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, leveys', 'Kulkuväylän leveys (A) ja (B) kulkuväylän kääntyessä.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (718, 3, 'Opaste, korkeus lattiasta', 'Opasteen sijoituskorkeus (C), myös koho- ja syvennyskuvio-opasteet.', 'Arvo C on välillä 1400 ... 1600 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (717, 3, 'Opaste, materiaali', NULL, 'Pintamateriaali kiiltämätön ja heijastamaton, lasipintaisessa opasteessa heijastamaton lasi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (728, 3, 'Opaste, sijoitus, ovi', NULL, 'Huonetilan opasteet sijoitetaan oven aukeamispuolelle törmäysvaaran välttämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (719, 3, 'Opaste, teksti, kirjaintyyppi', NULL, 'Suurehko ja selvä, esim. Helvetica Medium.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (721, 3, 'Opaste, teksti, kirjaintyyppi, pienet kirjaimet', NULL, 'Pienellä kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (704, 3, 'Opaste, teksti, korkeus', 'Tekstin korkeus (C) opasteissa, jotka luetaan läheltä.', 'Arvo A on vähintään 15 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (705, 3, 'Opaste, teksti, korkeus, sijainti-tai suuntakilpi', 'Tekstin korkeus (A) sijaintia ja suuntaa osoittavissa opasteissa, jotka luetaan muutaman metrin etäisyydeltä.', 'Arvo C on välillä 25 ... 40 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (722, 3, 'Opaste, väri, kontrasti', NULL, 'Tekstin ja symbolin väri- tai tummuuskontrasti taustaa vasten. Parhaiten erottuu musta teksti valkoisella pohjalla.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (852, 3, 'Ovi, aukipysymisaika', 'Aukipysymisaikaa (A) säätäessä otetaan huomioon hitaasti liikkuvat henkilöt. Mikäli ovi avataan painonappia tai painiketta käyttäen automaattisesti, riippuu tarvittava aika painikkeen ja oven välisestä etäisyydestä.', 'Arvo A on vähintään 25 s.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (745, 3, 'Ovi, avaamis- ja säätölaitteet, koodi', NULL, 'Avaamis- ja säätölaitteissa ei tule olla numerokoodia.', '3,7,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (853, 3, 'Ovi, avaamis- ja säätölaitteet, painonappi, sijoitus', NULL, 'Automaattioven painonappi sijoitetaan siten, ettei aukeava ovi aiheuta törmäämisvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1246, 3, 'Ovi, turvallisuus', NULL, 'Oveen suositellaan ikkunaa 900-1800 mm:n korkeudelle törmäysvaaran estämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (231, 3, 'Pihakalusteet', NULL, 'Kalusteen on oltava tukeva ja paikallaan pysyvä ja siinä ei saa olla teräviä särmiä ja kulmia. Materiaali ei saa aiheuttaa allergiaa.', '3,7,8,20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (842, 3, 'Porrashuone, nimitaulu, kirjaimet', NULL, 'Kohokirjaimet, jotka ovat 15 mm korkeat ja koholla 1 mm:n verran. Parhaiten erottuu musta teksti valkoisella pohjalla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (841, 3, 'Porrashuone, nimitaulu, korkeus lattiasta', 'Sijoituskorkeus (C) lattiasta. Asukkaiden nimitaulu sijoitetaan katsekorkeudelle helposti löydettävään ja hyvin valaistuun paikkaan esim. ulko-oven vierelle.', 'Arvo C on välillä 1400 ... 1600 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (702, 3, 'Seinä, varusteet, peiliseinä', NULL, 'Hahmottamisen ja häikäisyn takia ei peiliseiniä kulkuväylille.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (819, 3, 'Valaistus, opaste, ulkopuolelta', NULL, 'Opasteen valaisu helpottaa sen löytymistä. Häikäisemätön valo, josta ei muodostu häiritseviä varjoja.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (785, 3, 'Valaistus, sisäänkäynti', NULL, 'Valaistuksella tasoitetaan valaistuseroja sisä- ja ulkotilojen välillä ja estetään häikäistyminen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (863, 3, 'Valaistus, yhteistilat, kulkuväylä', NULL, 'Valaistusvoimakkuus yhteistilojen eteisissä, auloissa, käytävissä, luiskissa ja portaissa on 200 lx.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1059, 3, 'Valaistus, yhteistilat, kulkuväylä', NULL, 'Valaistusvoimakkuus yhteistilojen eteisissä, auloissa, käytävissä, luiskissa ja portaissa on 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (458, 4, 'Eteinen, kalusteet, naulakko', 'Pyörätuolin käyttäjälle soveltuvan naulakon korkeus (C). Sokkelittomaan naulakkoon on helpompi ulottua pyörätuolista kuin sokkelilla varustettuun.', 'Arvo C on välillä 1100 ... 1200 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (236, 4, 'Kulkuväylä, kalusteet, kalustevyöhyke', NULL, 'Kalustevyöhyke tulisi erottaa kulkuväylästä poikkeavalla pintamateriaalilla ja/tai -värityksellä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (222, 4, 'Kulkuväylä, reuna', NULL, 'Suunnistautumista helpottaa kulkuväylän rajoittuminen selvästi poikkeavan tuntuiseen pintaan.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (223, 4, 'Kulkuväylä, reuna, kaide', NULL, 'Kulkuväylän reunalla kulkuesteaitana voi olla esim. avokaide, jossa on vaakajohteet sekä 900 mm:n (käsijohde) että 100 mm:n korkeudella.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1212, 4, 'Kulkuväylä, reuna, korkeus', NULL, 'Turvallisessa kulkuväylässä on korotetun reunuksen lisäksi kaide tai istutuksia. Tai reunus on riittävän korkea (noin 600 mm), ettei siitä aiheudu kompastumisvaaraa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (833, 4, 'Liikkumisen tilantarve, kulkuväylä kääntyy, yhteenlaskettu leveys', 'Risteävien kulkuväylien a ja b yhteen laskettu leveys (A). Kulkusuunnan kääntyessä 90 astetta, a:n ja b:n leveys vähintään 900 mm.', 'Arvo A on vähintään 2300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (190, 4, 'Liikkumisen tilantarve, pyörätuolien kohdatessa', 'Kulkuväylän leveyden (A) vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1010, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, asunto', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1400 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (453, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, avustaja', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 1400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (907, 4, 'Liikkumisen tilantarve, ulkosähköpyörätuolien kohdatessa', 'Tilantarve esitetty kulkuväylän leveyden (A) avulla.', 'Arvo A on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (731, 4, 'Opaste, sijoitus, kartat', NULL, 'Rakennuksessa suunnistautumista opastavat kohokartat sijoitetaan hyvin näkyville, esimerkiksi ulko-oven viereen.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (725, 4, 'Opaste, teksti, kohokirjain, korkeus', 'Kirjaimen korkeus pinnasta (A) ja kirjaimen korkeus (C). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo A on vähintään 1 mm. Arvo C on välillä 15 ... 40 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (716, 4, 'Opaste, teksti, korkeus, nimikilpi', 'Tekstin korkeus (C) nimikilvissä, jotka luetaan 1..3 m etäisyydeltä.', 'Arvo C on välillä 70 ... 100 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (726, 4, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', NULL, 'Pyörätuolin käyttäjän pistekirjoitusopasteen korkeus lattiasta on 1100 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (818, 4, 'Valaistus, opaste, sisäpuolelta', NULL, 'Sisältäpäin valaistussa opasteessa vaalea teksti tummalla pohjalla häikäisyn estämiseksi.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1089, 5, 'Liikkumisen tilantarve, opaskoiran kanssa', 'Opaskoiraa käyttävän henkilön tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 1100 mm.', '3');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (724, 5, 'Opaste, kieli', NULL, 'Opasteisiin soveltuu selkokieli, jota ymmärtävät kaikki. Erityisesti sitä tarvitsevat kehitys- ja monivammaiset sekä esim. viittomakieltä äidinkielenään käyttävät.', '1,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (730, 5, 'Opaste, rakennuksen opastusjärjestelmä', NULL, 'Hälytysäänen ohella myös visuaalinen viesti ja synteettinen puheviesti.', '1,3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1179, 5, 'Opaste, teksti, kohokirjain, poikkileikkaus', NULL, 'Kohokirjaimen poikkileikkaus on kolmioimainen, jonka yläsärmää pitkin sormea voi liuuttaa. Yläsärmä ei saa olla terävä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1043, 5, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', 'Opasteen korkeus (C) lattiasta.', 'Arvo C on välillä 1300 ... 1400 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1180, 5, 'Opaste, teksti, syvennyskirjain, korkeus', NULL, 'Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta. Kirjaimen syvyys tai korkeus pinnasta 1 mm ja kirjaimen korkeus 30-60 mm. Kirjaimen korkeus 15-40 mm (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1181, 5, 'Opaste, teksti, syvennyskirjain, leveys', 'Kirjaimen leveys eli syvennysuran leveys (A). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo C on välillä 6 ... 10 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (744, 5, 'Ovi, avaamis- ja säätölaitteet, ulko-ovi', NULL, 'Raskaat ovet joko automaattisesti avautuvia tai kaukosäätimellä avattavia.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (836, 5, 'Porrashuone, vapaan tilan leveys', 'Tilan leveys (A). Porrashuoneessa tarvitaan tilaa siten, että kaksi pyörätuolia mahtuu ohittamaan toisensa.', 'Arvo A on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (699, 5, 'Seinä ja katto, akustiikka', NULL, 'Kuulovammaiselle pidetään yleensä lyhyttä jälkikaiunta-aikaa sopivana. Tarvittava vaimennus asunnossa saadaan käyttämällä sisustuksessa mattoja, verhoja yms. vaimentavia tekstiilejä.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (386, 1, 'Käsijohde, luiskat ja portaat, julkinen tila', NULL, 'Luiskat ja portaat varustetaan molemmin puolin yhtenäisillä käsijohteilla. Leveissä portaissa suositellaan portaan keskellä käsijohdetta 2400 mm:n välein.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1222, 1, 'Käsijohde, pituus, julkinen tila', 'Käsijohde ulotetaan luiskien ja portaiden alkamis- ja päättymiskohtien ohi. Käsijohteen ulokkeen pituus (A).', 'Arvo A on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1217, 1, 'Luiska, pituuskaltevuus', NULL, 'Portaan kaltevuuden mukainen, yli yhden askelman pituinen ns. lastenvaunuluiska ei ole turvallinen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1223, 1, 'Luiska/portaat, käsijohde', NULL, 'Käsijohde tai kädensija asennetaan kaikkiin portaisiin ja luiskiin, julkisissa tiloissa molemmin puolin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1224, 1, 'Luiska/portaat,käsijohde, pituus', NULL, 'Portaassa ja luiskassa käsijohde on asennettava koko pituudelle. Johteiden tulee jatkua yhtenäisinä myös välitasanteiden osuudella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (332, 1, 'Portaat, mitoitus, julkiset sisätilat', 'Auloissa ja muissa sisäisen liikenteen tiloissa toimivan portaan mitoitus etenemän (A) ja nousun (C) avulla.', 'Arvo A on vähintään 300 mm. Arvo C on enintään 160 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1125, 1, 'Portaat, mitoitus, uloskäytävä', 'Palomääräysten mukainen uloskäytävänä toimivan portaan mitoitus esitettynä etenemän (A) ja nousun (C) avulla.', 'Arvo A on vähintään 270 mm. Arvo C on enintään 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (843, 1, 'Portaat, sijoitus', NULL, 'Portaat eivät saa aiheuttaa putoamis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1087, 2, 'Käsijohde, luiskat ja portaat, korkeus lattiasta, julkinen tila', NULL, 'Kahden päällekkäisen käsijohteen sopivat korkeudet ovat noin 900 ja 700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1218, 2, 'Luiska, tasoero', 'Luiskaa ei suositella ainoaksi kulkuväyläksi yli metrin tasoerolle (C).', 'Arvo C on enintään 1000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (383, 2, 'Portaat, askelmat', NULL, 'Portaissa ei tulisi olla avonaisia tai yksittäisiä askelmia eikä ulkonevia askelmien etureunoja. Portaat, joissa on vähintään kolme nousua, on helpompi havaita.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (330, 2, 'Portaat, mitoitus, askelmat', 'Portaan nousun ja etenemän suhde määrää portaiden helppokulkuisuuden.', 'Mitoituksessa voidaan käyttää seuraavaa kaavaa: 2 * nousu + etenemä = 630 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1129, 2, 'Portaat, mitoitus, paarikuljetus', 'Paarien tilantarve kantajien liikkumavarat mukaanlukien esitettynä leveyden (A) ja pituuden (B) avulla.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 2400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1117, 2, 'Portaat, nousukulma, sisätilat', 'Portaiden nousukulman (C) yleinen vaihteluväli sisätiloissa.', 'Arvo C on välillä 25 ... 37 °.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (380, 2, 'Portaat, väri', NULL, 'Portaan väri- tai tummuuskontrasti seinän tai lattian kanssa auttaa portaiden erottamista seinä- tai lattiapinnasta. Porrasaskelmien etureunojen merkintä helpottaa askelmien erottamista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (736, 3, 'Kytkin/säädin, etäisyys alasjohtavasta portaasta tai luiskasta', 'Kytkimen tai säätimene etäisyys alasjohtavasta portaasta tai luiskasta (A).', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (381, 3, 'Portaat, liukueste', NULL, 'Liukuesteet porrasaskelmien etureunoissa ja niiden väriero portaan kanssa lisäävät portaan turvallisuutta.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1234, 3, 'Portaat, mitoitus, helppokulkuinen porras', 'Portaiden mitoitus henkilölle, jolla on jäykät nilkat. Mitoitus esitetty portaan etenemän (A) ja nousun (B) avulla.', 'Arvo A on vähintään 390 mm. Arvo C on enintään 120 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (384, 3, 'Portaat, tyyppi', NULL, 'Kierreporrasta ei saa käyttää.', '3,7,8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1128, 3, 'Portaat, välitasanne', NULL, 'Välitasanteita tulee olla vähintään yksi kerrosta kohti.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (956, 3, 'Valaistus, luiska, portaat, kohdevalaistus', 'Luiskan ja portaiden alun kohdevalaistus auttaa luiskan ja portaan havaitsemista.', 'Valaistusvoimakkuus portaissa ja luiskissa 20 - 50 lx. Kriittisissä kohteissa, kuten tasoerojen yhteydessä, valaistusvoimakkuus 400 - 500 lx (2. lähde).', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (863, 3, 'Valaistus, yhteistilat, kulkuväylä', NULL, 'Valaistusvoimakkuus yhteistilojen eteisissä, auloissa, käytävissä, luiskissa ja portaissa on 200 lx.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1059, 3, 'Valaistus, yhteistilat, kulkuväylä', NULL, 'Valaistusvoimakkuus yhteistilojen eteisissä, auloissa, käytävissä, luiskissa ja portaissa on 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (834, 4, 'Valaistus, kulkuväylä, pinta, häikäisy', NULL, 'Kulkuväylän pinta ei saa heijastaa keino- eikä päivänvaloa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (228, 4, 'Valaistus, kulkuväylä, sijoitus', NULL, 'Kulkuväylän hahmottamista helpottaa samansuuntainen yhtenäinen valaisinlinja.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1196, 1, 'WC-/peseytymistila, ovi, lukko', NULL, 'Lukko avattavissa myös ulkopuolelta ns. vahtimestarin avaimella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (363, 1, 'WC-/peseytymistila, ovi, vapaa leveys, julkinen tila', NULL, 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1199, 1, 'WC-/peseytymistila, sijoitus, julkinen tila', NULL, 'Tiloihin tulee olla pääsy suoraan aulasta tai käytävästä. Mikäli WC- ja pesutiloja on erikseen naisille ja miehille, kuhunkin sijoitetaan liikkumisesteiselle soveltuva WC- ja pesutila.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1136, 3, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus WC/peseytymistilassa on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1056, 3, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 200 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (627, 3, 'WC-/peseytymistila, käsituki, materiaali', NULL, 'Mattapintainen, muovipäällysteinen käsituki on suositeltavampi kuin metallipintainen. Metallipintainen käsijohde on liukas ja se saattaa häikäistä ja aiheuttaa allergiaa (nikkeli).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (690, 3, 'WC-/peseytymistila, lattia, kaltevuus', NULL, 'Peseytymistilan lattian suositeltava kaltevuus on 1,25 % (1:80), lattiakaivon ympärillä 2% (1:50).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1157, 3, 'WC-/peseytymistila, lattia, lattialämmitys', NULL, 'Lattialämmitys on suositeltava.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1156, 3, 'WC-/peseytymistila, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1158, 3, 'WC-/peseytymistila, lattiakaivo', NULL, 'Lattiakaivoa ei saa sijoittaa kulkuväylälle eikä paikkaan, jossa seisotaan (esim. suihkussa) liukastumisvaaran takia. Kannen on oltava tukeva ja pysyttävä hyvin paikallaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (361, 3, 'WC-/peseytymistila, ovi, avaaminen', NULL, 'Ovi avattavissa ja lukittavissa yhdellä kädellä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (369, 3, 'WC-/peseytymistila, varusteet, peili, yläreunan korkeus', 'Peilin yläreunan korkeus (C). Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo C on välillä 1700 ... 1800 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (616, 3, 'Yhteistilat, pukuhuone, laitteet, pistorasia, korkeus lattiasta', 'Asennuskorkeus (C) lattiasta. Peilin viereen sijoitetaan pistorasia hiustenkuivaajaa varten.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (365, 3, 'Yhteistilat, WC-/peseytymistila, varusteet', NULL, 'Pesualtaan tai suihkun läheisyyteen asennetaan hylly esim. silmälaseille, korkeus lattiasta 900 mm. Saippuatelineen yms. asennuskorkeus samoin 900 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1029, 4, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus on 150 lx. Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx. Wc-tiloissa esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (790, 4, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 300 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (368, 4, 'WC-/peseytymistila, varusteet, peili, alareunan korkeus', 'Pesualtaan yläpuolella oleva peilin alareunan korkeus (C) lattiasta riippuu pesualtaan korkeudesta.', 'Arvo C on välillä 800 ... 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (611, 4, 'Yhteistilat, pukuhuone, kalusteet, penkki', 'Mitoitus penkille, jossa voi pukeutua pitkällään. Mitat on esitetty syvyyden (A), leveyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 700 mm. Arvo B on vähintään 2000 mm. Arvo C on vähintään 500 mm.', '11,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1176, 4, 'Yhteistilat, pukuhuone, kalusteet, pukukaappi', NULL, 'Pukukaappiin on ulotuttava myös pyörätuolista käsin, edessä ei saa olla penkkiä.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (812, 5, 'Valaistus, kalusteet, kaapit', NULL, 'Kaappien valaiseminen sisältä auttaa tavaroiden löytämistä.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1230, 1, 'Ovi, oviaukko, vapaa leveys, asunto', 'Oviaukon vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (444, 1, 'Sauna, kiuas, suojaus', NULL, 'Kiuas suojataan suojakaiteilla tai -seinämillä palovammojen estämiseksi. Erityisesti suihkutuolia käyttävän henkilön jalat ovat vaarassa, joten suojaavaa rakennetta on jatkettava tavanomaista alemmaksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1194, 1, 'Sauna, lauteet, käsijohde', NULL, 'Käsijohde tai kädensija lauteille nousua varten.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1193, 1, 'Sauna, ovi, avautuminen', NULL, 'Oven aukeamissuunta löylyhuoneesta katsottuna ulospäin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1287, 1, 'Sauna, ovi, vapaa leveys', 'Oven kulkuaukon leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (363, 1, 'WC-/peseytymistila, ovi, vapaa leveys, julkinen tila', NULL, 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1199, 1, 'WC-/peseytymistila, sijoitus, julkinen tila', NULL, 'Tiloihin tulee olla pääsy suoraan aulasta tai käytävästä. Mikäli WC- ja pesutiloja on erikseen naisille ja miehille, kuhunkin sijoitetaan liikkumisesteiselle soveltuva WC- ja pesutila.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1198, 1, 'WC-/peseytymistila, varusteet', NULL, 'Kerrostalossa, jossa edellytetään hissiä, asuinhuoneiston WC-/peseytymistilan on sovelluttava myös pyörätuolin käyttäjälle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1246, 3, 'Ovi, turvallisuus', NULL, 'Oveen suositellaan ikkunaa 900-1800 mm:n korkeudelle törmäysvaaran estämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1166, 3, 'Sauna, ilmanvaihto, poistoilma', NULL, 'Löylyn painumista alas istumakorkeudelle tehostetaan, kun poistoilmaventtiili sijoitetaan kiukaan vastakkaiseen seinään, n. 200 mm:ä lattiasta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (443, 3, 'Sauna, ilmanvaihto, tuloilma', NULL, 'Löylyn painumista alas istumakorkeudelle tehostetaan, kun tuloilma otetaan ylhäältä 500 mm kiukaan yläpuolelta olevasta venttiilistä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1165, 3, 'Sauna, kiuas', NULL, 'Kiuas sijoitetaan ja ilmanvaihto järjestetään niin, että löylyä tulee myös alas.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1148, 3, 'Sauna, käsijohde', NULL, 'Puinen käsijohde, jossa ei saa olla kuumenevia metalliosia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1145, 3, 'Sauna, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1170, 3, 'Sauna, lauteet', NULL, 'Lauteet tehdään oksattomasta puusta. Lauteissa ei saa olla teräviä särmiä ja ulkonemia ja niiden etureuna pyöristetään. Näkyvissä ei saa olla kuumenevia metalliosia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (450, 3, 'Sauna, lauteet, istumakorkeus', NULL, 'Tavallinen lauteiden istumakorkeus on noin 450 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (451, 3, 'Sauna, lauteet, istumakorkeus', 'Sopiva istumakorkeus (C) henkilölle, jolla on jäykät polvet tai lonkat.', 'Arvo C on välillä 500 ... 550 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (447, 3, 'Sauna, lauteet, käsijohde', NULL, 'Lauteille nousemista varten käsijohteet molemmin puolin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (445, 3, 'Sauna, lauteet, pituus', 'Lauteiden pituus (A), jolloin lauteilla mahtuu makaamaan pitkällään.', 'Arvo A on vähintään 2000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1146, 3, 'Sauna, lauteet, portaat, mitoitus', 'Portaan leveyden (A) vaatimus.', 'Portaan leveys on 600 mm, silloin kun molemmin puolin on pidettävä kiinni käsijohteista.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1150, 3, 'Sauna, lauteet, portaat, mitoitus', 'Portaiden etenemä (A) ja nousu (C) henkilölle, jolla on jäykät polvet tai lonkat.', 'Arvo A on vähintään 390 mm. Arvo C on enintään 120 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1172, 3, 'Sauna, lauteet, portaat, mitoitus', 'Portaan etenemän (A) vaatimus.', 'Arvo A on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (446, 3, 'Sauna, lauteet, ylälauteen syvyys', 'Laudesyvyys (A). Lauteet suunnitellaan käyttäjäkohtaisesti. Suurempi laudesyvyys (B), jos lauteilla on voitava istua jalat suorana.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 1000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (439, 3, 'Sauna, ovi', NULL, 'Törmäysvaaran vuoksi ei kokolasiovea. Ovessa vaakapuite tai havaintomerkkejä noin 1000 mm:n korkeudella.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (441, 3, 'Sauna, ovi, kynnys', NULL, 'Ei kynnystä.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1169, 3, 'Sauna, ovi, vedin', NULL, 'Puinen saunan oven vedin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1136, 3, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus WC/peseytymistilassa on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (690, 3, 'WC-/peseytymistila, lattia, kaltevuus', NULL, 'Peseytymistilan lattian suositeltava kaltevuus on 1,25 % (1:80), lattiakaivon ympärillä 2% (1:50).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1157, 3, 'WC-/peseytymistila, lattia, lattialämmitys', NULL, 'Lattialämmitys on suositeltava.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1156, 3, 'WC-/peseytymistila, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1158, 3, 'WC-/peseytymistila, lattiakaivo', NULL, 'Lattiakaivoa ei saa sijoittaa kulkuväylälle eikä paikkaan, jossa seisotaan (esim. suihkussa) liukastumisvaaran takia. Kannen on oltava tukeva ja pysyttävä hyvin paikallaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (365, 3, 'Yhteistilat, WC-/peseytymistila, varusteet', NULL, 'Pesualtaan tai suihkun läheisyyteen asennetaan hylly esim. silmälaseille, korkeus lattiasta 900 mm. Saippuatelineen yms. asennuskorkeus samoin 900 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1144, 4, 'Sauna, kiuas, seinäkiuas', NULL, 'Seinälle asennettava kiuas lisää lattiapinta-alaa.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (448, 4, 'Sauna, lauteet, istumakorkeus, alin laude', NULL, 'Suihkutuolia käyttävän henkilön on helpompi päästä lauteille, jos alin laude on suihkutuolin istuintasossa eli noin 500 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (449, 4, 'Sauna, lauteet, lauteiden korkeusero', 'Lauteiden korkeusero (C), jolloin lauteelta toiselle siirtyminen on käsivoimin mahdollista.', 'Arvo C on enintään 300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1147, 4, 'Sauna, lauteet, portaat, mitoitus', 'Portaan leveyden (A) vaatimus.', 'Portaan leveys on 900 mm, silloin kun apuna on kainalo- tai kyynärsauvat.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1149, 4, 'Sauna, mitoitus', NULL, 'Normaali laudekorkeus kasvattaa saunan pinta-alaa, koska liikuntaesteinen tarvitseee loivat portaat päästäkseen lauteille. Jos löyly saadaan istumakorkeudelle kiukaan sijoituksella, lauteina voidaan käyttää penkkejä eikä porrasmaisia lauteita.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (442, 4, 'Sauna, mitoitus, vapaa tila', 'Tilantarve esitetty leveyden (A) ja syvyyden (B) avulla. Löylyhuone suunnitellaan niin tilavaksi, että siellä mahtuu kääntymään suihkutuolilla.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (440, 4, 'Sauna, ovi, vapaa leveys', 'Oven kulkuaukon leveys (A) liikuntaesteisille suunnittellussa saunatilassa. Vertailuarvona asuntosaunan oven leveys (B) 2. lähteestä.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1029, 4, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus on 150 lx. Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx. Wc-tiloissa esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1143, 5, 'Sauna, kiuas, lattiaan upotettu kiuas', NULL, 'Kiuas upotetaan lattiaan niin, että kiuaskivet ovat noin lattiapinnan tasossa. Tällöin löylyä saadaan myös penkkien istumakorkeudella.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1233, 5, 'Sauna, kiuas, lattiaan upotettu kiuas, kiuassyvennys', NULL, 'Kiuassyvennyksen on oltava riittävän tilava, jotta se pystytään puhdistamaan.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1173, 5, 'Sauna, kiuas, lattiaan upotettu kiuas, lattiakaivo', NULL, 'Kiuassyvennyksessä pitää olla lattiakaivo.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1237, 5, 'Sauna, kiuas, lattiaan upotettu kiuas, ritilälattia', NULL, 'Siivouksen vuoksi saunan koko lattia on irrotettava ritilälattia, jonka alapuolella on varsinainen lattiakaivollinen lattiapinta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (211, 1, 'Kulkuväylä, törmäysvaara', NULL, 'Kulkuväylällä ei saa olla törmäysvaaraa aiheuttavia rakenteita. Rakenteen törmäysvaara estetään esim. kaiteella ja siitä varoitetaan värillä tai materiaalilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1226, 1, 'Ovi, automaattiovi', NULL, 'Automaattioven avautuminen kulkuväylälle suunnitellaan ja toteutetaan niin, ettei törmäysvaaraa synny.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1227, 1, 'Ovi, automaattiovi, havaittavuus', NULL, 'Oven edustalle lattiaan tehdään tarvittaessa oven kääntymisaluetta kuvaava turvamerkintä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (281, 1, 'Ovi, lasiovi, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C). Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (701, 1, 'Seinä, lasiseinä, havaintomerkkien korkeus', 'Havaintomerkkien korkeus (C) lattiasta. Ikkunat, lasiseinät ja lasiovet, joihin on vaara törmätä, tulee merkitä siten, että ne helposti havaitaan.', 'Arvo C on välillä 900 ... 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (252, 1, 'Sisäänkäynti, katos', NULL, 'Sisäänkäynnin porras, luiska ja ulko-ovi suojataan sateelta katoksella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (176, 2, 'Kulkuväylä, opastava, pääovi', NULL, 'Kulkuväylän, joka ohjaa pääovelle, on oltava esteetön ja helposti havaittava.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1232, 2, 'Ovi, oviaukko, vapaa leveys, ulko-ovi', NULL, 'Asuntojen ulko- ja porrastaso-oviksi suositellaan leveydeltään 10 M-ovia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1008, 2, 'Sisäänkäynti, havaittavuus', NULL, 'Sisäänkäyntiä tulisi korostaa esim. katoksella, jotta sen pystyy erottamaan rakennuksessa.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (254, 2, 'Sisäänkäynti, tasanne, korkeus', 'Ulko-oven edessä olevan tasanteen tai ritilän korkeus (C) kulkuväylän pinnasta.', 'Arvo C on enintään 20 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1238, 2, 'Sisäänkäynti, tasanne, luiska', NULL, 'Ovi ei saa avautua luiskaa käyttävän henkilön tielle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (958, 2, 'Sisäänkäynti, tasoero, luiska', 'Tasoero (C) maanpinnan ja sisäänkäynnin lattian välillä.', 'Arvo C on enintään 500 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (260, 2, 'Tuulikaappi, mitoitus, kääntöovi aukeaa poispäin', 'Tuulikaapin tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (756, 3, 'Ikkuna, korkeus lattiasta', NULL, 'Ikkunan vaakapuitteen sijoitus ei saa estää pyörätuolissa istuvaa henkilöä näkemästä ulos.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (172, 3, 'Kulkuväylä, koordinaatisto', NULL, 'Kulkuväylien on oltava selkeässä koordinaatistossa ja yhtenäisiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (213, 3, 'Kulkuväylä, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton. Ulkona esim. asfaltti, betoni, kivituhka ja laatta.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1208, 3, 'Kulkuväylä, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (216, 3, 'Kulkuväylä, pinta, kuvio', NULL, 'Materiaalikontrasti ja kuviot auttaa suunnistautumista. Tummat kuviot vaalealla yksivärisellä pohjalla ovat selkeimpiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (217, 3, 'Kulkuväylä, pinta, kuvio, opastava', NULL, 'Opastava kuvio esim. pitkittäinen pyöristetty kohokuvio, jonka korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (895, 3, 'Kulkuväylä, pinta, väri', NULL, 'Kulkuväylän tai yksityiskohdan (esim. jalkalistan) väri- ja tummuuskontrasti auttaa suunnistautumista ja varoittaa vaarasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (177, 3, 'Kulkuväylä, päättyminen', NULL, 'Kulkureitti ei saa johtaa umpikujaan.', '3,7,11,14,15,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (840, 3, 'Kytkin/säädin, valopainike, havaittavuus', NULL, 'Painikkeen merkkivalon lisäksi painikkeen väri- tai tummuuskontrasti taustaa vasten auttaa sen havaitsemista.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (839, 3, 'Kytkin/säädin, valopainike, sijoitus', NULL, 'Valopainike sijoitetaan kulkureitin varrelle helposti havaittavaan ja ulottuvissa olevaan paikkaan.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (749, 3, 'Laitteet, kulunvalvontalaite', 'Kulunvalvontalaitteen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (693, 3, 'Lattia, pinta, kuvio', NULL, 'Lattiassa suuret, voimakasväriset kuviot haittaavat suunnistautumista ja tekevät yleisvaikutelman heikkonäköiselle sekavaksi. Heikkonäköinen voi erehtyä pitämään lattian tummaa kohtaa aukkona tai tasoerona lattiassa.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (940, 3, 'Liikkumisen tilantarve, automaattiovi aukeaa kohti, ulkopyörätuoli', 'Tilantarve ulko-oven oven edessä  (A), silloin kun tasanteen syvyys on mitta (B). Vieressä alas johtava porras tai luiska. Jos oven edessä on alasjohtava porras, tarvitaan tilaa enemmän kuin 2000 mm.Oviaukon vapaa leveys 900 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 2700 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (718, 3, 'Opaste, korkeus lattiasta', 'Opasteen sijoituskorkeus (C), myös koho- ja syvennyskuvio-opasteet.', 'Arvo C on välillä 1400 ... 1600 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (717, 3, 'Opaste, materiaali', NULL, 'Pintamateriaali kiiltämätön ja heijastamaton, lasipintaisessa opasteessa heijastamaton lasi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (728, 3, 'Opaste, sijoitus, ovi', NULL, 'Huonetilan opasteet sijoitetaan oven aukeamispuolelle törmäysvaaran välttämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (720, 3, 'Opaste, teksti', NULL, 'Isoilla kirjaimilla kirjoitetaan alkukirjaimet, lyhyet sanat sekä koho- ja syvennyskirjaimet. Pienillä kirjaimilla kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (719, 3, 'Opaste, teksti, kirjaintyyppi', NULL, 'Suurehko ja selvä, esim. Helvetica Medium.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (721, 3, 'Opaste, teksti, kirjaintyyppi, pienet kirjaimet', NULL, 'Pienellä kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (704, 3, 'Opaste, teksti, korkeus', 'Tekstin korkeus (C) opasteissa, jotka luetaan läheltä.', 'Arvo A on vähintään 15 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (705, 3, 'Opaste, teksti, korkeus, sijainti-tai suuntakilpi', 'Tekstin korkeus (A) sijaintia ja suuntaa osoittavissa opasteissa, jotka luetaan muutaman metrin etäisyydeltä.', 'Arvo C on välillä 25 ... 40 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (722, 3, 'Opaste, väri, kontrasti', NULL, 'Tekstin ja symbolin väri- tai tummuuskontrasti taustaa vasten. Parhaiten erottuu musta teksti valkoisella pohjalla.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (852, 3, 'Ovi, aukipysymisaika', 'Aukipysymisaikaa (A) säätäessä otetaan huomioon hitaasti liikkuvat henkilöt. Mikäli ovi avataan painonappia tai painiketta käyttäen automaattisesti, riippuu tarvittava aika painikkeen ja oven välisestä etäisyydestä.', 'Arvo A on vähintään 25 s.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (745, 3, 'Ovi, avaamis- ja säätölaitteet, koodi', NULL, 'Avaamis- ja säätölaitteissa ei tule olla numerokoodia.', '3,7,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (853, 3, 'Ovi, avaamis- ja säätölaitteet, painonappi, sijoitus', NULL, 'Automaattioven painonappi sijoitetaan siten, ettei aukeava ovi aiheuta törmäämisvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (842, 3, 'Porrashuone, nimitaulu, kirjaimet', NULL, 'Kohokirjaimet, jotka ovat 15 mm korkeat ja koholla 1 mm:n verran. Parhaiten erottuu musta teksti valkoisella pohjalla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (841, 3, 'Porrashuone, nimitaulu, korkeus lattiasta', 'Sijoituskorkeus (C) lattiasta. Asukkaiden nimitaulu sijoitetaan katsekorkeudelle helposti löydettävään ja hyvin valaistuun paikkaan esim. ulko-oven vierelle.', 'Arvo C on välillä 1400 ... 1600 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (702, 3, 'Seinä, varusteet, peiliseinä', NULL, 'Hahmottamisen ja häikäisyn takia ei peiliseiniä kulkuväylille.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (255, 3, 'Sisäänkäynti, tasanne, kaltevuus', 'Ulko-oven edessä olevan tasanteen tai ritilän kaltevuus (C) kulkusuunnassa.', 'Arvo C on enintään 2 %.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (256, 3, 'Tuulikaappi', NULL, 'Tuulikaappi vaikeuttaa usein liikkumista. Ahtaat tuulikaapit painavine ovineen ovat vaikeakulkuisia heikkovoimaisille ja apuvälinein liikkuville.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (269, 3, 'Tuulikaappi, automaattiovi', NULL, 'Automaattiovien molempien ovien on toimittava samalla periaatteella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (268, 3, 'Tuulikaappi, lattia, matto/ritilä', NULL, 'Tuulikaappiin ei sovellu paksu, pehmeä matto. Jos käytetään ritilää, se ei saa olla liukas ja sen rakojen enimmäisleveys on 5 mm. Tuulikaapissa olevan säleikön tai maton enimmäiskorkeus on 20 mm; hyvä ratkaisu on maton upotussyvennys.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (989, 3, 'Valaistus, lasiseinä', NULL, 'Valaistus suunnitellaan niin, että lasiseinästä ei aiheudu häikäiseviä heijastuksia.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (863, 3, 'Valaistus, yhteistilat, kulkuväylä', NULL, 'Valaistusvoimakkuus yhteistilojen eteisissä, auloissa, käytävissä, luiskissa ja portaissa on 200 lx.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1059, 3, 'Valaistus, yhteistilat, kulkuväylä', NULL, 'Valaistusvoimakkuus yhteistilojen eteisissä, auloissa, käytävissä, luiskissa ja portaissa on 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (236, 4, 'Kulkuväylä, kalusteet, kalustevyöhyke', NULL, 'Kalustevyöhyke tulisi erottaa kulkuväylästä poikkeavalla pintamateriaalilla ja/tai -värityksellä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (894, 4, 'Kulkuväylä, rakenteiden alareunan korkeus', NULL, 'Kulkuväylälle rajoittuvan rakenteen esim. ulokkeen alareuna ei saa olla liian korkealla, jotta näkövammainen pystyisi sen havaitsemaan. Korkeus maasta tai lattiasta noin 300 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (222, 4, 'Kulkuväylä, reuna', NULL, 'Suunnistautumista helpottaa kulkuväylän rajoittuminen selvästi poikkeavan tuntuiseen pintaan.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (190, 4, 'Liikkumisen tilantarve, pyörätuolien kohdatessa', 'Kulkuväylän leveyden (A) vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1010, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, asunto', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1400 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (453, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, avustaja', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 1400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (907, 4, 'Liikkumisen tilantarve, ulkosähköpyörätuolien kohdatessa', 'Tilantarve esitetty kulkuväylän leveyden (A) avulla.', 'Arvo A on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (725, 4, 'Opaste, teksti, kohokirjain, korkeus', 'Kirjaimen korkeus pinnasta (A) ja kirjaimen korkeus (C). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo A on vähintään 1 mm. Arvo C on välillä 15 ... 40 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (716, 4, 'Opaste, teksti, korkeus, nimikilpi', 'Tekstin korkeus (C) nimikilvissä, jotka luetaan 1..3 m etäisyydeltä.', 'Arvo C on välillä 70 ... 100 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (726, 4, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', NULL, 'Pyörätuolin käyttäjän pistekirjoitusopasteen korkeus lattiasta on 1100 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (261, 4, 'Tuulikaappi, mitoitus, automaattiovi', 'Tuulikaapin minimileveys (A) ja minimisyvyys (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (263, 4, 'Tuulikaappi, mitoitus, automaattiovi, pyörätuolien kohtaaminen', 'Tuulikaapin oviaukon vapaa leveys (A)  kahden pyörätuolin ohittamiseen. Tuulikaapin syvyys (B).', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 2400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (265, 4, 'Tuulikaappi, mitoitus, kääntyminen 90 astetta', 'Tuulikaapin  minimileveys (A) ja minimisyvyys(B), silloin kun ovet ovat eri seinillä ja tuulikaapissa joutuu kääntymään.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (259, 4, 'Tuulikaappi, mitoitus, kääntöovi aukeaa kohti', 'Tuulikaapin minimileveys (A) ja minimisyvyys(B).', 'Tuulikaapin syvyys vähintään 1500 mm + ovilehden leveys.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (266, 4, 'Tuulikaappi, mitoitus, kääntöovi aukeaa kohti', 'Avustettavan pyörätuolin käyttäjän tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 2400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1102, 4, 'Tuulikaappi, mitoitus, kääntöovi, ulkosähköpyörätuoli', 'Tuulikaapin syvyys (A) ulkosähköpyörätuolille.', 'Arvo A on vähintään 2400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (657, 4, 'Yhteistilat, pyörätuolin pesutila, kerrostalo', NULL, 'Käynti pesutilaan, joka sijoitetaan pyörätuolin säilytystilan yhteyteen, suoraan ulkoa. Laitoskäyttöön tarkoitettu pyörien harjauslaite voi tulla kyseeseen, jos on useita pyörätuolin käyttäjiä.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (658, 4, 'Yhteistilat, pyörätuolin pesutila, lattiakaivo', NULL, 'Varustettu hiekanerottimella tai vastaavalla laitteella.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1137, 4, 'Yhteistilat, pyörätuolin pesutila, mitoitus', 'Pesutilan tilantarve esitetty syvyyden (A) ja leveyden (B) avulla.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (724, 5, 'Opaste, kieli', NULL, 'Opasteisiin soveltuu selkokieli, jota ymmärtävät kaikki. Erityisesti sitä tarvitsevat kehitys- ja monivammaiset sekä esim. viittomakieltä äidinkielenään käyttävät.', '1,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (729, 5, 'Opaste, merkinantolaite', NULL, 'Äänimerkkien lisäksi esim. ovipuhelimiin ja merkinantolaitteisiin suositellaan myös visuaalisia ja taktuaalisia eli tuntoaistiin perustuvia merkkejä.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1179, 5, 'Opaste, teksti, kohokirjain, poikkileikkaus', NULL, 'Kohokirjaimen poikkileikkaus on kolmioimainen, jonka yläsärmää pitkin sormea voi liuuttaa. Yläsärmä ei saa olla terävä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1043, 5, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', 'Opasteen korkeus (C) lattiasta.', 'Arvo C on välillä 1300 ... 1400 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1180, 5, 'Opaste, teksti, syvennyskirjain, korkeus', NULL, 'Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta. Kirjaimen syvyys tai korkeus pinnasta 1 mm ja kirjaimen korkeus 30-60 mm. Kirjaimen korkeus 15-40 mm (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1181, 5, 'Opaste, teksti, syvennyskirjain, leveys', 'Kirjaimen leveys eli syvennysuran leveys (A). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo C on välillä 6 ... 10 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (744, 5, 'Ovi, avaamis- ja säätölaitteet, ulko-ovi', NULL, 'Raskaat ovet joko automaattisesti avautuvia tai kaukosäätimellä avattavia.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1195, 1, 'WC, ovi, avautuminen', NULL, 'Wc:stä katsottuna wc-oven aukeamissuunta on ulospäin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (438, 1, 'WC-/peseytymistila, opaste, julkinen tila', NULL, 'Julkisissa tiloissa liikkumisesteisen WC merkitään ISA-opasteella ja kuvalla, josta selviää kummalla puolella wc-istuinta on vapaa tila pyörätuolia varten.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1196, 1, 'WC-/peseytymistila, ovi, lukko', NULL, 'Lukko avattavissa myös ulkopuolelta ns. vahtimestarin avaimella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (363, 1, 'WC-/peseytymistila, ovi, vapaa leveys, julkinen tila', NULL, 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1198, 1, 'WC-/peseytymistila, varusteet', NULL, 'Kerrostalossa, jossa edellytetään hissiä, asuinhuoneiston WC-/peseytymistilan on sovelluttava myös pyörätuolin käyttäjälle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1297, 1, 'Yhteistilat, wc -istuin, käsituet', NULL, 'Wc -istuin varustetaan kääntyvillä käsituilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1151, 1, 'Yhteistilat, WC-/peseytymistila, mitoitus', NULL, 'Yhteistiloihin liittyvät wc:t suunnitellaan liikkumisesteiselle soveltuviksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (187, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli julkisessa tilassa', 'Pyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (350, 2, 'Pesuallas, etäisyys seinästä', 'Pesualtaan etäisyys seinästä (A). Pesuallas asennetaan ulos seinästä polvitilan aikaansaamiseksi.', 'Arvo A on vähintään 200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (348, 2, 'Pesuallas, kiinnitys', 'Altaan kiinnitys kestää painon (A) ja altaaseen tukeutumisen.', 'Arvo A on vähintään 150 kg.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (299, 2, 'WC-/peseytymistila, mitoitus, molemminpuolisesti käytettävä tila, julkinen tila', 'Tilantarve esitetty pesualtaan sijoitusseinän  sisämitan (A) ja  wc-istuimen sijoitusseinän sisämitan (B) avulla. Jos wc-istuimen toisella puolella on suihku, tulee wc-istuimen sijoitusseinän sisämitan olla mitta (C).', 'Arvo A on vähintään 2500 mm. Arvo B on vähintään 2200 mm. Arvo C on vähintään 2300 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (298, 2, 'WC-/peseytymistila, mitoitus, yksipuolisesti käytettävä tila, julkinen tila', 'Tilantarve esitetty leveyden (A) ja syvyyden (B) avulla. Pesuallas ja wc-istuin sijoitetaan lyhyille sivuille vastakkain.', 'Arvo A on vähintään 2700 mm. Arvo B on vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1200, 2, 'WC-/peseytymistila, sijoitus, yksipuolisesti käytettävä tila, julkinen tila', NULL, 'Molemminpuolisesti käytettävän wc-tilan sijaan voidaan käyttää yksipuolisesti käytettävää wc-tilaa peilikuvapareina.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (304, 2, 'WC-/peseytymistilat,  WC-istuin, vapaa tila vieressä, julkinen tila', 'Vapaa tila pyörätuolille wc-istuimen vieressä (A). Jos molemminpuolisesti käytettävässä WC:ssä vapaa tila on mitta (B), siihen voi sijoittaa myös suihkun ilman suihkuallasta.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (300, 2, 'WC-istuin, vapaa tila takana', 'Avustajan tilantarve (A) wc-istuimen takana. Vertailuarvona tilantarve (B) yhteistilojen wc:ssä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1248, 2, 'Yhteistilat, WC-/peseytymistila', NULL, 'Asuinrakennuksessa pitää olla yksi WC- tila, joka soveltuu liikkumisesteiselle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (371, 2, 'Yhteistilat, WC-/peseytymistila, varusteet, hälytysjärjestelmä', NULL, 'Jos rakennuksessa on valvontajärjestelmä, asennetaan liikkumisesteisen wc-tiloista hälytysyhteys.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1243, 3, 'Käsituki, vinotuki', NULL, 'Vinotuki asennetaan wc-istuimen etulinjan kohdalle. Asennuskorkeus lattiasta 800 ja 1000 mm kts. kuva.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (693, 3, 'Lattia, pinta, kuvio', NULL, 'Lattiassa suuret, voimakasväriset kuviot haittaavat suunnistautumista ja tekevät yleisvaikutelman heikkonäköiselle sekavaksi. Heikkonäköinen voi erehtyä pitämään lattian tummaa kohtaa aukkona tai tasoerona lattiassa.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (692, 3, 'Lattia, pinta, kuvio, opastava', NULL, 'Kulkusuunnan suuntainen kuviointi helpottaa suunnistautumista.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1069, 3, 'Liikkumisen tilantarve, kääntyminen 360 astetta, sisäpyörätuoli', 'Sisäpyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1082, 3, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, sisäpyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Oviaukon leveys 750 - 800 mm.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1109, 3, 'Ovi, oviaukko, vapaa leveys, sisäpyörätuoli', 'Oven vapaa leveys (A). Vertailuarvona leveys (B) silloin kun ovi on raskas tai siinä on kynnys.', 'Arvo A on vähintään 750 mm. Arvo B on vähintään 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (346, 3, 'Pesuallas, laskutaso', NULL, 'Pesualtaan yhteydessä laskutaso, johon voi myös tukeutua.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (347, 3, 'Pesuallas, reuna', NULL, 'Altaan reunamuoto sellainen, johon on helppo tukeutua.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (357, 3, 'Pesuallas, sekoittaja', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (358, 3, 'Pesuallas, sekoittaja, sijoitus', NULL, 'Pesualtaan sekoittajaan on ulotuttava kurkottamatta. Ulottumisen helpottamiseksi voidaan käyttää tavallista pidempää vipua tai sijoittaa sekoittaja pesualtaan etuosaan.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1108, 3, 'Pesuallas, tyyppi, käsituki', NULL, 'Erikoisallas, jonka etureunassa on käsituki.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1107, 3, 'Pesuallas, vapaa tila altaan edessä', 'Rollaattorin käyttäjän tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (359, 3, 'Pesuallas, väri', NULL, 'Väri- tai tummuusero taustaa vasten auttaa altaan havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1136, 3, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus WC/peseytymistilassa on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1056, 3, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 200 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (627, 3, 'WC-/peseytymistila, käsituki, materiaali', NULL, 'Mattapintainen, muovipäällysteinen käsituki on suositeltavampi kuin metallipintainen. Metallipintainen käsijohde on liukas ja se saattaa häikäistä ja aiheuttaa allergiaa (nikkeli).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (690, 3, 'WC-/peseytymistila, lattia, kaltevuus', NULL, 'Peseytymistilan lattian suositeltava kaltevuus on 1,25 % (1:80), lattiakaivon ympärillä 2% (1:50).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1157, 3, 'WC-/peseytymistila, lattia, lattialämmitys', NULL, 'Lattialämmitys on suositeltava.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1156, 3, 'WC-/peseytymistila, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1158, 3, 'WC-/peseytymistila, lattiakaivo', NULL, 'Lattiakaivoa ei saa sijoittaa kulkuväylälle eikä paikkaan, jossa seisotaan (esim. suihkussa) liukastumisvaaran takia. Kannen on oltava tukeva ja pysyttävä hyvin paikallaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (361, 3, 'WC-/peseytymistila, ovi, avaaminen', NULL, 'Ovi avattavissa ja lukittavissa yhdellä kädellä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (369, 3, 'WC-/peseytymistila, varusteet, peili, yläreunan korkeus', 'Peilin yläreunan korkeus (C). Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo C on välillä 1700 ... 1800 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1203, 3, 'WC-istuin, etäisyys sivuseinästä', NULL, 'WC-istuimen keskilinjan etäisyys sivuseinästä on 400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (314, 3, 'WC-istuin, huuhtelu', NULL, 'Vähäisellä käsivoimalla, valokennolla tai pitkällä vivulla toimiva laite.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (315, 3, 'WC-istuin, kansi', NULL, 'Kansi kestää istumisen (150 kg) ja pysyy tukevasti paikoillaan.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (310, 3, 'WC-istuin, korkeus', NULL, 'WC-istuimen normaali korkeus on 450 mm. Istumakorkeutta voi muuttaa korkeussäädettävässä wc-istuimessa. Tavalliseen WC- istuimeen voidaan asentaa istuinrenkaan korotusosa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (313, 3, 'WC-istuin, korkeus', 'Sopiva istumakorkeus (C) henkilölle, jolla on jäykät polvet tai lonkat.', 'Arvo C on välillä 500 ... 550 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (321, 3, 'WC-istuin, käsisuihku', 'Käsisuihkun etäisyys (C) WC-istuimelta. WC-istuimelta on ulotuttava käsisuihkuun ja sen hanaan.', 'Arvo C on enintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (322, 3, 'WC-istuin, käsituet', NULL, 'Käsituet ovat käännettävissä tai nostettavissa pois avustajan tieltä ja niiden pitää salpautua sivulle tai ylös.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (329, 3, 'WC-istuin, käsituet, etäisyys toisistaan', 'Käsitukien välinen etäisyys (C).', 'Arvo C on välillä 550 ... 600 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (324, 3, 'WC-istuin, käsituet, korkeus lattiasta', 'Käsitukien korkeus (C) lattiasta. Vaihtoehtoisesti korkeussäädettävät käsituet.', 'Arvo C on välillä 750 ... 800 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (326, 3, 'WC-istuin, käsituet, pituus', NULL, 'Käsituet ulottuvat vähintään wc-istuimen etureunaan, mutta enintään 100 mm etureunan ohitse. Julkisissa WC-tiloissa enintään 200 mm etureunan ohitse.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1206, 3, 'WC-istuin, käsituki, poikkituki', NULL, 'Istuimen eteen sijoitettava poikkituki ulottuu 100 mm wc-istuimen keskilinjan ohi, jotta siihen voi tarttua kaksin käsin.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (334, 3, 'WC-istuin, paperirullateline', NULL, 'Paperirullaan on ulotuttava WC- istuimelta kurkottamatta. Paperirullatelineen voidaan kiinnittää käsitukeen.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (303, 3, 'WC-istuin, vapaa tila', 'Rollaattorin kääntämiseen tarvitava vapaa tila wc-istuimen edessä. Pesualtaan sijaitessa wc-istuimen edessä riittää mitta (B), jos rollaattorin pyörät ja etuosa mahtuvat osittain pesualtaan alle. Wc -istuimen etäisyys (C) sivuseinästä.', 'Arvo A on vähintään 1050 mm. Arvo B on vähintään 800 mm. Arvo C on välillä 600 ... 650 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (365, 3, 'Yhteistilat, WC-/peseytymistila, varusteet', NULL, 'Pesualtaan tai suihkun läheisyyteen asennetaan hylly esim. silmälaseille, korkeus lattiasta 900 mm. Saippuatelineen yms. asennuskorkeus samoin 900 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (525, 4, 'Kalusteet, polvitilan vesijohdot', NULL, 'Polvitilassa olevat kuumavesiputket yms. asennetaan lähelle seinää ja suojataan.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (911, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, sisäpyörätuoli', 'Käytävän leveys (A). Vertailuarvona tilantarve (B), silloin kun oviaukkoa voi käyttää kääntymistilana.', 'Arvo A on vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (870, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1070, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 1000 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 250 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1073, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B)  Oven leveys 1000 mm.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1074, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oven leveys 900 mm.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1075, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, sisäpyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta mahdollistaa esteettömän kääntymisen. Oven leveys 900/1000 mm.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1202, 4, 'Pesuallas, etäisyys sivuseinästä', 'Normaalikokoisen pesualtaan (esim. 550 x 400 mm) keskilinjan etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (349, 4, 'Pesuallas, korkeus lattiasta', 'Korkeus (C) lattiasta. Pyörätuolin käyttäjälle soveltuvan pesualtaan asennuskorkeus. Pyörätuolilla pääsee lähemmäksi allasta, jos sen pohja on vino.', 'Arvo C on välillä 770 ... 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1103, 4, 'Pesuallas, korkeus lattiasta', 'Korkeus (C) lattiasta. Pesuallas asennetaan käyttäjän yksilölliselle korkeudelle.', 'Arvo C on välillä 700 ... 950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (355, 4, 'Pesuallas, muoto', NULL, 'Kovera etureuna helpottaa ulottumista ja kumartumista altaan ylle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (353, 4, 'Pesuallas, polvitila', 'Esitetty polvitilan leveyden (A), syvyyden (B) ja korkeuden (C) avulla.', 'Arvo A on vähintään 800 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1105, 4, 'Pesuallas, tyyppi, korkeussäädettävä allas', NULL, 'Käyttäjä voi säätää sähköisesti korkeussäädettävän altaan sopivalle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1106, 4, 'Pesuallas, tyyppi, siirrettävä allas', NULL, 'WC-istuimen vierellä sivusuunnassa kiskoilla siirrettävä allas, jolloin sitä käytetään WC-istuimella istuen. Istuimen vierestä vapautuu tilaa avustajalle, kun allas siirretään pois.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (351, 4, 'Pesuallas, vapaa tila altaan edessä', 'Vapaan tilan syvyys (A).', 'Arvo A on vähintään 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1029, 4, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus on 150 lx. Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx. Wc-tiloissa esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (790, 4, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 300 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (360, 4, 'WC-/peseytymistila, ovi, etäisyys sivuseinästä', 'Oven karmin etäisyys (A) nurkasta oven avaamispuolella.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (368, 4, 'WC-/peseytymistila, varusteet, peili, alareunan korkeus', 'Pesualtaan yläpuolella oleva peilin alareunan korkeus (C) lattiasta riippuu pesualtaan korkeudesta.', 'Arvo C on välillä 800 ... 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (311, 4, 'WC-istuin, korkeus', 'Wc-istuimen korkeus (C) on lähes sama kuin pyörätuolin istuimen korkeus. Jos käytetään reiällistä suihku/wc-tuolia, silloin wc-istuin on normaalin korkuinen.', 'Arvo C on välillä 480 ... 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (302, 4, 'WC-istuin, vapaa tila', 'Suoraan edestäpäin wc-istuimelle siirryttäessä tarvittavan tilan syvyys(A) ja leveys (B).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (308, 4, 'WC-istuin, vapaa tila', NULL, 'Vapaa tila istuimen edessä on vähintään 1050 mm. WC-istuimen keskilinjan etäisyys sivuseinästä on vähintään 650 - 700 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (301, 4, 'Yhteistilat, WC-istuin, vapaa tila edessä', 'Vapaa tila annettu syvyyden (A) avulla.', 'Arvo A on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (356, 5, 'Pesuallas, muoto', NULL, 'Allastasossa tai tasoon upotetussa altaassa selkeä reuna, jotta allas erottuu tasosta', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1104, 5, 'Pesuallas, tyyppi, käännettävä allas', 'Altaan leveys (A) ja syvyys (B). Allas, joka voidaan kääntää vinoon asentoon, soveltuu vinossa asennossa pyörätuolin käyttäjälle ja suorana muille käyttäjille. Polvitilaa lisää, jos se asennetaan 200 mm irti seinästä.', 'Arvo A on vähintään 670 mm. Arvo B on vähintään 430 mm, mutta arvoksi suositellaan vähintään 630 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1289, 5, 'Wc-/ peseytymistilat, varusteet, henkilönosturi', 'Wc:ssä voidaan käyttää apuvälineenä kattonosturia tai liikuteltavaa nosturia.', 'Kattonosturin käyttämiseen tarvittavan tilan leveys 1800 mm ja syvyys 2600 mm wc-istuimen ympärillä. Liikuteltavan nosturin käyttämiseen tarvittavan tilan leveys 1600 mm ja syvyys 2150 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (316, 5, 'WC-istuin, kansi, väri', NULL, 'Väri tai tummuskontrasti istuinta vasten.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1204, 5, 'WC-istuin, tyyppi', NULL, 'Seinään kiinnitettävä wc-istuin voidaan asentaa yksilölliselle korkeudelle. Esim. rollaattorin pyörät ja etuosa mahtuvat osittain istuimen alle ja lisäävät kääntymistilaa. Vapaa lattiapinta helpottaa myös siivousta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1229, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Kulkuväylältä hisseihin tai tasonvaihtojärjestelmiin sekä hallinto-, palvelu- ja liiketiloihin johtavien ovien vapaa leveys (A).', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1195, 1, 'WC, ovi, avautuminen', NULL, 'Wc:stä katsottuna wc-oven aukeamissuunta on ulospäin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1196, 1, 'WC-/peseytymistila, ovi, lukko', NULL, 'Lukko avattavissa myös ulkopuolelta ns. vahtimestarin avaimella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1198, 1, 'WC-/peseytymistila, varusteet', NULL, 'Kerrostalossa, jossa edellytetään hissiä, asuinhuoneiston WC-/peseytymistilan on sovelluttava myös pyörätuolin käyttäjälle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (333, 2, 'Käsituki seinässä', NULL, 'Seinärakenteet suunnitellaan sellaisiksi, että käsijohteita ja tukikahvoja voidaan lisätä tarvittaessa.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (348, 2, 'Pesuallas, kiinnitys', 'Altaan kiinnitys kestää painon (A) ja altaaseen tukeutumisen.', 'Arvo A on vähintään 150 kg.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1171, 2, 'WC-/peseytymistila, luiska', NULL, 'Märkätiloissa ei saa olla luiskaa liukastumisriskin takia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (300, 2, 'WC-istuin, vapaa tila takana', 'Avustajan tilantarve (A) wc-istuimen takana. Vertailuarvona tilantarve (B) yhteistilojen wc:ssä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (371, 2, 'Yhteistilat, WC-/peseytymistila, varusteet, hälytysjärjestelmä', NULL, 'Jos rakennuksessa on valvontajärjestelmä, asennetaan liikkumisesteisen wc-tiloista hälytysyhteys.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (707, 3, 'Kalusteet, pintamateriaali', NULL, 'Pinnan on oltava kiiltämätön, helposti puhdistettava, kestävä ja se ei saa aiheuttaa allergiaa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (331, 3, 'Käsituki seinässä, korkeus', NULL, 'Käsituet sijoitetaan toimintakohtaisesti ja asennetaan yksilölliselle korkeudelle. Yleisesti käytetty asennuskorkeus on käsijohteen korkeudella eli 900 mm lattiasta.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1240, 3, 'Käsituki, materiaali', NULL, 'Käsituen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1241, 3, 'Käsituki, pyöreä', 'Käsituen etäisyys seinästä eli vapaa sormitila (A). Pyöreän käsituen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 30 ... 40 mm.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1225, 3, 'Käsituki, sijoitus', NULL, 'Käsituet asennetaan asukkaan yksilöllisten vaatimusten mukaisesti. Mittasuositukset ja asennuskorkeudet ovat yleisiä ohjeita, ellei käsituki ole säädettävä esim. wc-istuimen yhteydessä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1243, 3, 'Käsituki, vinotuki', NULL, 'Vinotuki asennetaan wc-istuimen etulinjan kohdalle. Asennuskorkeus lattiasta 800 ja 1000 mm kts. kuva.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (693, 3, 'Lattia, pinta, kuvio', NULL, 'Lattiassa suuret, voimakasväriset kuviot haittaavat suunnistautumista ja tekevät yleisvaikutelman heikkonäköiselle sekavaksi. Heikkonäköinen voi erehtyä pitämään lattian tummaa kohtaa aukkona tai tasoerona lattiassa.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (692, 3, 'Lattia, pinta, kuvio, opastava', NULL, 'Kulkusuunnan suuntainen kuviointi helpottaa suunnistautumista.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (696, 3, 'Lattia, pinta, matto', NULL, 'Irtomaton on pysyttävä paikallaan ja se ei saa aiheuttaa kompastumisvaaraa.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1228, 3, 'Ovi, kynnys', NULL, 'Kynnykset suunnitellaan ja asennetaan siten, että ne voidaan tarvittaessa vaihtaa tai poistaa. Kosteissa tiloissa voidaan käyttää kumikynnyksiä tai kynnyskaivoa. Äänieristysovissa suositellaan mekaanisia tiivistekynnyksiä.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (346, 3, 'Pesuallas, laskutaso', NULL, 'Pesualtaan yhteydessä laskutaso, johon voi myös tukeutua.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (347, 3, 'Pesuallas, reuna', NULL, 'Altaan reunamuoto sellainen, johon on helppo tukeutua.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (357, 3, 'Pesuallas, sekoittaja', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (358, 3, 'Pesuallas, sekoittaja, sijoitus', NULL, 'Pesualtaan sekoittajaan on ulotuttava kurkottamatta. Ulottumisen helpottamiseksi voidaan käyttää tavallista pidempää vipua tai sijoittaa sekoittaja pesualtaan etuosaan.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1108, 3, 'Pesuallas, tyyppi, käsituki', NULL, 'Erikoisallas, jonka etureunassa on käsituki.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1107, 3, 'Pesuallas, vapaa tila altaan edessä', 'Rollaattorin käyttäjän tilantarve esitetty leveyden (A) ja syvyyden (B) avulla.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (359, 3, 'Pesuallas, väri', NULL, 'Väri- tai tummuusero taustaa vasten auttaa altaan havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (832, 3, 'Valaistus, heijastumisprosenttien suhde', NULL, 'Liikkumisen ja toimimisen kannalta tärkeät kohdat esim. ovet on helpompi havaita, jos vaalean ja tumman rakennusosan valon heijastumisprosenttien suhde on vähintään 1:3.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (981, 3, 'Valaistus, ikkuna', NULL, 'Ikkunan luona oleva valaistus ei saa häikäistä eikä heijastua ikkunalasista. Epäsuora valaistus sijoitetaan ikkunaseinällä esim. verholaudan taakse.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1136, 3, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus WC/peseytymistilassa on 100 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1056, 3, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 200 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (612, 3, 'Varusteet, koukut, korkeus lattiasta', 'Vaate- ja pyyhekoukkujen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 1100 ... 1400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (613, 3, 'Varusteet, peili, korkeus lattiasta', 'Alareunan (A) ja ylareunan (B) korkeus lattiasta niin, että myös pyörätuolin käyttäjä näkee itsensä peilistä. Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo A on vähintään 300 mm. Arvo B on vähintään 1800 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (627, 3, 'WC-/peseytymistila, käsituki, materiaali', NULL, 'Mattapintainen, muovipäällysteinen käsituki on suositeltavampi kuin metallipintainen. Metallipintainen käsijohde on liukas ja se saattaa häikäistä ja aiheuttaa allergiaa (nikkeli).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (690, 3, 'WC-/peseytymistila, lattia, kaltevuus', NULL, 'Peseytymistilan lattian suositeltava kaltevuus on 1,25 % (1:80), lattiakaivon ympärillä 2% (1:50).', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1157, 3, 'WC-/peseytymistila, lattia, lattialämmitys', NULL, 'Lattialämmitys on suositeltava.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1156, 3, 'WC-/peseytymistila, lattia, ritilä', NULL, 'Irrallista lattiaritilää ei pidä käyttää liukastumis- ja kompastumisvaaran vuoksi.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1158, 3, 'WC-/peseytymistila, lattiakaivo', NULL, 'Lattiakaivoa ei saa sijoittaa kulkuväylälle eikä paikkaan, jossa seisotaan (esim. suihkussa) liukastumisvaaran takia. Kannen on oltava tukeva ja pysyttävä hyvin paikallaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (361, 3, 'WC-/peseytymistila, ovi, avaaminen', NULL, 'Ovi avattavissa ja lukittavissa yhdellä kädellä.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (369, 3, 'WC-/peseytymistila, varusteet, peili, yläreunan korkeus', 'Peilin yläreunan korkeus (C). Seinän korkuisia peilejä vältetään, koska ne voivat antaa näkövammaiselle mielikuvan aukosta.', 'Arvo C on välillä 1700 ... 1800 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1203, 3, 'WC-istuin, etäisyys sivuseinästä', NULL, 'WC-istuimen keskilinjan etäisyys sivuseinästä on 400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (314, 3, 'WC-istuin, huuhtelu', NULL, 'Vähäisellä käsivoimalla, valokennolla tai pitkällä vivulla toimiva laite.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (315, 3, 'WC-istuin, kansi', NULL, 'Kansi kestää istumisen (150 kg) ja pysyy tukevasti paikoillaan.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (310, 3, 'WC-istuin, korkeus', NULL, 'WC-istuimen normaali korkeus on 450 mm. Istumakorkeutta voi muuttaa korkeussäädettävässä wc-istuimessa. Tavalliseen WC- istuimeen voidaan asentaa istuinrenkaan korotusosa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (313, 3, 'WC-istuin, korkeus', 'Sopiva istumakorkeus (C) henkilölle, jolla on jäykät polvet tai lonkat.', 'Arvo C on välillä 500 ... 550 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (321, 3, 'WC-istuin, käsisuihku', 'Käsisuihkun etäisyys (C) WC-istuimelta. WC-istuimelta on ulotuttava käsisuihkuun ja sen hanaan.', 'Arvo C on enintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (322, 3, 'WC-istuin, käsituet', NULL, 'Käsituet ovat käännettävissä tai nostettavissa pois avustajan tieltä ja niiden pitää salpautua sivulle tai ylös.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (329, 3, 'WC-istuin, käsituet, etäisyys toisistaan', 'Käsitukien välinen etäisyys (C).', 'Arvo C on välillä 550 ... 600 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (324, 3, 'WC-istuin, käsituet, korkeus lattiasta', 'Käsitukien korkeus (C) lattiasta. Vaihtoehtoisesti korkeussäädettävät käsituet.', 'Arvo C on välillä 750 ... 800 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (326, 3, 'WC-istuin, käsituet, pituus', NULL, 'Käsituet ulottuvat vähintään wc-istuimen etureunaan, mutta enintään 100 mm etureunan ohitse. Julkisissa WC-tiloissa enintään 200 mm etureunan ohitse.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1206, 3, 'WC-istuin, käsituki, poikkituki', NULL, 'Istuimen eteen sijoitettava poikkituki ulottuu 100 mm wc-istuimen keskilinjan ohi, jotta siihen voi tarttua kaksin käsin.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (334, 3, 'WC-istuin, paperirullateline', NULL, 'Paperirullaan on ulotuttava WC- istuimelta kurkottamatta. Paperirullatelineen voidaan kiinnittää käsitukeen.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (303, 3, 'WC-istuin, vapaa tila', 'Rollaattorin kääntämiseen tarvitava vapaa tila wc-istuimen edessä. Pesualtaan sijaitessa wc-istuimen edessä riittää mitta (B), jos rollaattorin pyörät ja etuosa mahtuvat osittain pesualtaan alle. Wc -istuimen etäisyys (C) sivuseinästä.', 'Arvo A on vähintään 1050 mm. Arvo B on vähintään 800 mm. Arvo C on välillä 600 ... 650 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (365, 3, 'Yhteistilat, WC-/peseytymistila, varusteet', NULL, 'Pesualtaan tai suihkun läheisyyteen asennetaan hylly esim. silmälaseille, korkeus lattiasta 900 mm. Saippuatelineen yms. asennuskorkeus samoin 900 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (985, 4, 'Kalusteet, sokkeli', NULL, 'Kalusteisiin tehdään ulottuvuuden parantamiseksi sisäänvedetty sokkeli tai asennetaan säädettävät jalat tai irrotettava sokkelilevy. Kaluste voidaan jättää ilman sokkelia, jolloin se asennetaan valmiiin lattiapinnan päälle. Sokkelin korkeus min. 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (708, 4, 'Kalusteet, sokkeli, mitoitus', 'Kiintokalusteiden sokkelin syvyys (A) ja korkeus (B). Korkeus (C) 2. lähteestä.', 'Arvo A on vähintään 150 mm. Arvo B on vähintään 200 mm. Arvo C on vähintään 250 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (748, 4, 'Ovi, avaamis- ja säätölaitteet, ovisummeri', NULL, 'Ovisummerin täydentäminen esim. valomerkillä, täristimellä tai muulla kuulovamman laatuun sopivalla merkinantolaitteella.', '1');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1103, 4, 'Pesuallas, korkeus lattiasta', 'Korkeus (C) lattiasta. Pesuallas asennetaan käyttäjän yksilölliselle korkeudelle.', 'Arvo C on välillä 700 ... 950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1105, 4, 'Pesuallas, tyyppi, korkeussäädettävä allas', NULL, 'Käyttäjä voi säätää sähköisesti korkeussäädettävän altaan sopivalle korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1106, 4, 'Pesuallas, tyyppi, siirrettävä allas', NULL, 'WC-istuimen vierellä sivusuunnassa kiskoilla siirrettävä allas, jolloin sitä käytetään WC-istuimella istuen. Istuimen vierestä vapautuu tilaa avustajalle, kun allas siirretään pois.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1029, 4, 'Valaistus, WC-/peseytymistila', NULL, 'Valaistusvoimakkuus on 150 lx. Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx. Wc-tiloissa esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (790, 4, 'Valaistus, WC-/peseytymistila, kohdevalaistus', NULL, 'Peilivalaisimen valaistusvoimakkuus on 300 lx. Valaisin peilin yläpuolella tai molemmin puolin, häikäisemätön valo.Sopiva valaistusvoimakkus lähityöskentelyssä 400- 1000 lx esim. peilin edessä tai pesuvälineiden säilytyskohdassa ( 2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (356, 5, 'Pesuallas, muoto', NULL, 'Allastasossa tai tasoon upotetussa altaassa selkeä reuna, jotta allas erottuu tasosta', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (316, 5, 'WC-istuin, kansi, väri', NULL, 'Väri tai tummuskontrasti istuinta vasten.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1204, 5, 'WC-istuin, tyyppi', NULL, 'Seinään kiinnitettävä wc-istuin voidaan asentaa yksilölliselle korkeudelle. Esim. rollaattorin pyörät ja etuosa mahtuvat osittain istuimen alle ja lisäävät kääntymistilaa. Vapaa lattiapinta helpottaa myös siivousta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1085, 1, 'Autopaikka, liikkumisesteisen autopaikka', NULL, 'Osan rakennuksen autopaikoista tulee soveltua pyörätuolin käyttäjälle. Ne tulee merkitä liikkumisesteisten tunnuksella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (242, 1, 'Autopaikka, liikkumisesteisen autopaikka, mitoitus', 'Mitoitus esitetty leveyden (A) ja pituuden (B) avulla. Mikäli pysäköintipaikka sijoitetaan jalkakäytävän viereen, riittää normaali leveys (2500 mm).', 'Arvo A on vähintään 3600 mm. Arvo B on vähintään 5000 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (272, 2, 'Autopaikka, opaste', NULL, 'Liikkumisesteisten autopaikat pitää merkitä kansainvälisellä ISA-tunnuksella.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (245, 3, 'Autopaikka, kaltevuus', 'Autopaikan kaltevuus (C) pituus- ja leveyssuuntaan.', 'Arvo C on enintään 2 %.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (241, 3, 'Autopaikka, lukumäärä/ autopaikka', NULL, 'Kaksi liikkumisesteisen autopaikkaa/ 50 ensimmäistä autopaikkaa, jonka jälkeen 1 ap/ 50 ap. Asemakaavassa voi olla erillinen määräys autopaikkojen määrästä.', '11,14,15,20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1025, 3, 'Autopaikka, lukumäärä/ kerrosalaneliömetri', NULL, 'Kaksi liikkumisesteisen autopaikkaa/ 2500 kerrosalaneliömetri, jonka jälkeen 1 ap/ 2500-5000 kem2. Asemakaavassa voi olla erillinen määräys autopaikkojen määrästä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1026, 3, 'Autopaikka, lukumäärä/ pysäköintilaitos', NULL, 'Asuntoalueilla 1 liikkumisesteisen autopaikkaa/ 30 autopaikkaa. Muualla  1 ap/ 50 ap.Asemakaavassa voi olla erillinen määräys autopaikkojen määrästä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1154, 3, 'Autopaikka, lämmityslaite, korkeus maanpinnasta', NULL, 'Arvo C on välillä 900 ... 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (244, 3, 'Autopaikka, mitoitus,  erityisajoneuvo', 'Mitoitus on esitetty leveyden (A) ja pituuden (B) avulla.', 'Arvo A on vähintään 3600 mm. Arvo B on vähintään 8500 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (246, 3, 'Autopaikka, mitoitus, vapaa tila auton takana', 'Tilaa (A) tarvitaan pyörätuolin tavaratilaan lastaamista varten.', 'Arvo A on vähintään 1200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (247, 3, 'Autopaikka, mitoitus, vapaa tila auton vieressä', 'Tilaa (A) tarvitaan autoon ja autosta pois nousemiseen.', 'Arvo A on vähintään 1200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (951, 4, 'Autopaikka, autotalli, leveys', 'Autopaikan leveys (A).', 'Arvo A on vähintään 3600 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (273, 4, 'Autopaikka, autotalli, ovi', NULL, 'Ovessa automaattinen avauslaite, jota käytetään autosta kaukosäätimellä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (952, 4, 'Autopaikka, autotalli, ovi, leveys', 'Autotallin oven leveys (A).', 'Arvo A on vähintään 2500 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (816, 2, 'Valaistus, yhteistilat', 'Yhteistilojen, jätetilojen ja varastojen yleisvalaistusvaatimus.', 'Valaistusvoimakkuus varastotiloissa on 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (839, 3, 'Kytkin/säädin, valopainike, sijoitus', NULL, 'Valopainike sijoitetaan kulkureitin varrelle helposti havaittavaan ja ulottuvissa olevaan paikkaan.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (852, 3, 'Ovi, aukipysymisaika', 'Aukipysymisaikaa (A) säätäessä otetaan huomioon hitaasti liikkuvat henkilöt. Mikäli ovi avataan painonappia tai painiketta käyttäen automaattisesti, riippuu tarvittava aika painikkeen ja oven välisestä etäisyydestä.', 'Arvo A on vähintään 25 s.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (668, 3, 'Yhteistilat, jätetila, sijoitus', 'Jätetilan etäisyys (C) sisäänkäynnistä. Jätetila sijoitetaan pääkulkureitin varrelle.', 'Arvo C on enintään 50 m.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (669, 3, 'Yhteistilat, jätetila, säiliöt', NULL, 'Käytettävissä myös heikoilla voimilla ja pyörätuolissa istuen.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (670, 3, 'Yhteistilat, jätetila, säiliöt, lajittelu', NULL, 'Eri laatuisten jätteiden säiliöiden merkintä selvästi.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (179, 1, 'Kulkuväylä, turvallisuus', NULL, 'Ajoneuvoliikenne tontilla ei saisi ristetä jalankulkuliikenteen kanssa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (211, 1, 'Kulkuväylä, törmäysvaara', NULL, 'Kulkuväylällä ei saa olla törmäysvaaraa aiheuttavia rakenteita. Rakenteen törmäysvaara estetään esim. kaiteella ja siitä varoitetaan värillä tai materiaalilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1298, 1, 'Kulkuväylä, ulkotilat', NULL, 'Rakennuksen ulkopuolisten välttämättömien kulkuyhteyksien on sovelluttava liikkumisesteisille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (237, 2, 'Kulkuväylä, istutukset', NULL, 'Kulkuväylän varrella olevien istutusten on oltava myrkyttömiä ja allergiaa aiheuttamattomia. Kasvit on sijoitettava kulkuväylän ulkopuolelle.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (948, 2, 'Kulkuväylä, kalusteet, pollari, väri', NULL, 'Väri- ja tummuuskontrasti varoittaa törmäysvaarasta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (176, 2, 'Kulkuväylä, opastava, pääovi', NULL, 'Kulkuväylän, joka ohjaa pääovelle, on oltava esteetön ja helposti havaittava.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (224, 2, 'Kulkuväylä, reunakiven korkeus', 'Ajoväylän ja kulkuväylän erottavan reunakiven korkeus (C).', 'Arvo C on välillä 30 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (914, 2, 'Kulkuväylä, ulkona, pituuskaltevuus', 'Jalankulkutien suositeltava pituuskaltevuus (C). Pitkillä etäisyyksillä  2. lähde suosittelee pituuskaltevuudeksi enintään 2,5 %.', 'Arvo C on enintään 5 %.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (197, 2, 'Kulkuväylä, ulkona, sivukaltevuus', 'Kevytliikenteen väylän sivukaltevuus (C). Suurempi kaltevuus aiheuttaa pyörätuolia ohjattaessa jatkuvia korjausliikkeitä.', 'Arvo C on enintään 2 %.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1041, 2, 'Valaistus, kulkuväylä ulkona', NULL, 'Yleisvalaistusvoimakkuus kulkuväylällä ulkona on 5-30 lx.  Risteyksien, portaiden ja luiskien kohdalla valaitusvoimakkuus on 20-50 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (232, 3, 'Kulkuväylä, kalusteet, sijoitus', NULL, 'Tontilla olevat kalusteet, kuten valaisinpylväät, opasteet, roskalaatikot, pyörätelineet ja penkit on sijoitettava kulkuväylän ulkopuolelle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (172, 3, 'Kulkuväylä, koordinaatisto', NULL, 'Kulkuväylien on oltava selkeässä koordinaatistossa ja yhtenäisiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (213, 3, 'Kulkuväylä, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton. Ulkona esim. asfaltti, betoni, kivituhka ja laatta.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1208, 3, 'Kulkuväylä, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (215, 3, 'Kulkuväylä, pinta, korkeusero', 'Kohoumien ja kuoppien korkeusero (C) pinnasta.', 'Arvo C on enintään 5 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (216, 3, 'Kulkuväylä, pinta, kuvio', NULL, 'Materiaalikontrasti ja kuviot auttaa suunnistautumista. Tummat kuviot vaalealla yksivärisellä pohjalla ovat selkeimpiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (217, 3, 'Kulkuväylä, pinta, kuvio, opastava', NULL, 'Opastava kuvio esim. pitkittäinen pyöristetty kohokuvio, jonka korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (214, 3, 'Kulkuväylä, pinta, saumojen leveys', 'Sauman leveys (C).', 'Arvo C on enintään 5 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (895, 3, 'Kulkuväylä, pinta, väri', NULL, 'Kulkuväylän tai yksityiskohdan (esim. jalkalistan) väri- ja tummuuskontrasti auttaa suunnistautumista ja varoittaa vaarasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (171, 3, 'Kulkuväylä, pituus', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (915, 3, 'Kulkuväylä, pituuskaltevuus lepotasoilla ja kulkuväylän kääntyessä', 'Kaltevuusprosentti (C).Käännöskohdissa tasanne on vaakasuorassa ( 2. lähde).', 'Arvo C on enintään 2 %.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (177, 3, 'Kulkuväylä, päättyminen', NULL, 'Kulkureitti ei saa johtaa umpikujaan.', '3,7,11,14,15,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (219, 3, 'Kulkuväylä, sadevesikaivo, kansi', NULL, 'Kaivon kansi, jossa on pitkänomaiset raot (esim.10x20 mm), tulisi asettaa niin, että raon pidempi sivu (20mm) on kohtisuorasti liikenteen pääkulkusuuuntaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (221, 3, 'Kulkuväylä, sadevesikaivo, reunan korkeus', 'Kannen reunan korkeusero (C) ympäröivän pinnan kanssa.', 'Arvo C on enintään 5 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (238, 3, 'Kulkuväylä, ulkona', NULL, 'Kulkuväylä on pidettävä puhtaana; vesilammikot ja lumi eivät saa haitata liikkumista. Irtohiekka kovalla pinnalla aiheuttaa liukastumisriskin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (940, 3, 'Liikkumisen tilantarve, automaattiovi aukeaa kohti, ulkopyörätuoli', 'Tilantarve ulko-oven oven edessä  (A), silloin kun tasanteen syvyys on mitta (B). Vieressä alas johtava porras tai luiska. Jos oven edessä on alasjohtava porras, tarvitaan tilaa enemmän kuin 2000 mm.Oviaukon vapaa leveys 900 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 2700 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (192, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, kulman avartaminen', NULL, 'Kulkuväylän (leveys  900 mm) kääntymiskulmaa avarretaan niin, että kulman mitat ovat 1150 x 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (251, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, leveys', 'Kulkuväylän leveys (A) ja (B) kulkuväylän kääntyessä.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1290, 3, 'Liikkumisen tilantarve, portti aukeaa kohti', 'Vapaa tila portin edessä (A) kun vieressä on tilaa (B). Vapaa tila (C), jos ovea vastapäätä on alaspäin johtava porras tai luiska.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm. Arvo C on vähintään 2000 mm.', '15,14,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1285, 3, 'Liikkumisen tilantarve, portti aukeaa poispäin', 'Vapaa tila (A) portin edessä. Pyörätuolin käyttäjä pystyy avaamaan portin, jos vieressä on vapaa tila (B).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 450 mm.', '14,15,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (718, 3, 'Opaste, korkeus lattiasta', 'Opasteen sijoituskorkeus (C), myös koho- ja syvennyskuvio-opasteet.', 'Arvo C on välillä 1400 ... 1600 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (717, 3, 'Opaste, materiaali', NULL, 'Pintamateriaali kiiltämätön ja heijastamaton, lasipintaisessa opasteessa heijastamaton lasi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (728, 3, 'Opaste, sijoitus, ovi', NULL, 'Huonetilan opasteet sijoitetaan oven aukeamispuolelle törmäysvaaran välttämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (720, 3, 'Opaste, teksti', NULL, 'Isoilla kirjaimilla kirjoitetaan alkukirjaimet, lyhyet sanat sekä koho- ja syvennyskirjaimet. Pienillä kirjaimilla kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (719, 3, 'Opaste, teksti, kirjaintyyppi', NULL, 'Suurehko ja selvä, esim. Helvetica Medium.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (721, 3, 'Opaste, teksti, kirjaintyyppi, pienet kirjaimet', NULL, 'Pienellä kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (704, 3, 'Opaste, teksti, korkeus', 'Tekstin korkeus (C) opasteissa, jotka luetaan läheltä.', 'Arvo A on vähintään 15 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (705, 3, 'Opaste, teksti, korkeus, sijainti-tai suuntakilpi', 'Tekstin korkeus (A) sijaintia ja suuntaa osoittavissa opasteissa, jotka luetaan muutaman metrin etäisyydeltä.', 'Arvo C on välillä 25 ... 40 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (722, 3, 'Opaste, väri, kontrasti', NULL, 'Tekstin ja symbolin väri- tai tummuuskontrasti taustaa vasten. Parhaiten erottuu musta teksti valkoisella pohjalla.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (231, 3, 'Pihakalusteet', NULL, 'Kalusteen on oltava tukeva ja paikallaan pysyvä ja siinä ei saa olla teräviä särmiä ja kulmia. Materiaali ei saa aiheuttaa allergiaa.', '3,7,8,20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (819, 3, 'Valaistus, opaste, ulkopuolelta', NULL, 'Opasteen valaisu helpottaa sen löytymistä. Häikäisemätön valo, josta ei muodostu häiritseviä varjoja.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (236, 4, 'Kulkuväylä, kalusteet, kalustevyöhyke', NULL, 'Kalustevyöhyke tulisi erottaa kulkuväylästä poikkeavalla pintamateriaalilla ja/tai -värityksellä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (950, 4, 'Kulkuväylä, kalusteet, pollari, etäisyys toisistaan', 'Pollarit sijoitetaan tasaisin välein (A) hahmottamisen helpottamiseksi ja törmäysvaaran vähentämiseksi.', 'Arvo A on vähintään 900 mm.', '3,7,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (235, 4, 'Kulkuväylä, kalusteet, pollari, korkeus', 'Esteen korkeus (A). Este ulottuu mieluummin vyötärön kuin polven korkeudelle.', 'Arvo A on vähintään 1000 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (894, 4, 'Kulkuväylä, rakenteiden alareunan korkeus', NULL, 'Kulkuväylälle rajoittuvan rakenteen esim. ulokkeen alareuna ei saa olla liian korkealla, jotta näkövammainen pystyisi sen havaitsemaan. Korkeus maasta tai lattiasta noin 300 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (222, 4, 'Kulkuväylä, reuna', NULL, 'Suunnistautumista helpottaa kulkuväylän rajoittuminen selvästi poikkeavan tuntuiseen pintaan.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (223, 4, 'Kulkuväylä, reuna, kaide', NULL, 'Kulkuväylän reunalla kulkuesteaitana voi olla esim. avokaide, jossa on vaakajohteet sekä 900 mm:n (käsijohde) että 100 mm:n korkeudella.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1212, 4, 'Kulkuväylä, reuna, korkeus', NULL, 'Turvallisessa kulkuväylässä on korotetun reunuksen lisäksi kaide tai istutuksia. Tai reunus on riittävän korkea (noin 600 mm), ettei siitä aiheudu kompastumisvaaraa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (833, 4, 'Liikkumisen tilantarve, kulkuväylä kääntyy, yhteenlaskettu leveys', 'Risteävien kulkuväylien a ja b yhteen laskettu leveys (A). Kulkusuunnan kääntyessä 90 astetta, a:n ja b:n leveys vähintään 900 mm.', 'Arvo A on vähintään 2300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (190, 4, 'Liikkumisen tilantarve, pyörätuolien kohdatessa', 'Kulkuväylän leveyden (A) vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (907, 4, 'Liikkumisen tilantarve, ulkosähköpyörätuolien kohdatessa', 'Tilantarve esitetty kulkuväylän leveyden (A) avulla.', 'Arvo A on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (731, 4, 'Opaste, sijoitus, kartat', NULL, 'Rakennuksessa suunnistautumista opastavat kohokartat sijoitetaan hyvin näkyville, esimerkiksi ulko-oven viereen.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (725, 4, 'Opaste, teksti, kohokirjain, korkeus', 'Kirjaimen korkeus pinnasta (A) ja kirjaimen korkeus (C). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo A on vähintään 1 mm. Arvo C on välillä 15 ... 40 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (716, 4, 'Opaste, teksti, korkeus, nimikilpi', 'Tekstin korkeus (C) nimikilvissä, jotka luetaan 1..3 m etäisyydeltä.', 'Arvo C on välillä 70 ... 100 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (726, 4, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', NULL, 'Pyörätuolin käyttäjän pistekirjoitusopasteen korkeus lattiasta on 1100 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (834, 4, 'Valaistus, kulkuväylä, pinta, häikäisy', NULL, 'Kulkuväylän pinta ei saa heijastaa keino- eikä päivänvaloa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (228, 4, 'Valaistus, kulkuväylä, sijoitus', NULL, 'Kulkuväylän hahmottamista helpottaa samansuuntainen yhtenäinen valaisinlinja.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (818, 4, 'Valaistus, opaste, sisäpuolelta', NULL, 'Sisältäpäin valaistussa opasteessa vaalea teksti tummalla pohjalla häikäisyn estämiseksi.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1089, 5, 'Liikkumisen tilantarve, opaskoiran kanssa', 'Opaskoiraa käyttävän henkilön tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 1100 mm.', '3');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (724, 5, 'Opaste, kieli', NULL, 'Opasteisiin soveltuu selkokieli, jota ymmärtävät kaikki. Erityisesti sitä tarvitsevat kehitys- ja monivammaiset sekä esim. viittomakieltä äidinkielenään käyttävät.', '1,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (729, 5, 'Opaste, merkinantolaite', NULL, 'Äänimerkkien lisäksi esim. ovipuhelimiin ja merkinantolaitteisiin suositellaan myös visuaalisia ja taktuaalisia eli tuntoaistiin perustuvia merkkejä.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1179, 5, 'Opaste, teksti, kohokirjain, poikkileikkaus', NULL, 'Kohokirjaimen poikkileikkaus on kolmioimainen, jonka yläsärmää pitkin sormea voi liuuttaa. Yläsärmä ei saa olla terävä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1043, 5, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', 'Opasteen korkeus (C) lattiasta.', 'Arvo C on välillä 1300 ... 1400 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1180, 5, 'Opaste, teksti, syvennyskirjain, korkeus', NULL, 'Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta. Kirjaimen syvyys tai korkeus pinnasta 1 mm ja kirjaimen korkeus 30-60 mm. Kirjaimen korkeus 15-40 mm (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1181, 5, 'Opaste, teksti, syvennyskirjain, leveys', 'Kirjaimen leveys eli syvennysuran leveys (A). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo C on välillä 6 ... 10 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1210, 1, 'Kulkuväylä, sisällä, julkinen tila', NULL, 'Rakennuksessa toisiinsa toiminnallisesti yhteydessä olevien tasojen ja tasanteiden välillä tulee olla pyörätuolin käyttäjälle soveltuva sisäinen kulkuväylä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (287, 1, 'Ovi, oviaukko, korkeus', 'Pääsääntöinen oviaukon korkeus (A). Vertailuarvo (B) paikoittain ks. RakMK F2 ohje.', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 1950 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (286, 1, 'Ovi, oviaukko, vapaa leveys, yhteistilat', 'Pyörätuolin käyttäjille soveltuvien sisäänkäyntien, tuulikaappien, käytävillä sijaitsevien ovien ja aukkojen sekä pyörätuolin käyttäjille soveltuvien hygieniatilojen ovien vapaa leveys (A).', 'Arvo A on vähintään 850 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (605, 1, 'Yhteistilat, mitoitus, julkinen tila', NULL, 'Yhteistilojen on sovelluttava kaikille käyttäjäryhmille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (288, 1, 'Yhteistilat, ovi, kynnyskorkeus', NULL, 'Mikäli kynnykset ovat välttämättömiä, ne saavat olla enintään 20 mm korkeita.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (754, 2, 'Ikkuna, avautuminen', NULL, 'Ikkunan tai osan siitä tulee olla avattavissa. Ikkunan avautumissuunta sisäänpäin pesemisen ja kunnostamisen helpottamiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (826, 2, 'Ikkuna, häikäisy', NULL, 'Ikkuna ei saa aiheuttaa vastavalohäikäisyä. Suuria valaistuseroja vältetään lisäämällä valaistusta huoneen sisäosissa ja estämällä suora auringon valon häikäisy esim. sälekaihtimin.', '1,3,7,8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (685, 2, 'Lattia, pinta, materiaali', NULL, 'Kestävä, helposti puhdistettava ja märkänäkin luistamaton pintamateriaali.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1045, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 300 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1192, 2, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Tilantarve (A) oven edessä silloin kun oven vieressä ei ole vapaata tilaa.', 'Arvo A on vähintään 1200 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (278, 2, 'Ovi, avaamisvoima', 'Avaamisvoima (C). Ovityypin valinnassa otetaan huomioon oven helppo ja kevyt avattavuus.', 'Arvo C on enintään 10 newton.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1040, 2, 'Painike/vedin, korkeus lattiasta', 'Painikkeen/vetimen korkeus lattiasta (C).', 'Arvo C on välillä 900 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (733, 2, 'Painike/vedin, ulottuminen', NULL, 'Käyttäjän on ulotuttava painikkeeseen tai vetimeen kurkottamatta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (698, 2, 'Seinä ja katto, pinta', NULL, 'Mattapinta, joka ei kiillä eikä heijasta valoa häiritsevästi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (816, 2, 'Valaistus, yhteistilat', 'Yhteistilojen, jätetilojen ja varastojen yleisvalaistusvaatimus.', 'Valaistusvoimakkuus varastotiloissa on 150 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (750, 3, 'Ikkuna', NULL, 'Suurikokoisia tai leveitä tai yläsaranoituja ikkunoita on vältettävä, koska ne ovat hankala avata ja puhdistaa.', '8,11,14,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (753, 3, 'Ikkuna, avauspainike, etäisyys sivuseinästä', 'Painikkeen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan avaamaan ikkunaa.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (735, 3, 'Kytkin/säädin, etäisyys sivuseinästä', 'Kytkimen tai säätimen etäisyys (A) sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1122, 3, 'Kytkin/säädin, korkeus lattiasta', 'Kytkimen tai säätimen asennuskorkeus (C) lattiasta.', 'Arvo C on välillä 850 ... 1100 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (757, 3, 'Kytkin/säädin, muotoilu', NULL, 'Suurikokoinen keinukytkin on helppo ja kevyt käyttää.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (839, 3, 'Kytkin/säädin, valopainike, sijoitus', NULL, 'Valopainike sijoitetaan kulkureitin varrelle helposti havaittavaan ja ulottuvissa olevaan paikkaan.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (567, 3, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen ja säätimen on oltava helppokäyttöinen ja helposti ulottuvilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (686, 3, 'Lattia, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (695, 3, 'Lattia, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (693, 3, 'Lattia, pinta, kuvio', NULL, 'Lattiassa suuret, voimakasväriset kuviot haittaavat suunnistautumista ja tekevät yleisvaikutelman heikkonäköiselle sekavaksi. Heikkonäköinen voi erehtyä pitämään lattian tummaa kohtaa aukkona tai tasoerona lattiassa.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (692, 3, 'Lattia, pinta, kuvio, opastava', NULL, 'Kulkusuunnan suuntainen kuviointi helpottaa suunnistautumista.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (687, 3, 'Lattia, pinta, materiaali', NULL, 'Pölyä keräämätön, allergiaa aiheuttamaton ja helposti puhtaana pidettävä materiaali.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (694, 3, 'Lattia, pinta, väri', NULL, 'Lattia voi olla 10...20% tummempi kuin seinät tai katto, koska vaakasuuntainen pinta saa yleensä enemmän valoa kuin pystypinta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1191, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Rollattorin tilantarve oven edessä (A), silloin kun oven vieressä ei ole vapaata tilaa. Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (283, 3, 'Ovi, aukipitolaite', NULL, 'Sijoitetaan siten, että se ei aiheuta kompastumis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (285, 3, 'Ovi, aukipitolaite, ovipumppu', NULL, 'Oven hitaasti sulkeva ovipumppu helpottaa apuvälineen kanssa liikkuvaa kulkemaan ovesta.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (284, 3, 'Ovi, aukipitolaite, ulottuminen', NULL, 'Aukipitolaitteeseen on ulotuttava helposti kurkottamatta ja kumartumatta. Esim. maahan kiinnitetty tappi on huono ratkaisu liikuntaesteisille, lisäksi siitä aiheutuu kompastumisvaara.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (852, 3, 'Ovi, aukipysymisaika', 'Aukipysymisaikaa (A) säätäessä otetaan huomioon hitaasti liikkuvat henkilöt. Mikäli ovi avataan painonappia tai painiketta käyttäen automaattisesti, riippuu tarvittava aika painikkeen ja oven välisestä etäisyydestä.', 'Arvo A on vähintään 25 s.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (977, 3, 'Ovi, avaamis- ja säätölaitteet', NULL, 'Avaamismekanismin on oltava helposti käytettävä ja ymmärrettävä. Automaattiovien tulee toimia kummaltakin puolelta samalla tavalla.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1095, 3, 'Ovi, avautuminen', NULL, 'Ovet avautuvat pääkulkusuuntaan ja ovat auki ollessaan 90º  tai 180º  kulmassa seinää vasten törmäysvaaran vähentämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (279, 3, 'Ovi, havaittavuus', NULL, 'Väri- ja materiaalikontrasti esim. ovilevyssä tai -listoissa taustaa vasten. Kohdevalaistus lisää havaittavuutta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (960, 3, 'Ovi, kynnys, väri', NULL, 'Värikontrasti lattiapinnan kanssa auttaa kynnyksen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1231, 3, 'Ovi, oviaukko, vapaa leveys, pyörätuoli', 'Mitta (A) on riittävä silloin kun ovesta voi ajaa suoraan ja kun oven jälkeen vapaata tilaa. Jos oven jälkeen joudutaan kääntymään, oven on oltava leveämpi.', 'Arvo A on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (280, 3, 'Ovi, potkulevyn korkeus', NULL, 'Oven alareuna suojataan 300 mm korkealla potkulevyllä, jotta esim. pyörätuoli ei kolhi ovea.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (737, 3, 'Painike/vedin, käytettävyys', NULL, 'Painikkeen tai vetimen tulee olla käytettävissä yhdellä kädellä.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (741, 3, 'Painike/vedin, lankavedin', 'Lankavetimen vapaa leveys (A). Vapaa sormitila (B), silloin kun vedintä käytetään sormilla. Vapaa tila (C), silloin kun vedintä käytetään ranteella.', 'Arvo A on vähintään 140 mm. Arvo B on vähintään 35 mm. Arvo C on vähintään 60 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1007, 3, 'Painike/vedin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa painikkeen tai vetimen havaitsemista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (700, 3, 'Seinä ja katto, väri', NULL, 'Vaaleat sävyt suositeltavia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (673, 3, 'Yhteistilat, sijoitus', NULL, 'Yhteistiloihin tulee olla esteetön pääsy. Sisäänkäynti suoraan maan tasolta ilman portaita. Mikäli yhteistilat sijaitsevat kellarissa tai ullakolla, pääsy tiloihin järjestetään hissin avulla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1152, 3, 'Yhteistilat, varasto, liikkumisvälineet', NULL, 'Mitoituksessa on huomioitava asukkaiden ikärakenne; mitä enemmän talossa asuu ikäihmisiä, sitä enemmän tarvitaan varastotilaa apuvälineille.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (645, 3, 'Yhteistilat, varasto, sijoitus', NULL, 'Sijoitetaan sisäänkäynnin lähelle. Kulkuväylä lyhyt ja helppokulkuinen. Varastoon johtavalla kulkuväylällä ei saa olla portaita, korkeita kynnyksiä tai muita esteitä.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (738, 4, 'Kytkin/säädin, käytettävyys, voima', NULL, 'Kytkimen tai säätimen on oltava kevyt, mutta se ei saa mennä vahingossa päälle.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (983, 4, 'Laitteet, pistorasia', NULL, 'Riittävästi pistorasioita apuvälineille ja -laitteille eri tiloissa.', '1,3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1111, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, pyörätuoli avustettu', 'Käytävän leveys (A).', 'Arvo A on vähintään 1200 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1081, 4, 'Liikkumisen tilantarve, kääntyminen 90 astetta oviaukkoon, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1071, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1400 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 450 mm, mutta arvoksi suositellaan vähintään 600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1078, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Suositusmitta (A) mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1300 mm, mutta arvoksi suositellaan vähintään 1500 mm. Arvo B on vähintään 1100 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1010, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, asunto', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1400 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (453, 4, 'Liikkumisen tilantarve, pyörätuoliin siirtyminen, avustaja', 'Tilantarve esitetty leveyden (A)  ja syvyyden (B) avulla. Kulkuväylän leveys 1500 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 1400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (206, 4, 'Ovi, oviaukko, syvyys', 'Seinän ja ovikarmin välinen mitta (C) oviaukossa, jolloin ulottuu avaamaan oven.', 'Arvo C on enintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1110, 4, 'Ovi, oviaukko, vapaa leveys, ulkopyörätuoli', 'Oven vapaa leveys (A).', 'Arvo A on vähintään 850 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (362, 4, 'Ovi, vedin, lisävedin', NULL, 'Jotta oven voi vetää kiinni perässään, asennetaan oven saranapuolella vaakasuuntainen vähintään 300 mm levyinen lankavedin. Etäisyys saranasta on 200 mm ja asennuskorkeus on 800 mm lattiasta.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1091, 4, 'Painike/vedin, ovi, etäisyys sivuseinästä', 'Painikkeen tai vetimen etäisyys sivuseinästä, koska pyörätuolilla ei pääse nurkkaan.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (760, 4, 'Pistorasia, etäisyys sivuseinästä', 'Etäisyys (A) sivuseinästä.', 'Arvo A on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1096, 4, 'Pistorasia, korkeus lattiasta', 'Korkeus (C) lattiasta.', 'Arvo C on välillä 400 ... 1100 mm.', '11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (761, 4, 'Pistorasia, sijoitus', NULL, 'Pistorasioita sijoitetaan huoneeseen oven molemmille puolille, jotta laitteiden johdot eivät aiheuttaisi kompastumisvaaraa kulkuväylällä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (657, 4, 'Yhteistilat, pyörätuolin pesutila, kerrostalo', NULL, 'Käynti pesutilaan, joka sijoitetaan pyörätuolin säilytystilan yhteyteen, suoraan ulkoa. Laitoskäyttöön tarkoitettu pyörien harjauslaite voi tulla kyseeseen, jos on useita pyörätuolin käyttäjiä.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (658, 4, 'Yhteistilat, pyörätuolin pesutila, lattiakaivo', NULL, 'Varustettu hiekanerottimella tai vastaavalla laitteella.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1137, 4, 'Yhteistilat, pyörätuolin pesutila, mitoitus', 'Pesutilan tilantarve esitetty syvyyden (A) ja leveyden (B) avulla.', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1139, 4, 'Yhteistilat, pyörätuolivarasto, ilmanvaihto', NULL, 'Hyvä tuuletus akkujen lataamisessa syntyvän vetykaasun poistamiseksi.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (654, 4, 'Yhteistilat, pyörätuolivarasto, kulkuväylä', 'Pyörätuolivarastoon johtavan kulkuväylän leveys (A).', 'Arvo A on vähintään 1600 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (656, 4, 'Yhteistilat, pyörätuolivarasto, lämpötila', 'Lämpötila (C).', 'Arvo C on välillä 10 ... 25 astetta C.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (652, 4, 'Yhteistilat, pyörätuolivarasto, mitoitus', 'Pyörätuolin säilytystilan tilantarve esitetty leveyden (A) ja syvyyden (B) avulla. Tilaan  mahtuu kaksi pyörätuolia vierekkäin, jolloin voidaan siirtyä sisällä käytettävään pyörätuoliin.', 'Arvo A on vähintään 1800 mm. Arvo B on vähintään 1400 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (653, 4, 'Yhteistilat, pyörätuolivarasto, mitoitus, avustaja', 'Pyörätuolin säilytystilan tilantarve esitetty leveyden (A) ja syvyyden (B) avulla. Tilaan  mahtuu kaksi pyörätuolia vierekkäin, jolloin voidaan siirtyä sisällä käytettävään pyörätuoliin.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 1400 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (655, 4, 'Yhteistilat, pyörätuolivarasto, pistorasia', NULL, 'Säilytystilan varusteina on maadoitettu pistorasia sähköpyörätuolin akkujen lataamista varten.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (649, 4, 'Yhteistilat, varasto, liikkumisvälineet', NULL, 'Lämmitetyssä lastenvaunu- tai ulkoiluvälinevarastossa voidaan säilyttää myös sähkökäyttöisiä pyörätuoleja ja vanhusten kävelytukia. Esteetön pääsy ja sijainti sisäänkäynnin lähellä.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (651, 4, 'Yhteistilat, varasto, liikkumisvälineet, ilmanvaihto', NULL, 'Säilytystilassa, jossa ladataan sähköpyörätuolien akut, on oltava riittävä ilmanvaihto.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (650, 4, 'Yhteistilat, varasto, liikkumisvälineet, mitoitus, pinta-ala', 'Pinta-ala (A).', 'Arvo A on vähintään 10 m2.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (646, 4, 'Yhteistilat, varasto, mitoitus', NULL, 'Varaston ja sen kulkuväylien on oltava tavanomaista tilavampia, jotta siellä pystyy liikkumaan pyörätuolilla.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1177, 5, 'Kytkin/säädin, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa kytkimen tai säätimen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1090, 5, 'Laitteet, kytkin/säädin', NULL, 'Kytkimen asento helposti havaittavissa, ei portaatonta säätöä. Lisävarusteita esim. äänimerkki, pistekirjoituspaneeli, vahvistava lisäkello tai valomerkki.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (386, 1, 'Käsijohde, luiskat ja portaat, julkinen tila', NULL, 'Luiskat ja portaat varustetaan molemmin puolin yhtenäisillä käsijohteilla. Leveissä portaissa suositellaan portaan keskellä käsijohdetta 2400 mm:n välein.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1222, 1, 'Käsijohde, pituus, julkinen tila', 'Käsijohde ulotetaan luiskien ja portaiden alkamis- ja päättymiskohtien ohi. Käsijohteen ulokkeen pituus (A).', 'Arvo A on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1213, 1, 'Luiska, pituus', 'Luiska, jonka kaltevuus on 5 - 8 %, saa olla pituudeltaan (C) yhtäjaksoisena enintään kuusi metriä, jonka jälkeen tulee olla välitasanne.', 'Arvo C on enintään 6000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1217, 1, 'Luiska, pituuskaltevuus', NULL, 'Portaan kaltevuuden mukainen, yli yhden askelman pituinen ns. lastenvaunuluiska ei ole turvallinen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (275, 1, 'Luiska, pituuskaltevuus, ei välitasannetta', 'Ilman välitasanteita olevan luiskan kaltevuus (C). Vaikka luiska olisi loiva (5%), suositellaan välitasanteita korkeuseroille 480 mm, 960 mm jne.', 'Arvo C on enintään 5 %.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (276, 1, 'Luiska, pituuskaltevuus, välitasanne', 'Luiska saa olla kaltevuudeltaan enintään 8 % (1:12,5) ja pituudeltaan yhtäjaksoisena enintään kuusi metriä, jonka jälkeen kulkuväylällä tulee olla vaakasuora vähintään 2000 mm:n pituinen välitasanne.', '', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (319, 1, 'Luiska, välitasanne, pituus', 'Luiska saa olla kaltevuudeltaan enintään 8 % (1:12,5) ja pituudeltaan yhtäjaksoisena enintään kuusi metriä. Sen jälkeen kulkuväylällä tulee olla vaakasuora vähintään 2000 mm:n pituinen välitasanne (A).', 'Arvo A on vähintään 2000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1223, 1, 'Luiska/portaat, käsijohde', NULL, 'Käsijohde tai kädensija asennetaan kaikkiin portaisiin ja luiskiin, julkisissa tiloissa molemmin puolin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1224, 1, 'Luiska/portaat,käsijohde, pituus', NULL, 'Portaassa ja luiskassa käsijohde on asennettava koko pituudelle. Johteiden tulee jatkua yhtenäisinä myös välitasanteiden osuudella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1087, 2, 'Käsijohde, luiskat ja portaat, korkeus lattiasta, julkinen tila', NULL, 'Kahden päällekkäisen käsijohteen sopivat korkeudet ovat noin 900 ja 700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (954, 2, 'Luiska, sijoitus', NULL, 'Luiska johtaa aina tasanteelta tasanteelle. Se ei saa lähteä esim. suoraan ovelta ilman tasannetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (274, 2, 'Luiska, sivukaltevuus', NULL, 'Luiskassa ei saa olla sivukaltevuutta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1218, 2, 'Luiska, tasoero', 'Luiskaa ei suositella ainoaksi kulkuväyläksi yli metrin tasoerolle (C).', 'Arvo C on enintään 1000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1214, 2, 'Luiska, välitasanne', NULL, 'Luiskan tulee olla suora kulkusuunnassa, käännöskohdissa tulee olla välitasanne.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1215, 2, 'Luiska, ylätasanne', 'Ylätasanteen syvyys (A), jotta ovi mahtuu aukeamaan ja tasanteella mahtuu kääntymään.', 'Arvo A on vähintään 1800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1027, 2, 'Painike/vedin, ovi, etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys (A) alasjohtavasta portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1238, 2, 'Sisäänkäynti, tasanne, luiska', NULL, 'Ovi ei saa avautua luiskaa käyttävän henkilön tielle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (958, 2, 'Sisäänkäynti, tasoero, luiska', 'Tasoero (C) maanpinnan ja sisäänkäynnin lattian välillä.', 'Arvo C on enintään 500 mm.', '8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1041, 2, 'Valaistus, kulkuväylä ulkona', NULL, 'Yleisvalaistusvoimakkuus kulkuväylällä ulkona on 5-30 lx.  Risteyksien, portaiden ja luiskien kohdalla valaitusvoimakkuus on 20-50 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (213, 3, 'Kulkuväylä, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton. Ulkona esim. asfaltti, betoni, kivituhka ja laatta.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1208, 3, 'Kulkuväylä, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (215, 3, 'Kulkuväylä, pinta, korkeusero', 'Kohoumien ja kuoppien korkeusero (C) pinnasta.', 'Arvo C on enintään 5 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (216, 3, 'Kulkuväylä, pinta, kuvio', NULL, 'Materiaalikontrasti ja kuviot auttaa suunnistautumista. Tummat kuviot vaalealla yksivärisellä pohjalla ovat selkeimpiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (217, 3, 'Kulkuväylä, pinta, kuvio, opastava', NULL, 'Opastava kuvio esim. pitkittäinen pyöristetty kohokuvio, jonka korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (214, 3, 'Kulkuväylä, pinta, saumojen leveys', 'Sauman leveys (C).', 'Arvo C on enintään 5 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (895, 3, 'Kulkuväylä, pinta, väri', NULL, 'Kulkuväylän tai yksityiskohdan (esim. jalkalistan) väri- ja tummuuskontrasti auttaa suunnistautumista ja varoittaa vaarasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (915, 3, 'Kulkuväylä, pituuskaltevuus lepotasoilla ja kulkuväylän kääntyessä', 'Kaltevuusprosentti (C).Käännöskohdissa tasanne on vaakasuorassa ( 2. lähde).', 'Arvo C on enintään 2 %.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (736, 3, 'Kytkin/säädin, etäisyys alasjohtavasta portaasta tai luiskasta', 'Kytkimen tai säätimene etäisyys alasjohtavasta portaasta tai luiskasta (A).', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (192, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, kulman avartaminen', NULL, 'Kulkuväylän (leveys  900 mm) kääntymiskulmaa avarretaan niin, että kulman mitat ovat 1150 x 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (251, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, leveys', 'Kulkuväylän leveys (A) ja (B) kulkuväylän kääntyessä.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (312, 3, 'Luiska, leveys', 'Luiskan leveys (A) riippuu sijainnista ja käyttötarkoituksesta. Suositusmitta on kaksikaistaisen poistumistien leveys.', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (317, 3, 'Luiska, reuna, korkeus', 'Luiskassa tulee olla 50 mm:n suojareunus silloin, kun luiskan reuna ei ole maan tasossa tai se ei rajoitu seinään.', '', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (953, 3, 'Luiska, ulkona', NULL, 'Ulkotiloissa luiska pidetään puhtaana ja kuivana kattamalla tai lämmittämällä. Jäinen tai märkä luiska on liukas ja vaarallinen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1094, 3, 'Luiska, välitasanne, kulkuväylä kääntyy 90 astetta', 'Välitasanteen kääntymiskulmaa avarretaan niin, että kulman mitat ovat A x B. Suositusmitat 2. lähteestä.', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1150 mm. Arvo B on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (956, 3, 'Valaistus, luiska, portaat, kohdevalaistus', 'Luiskan ja portaiden alun kohdevalaistus auttaa luiskan ja portaan havaitsemista.', 'Valaistusvoimakkuus portaissa ja luiskissa 20 - 50 lx. Kriittisissä kohteissa, kuten tasoerojen yhteydessä, valaistusvoimakkuus 400 - 500 lx (2. lähde).', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (833, 4, 'Liikkumisen tilantarve, kulkuväylä kääntyy, yhteenlaskettu leveys', 'Risteävien kulkuväylien a ja b yhteen laskettu leveys (A). Kulkusuunnan kääntyessä 90 astetta, a:n ja b:n leveys vähintään 900 mm.', 'Arvo A on vähintään 2300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (834, 4, 'Valaistus, kulkuväylä, pinta, häikäisy', NULL, 'Kulkuväylän pinta ei saa heijastaa keino- eikä päivänvaloa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (228, 4, 'Valaistus, kulkuväylä, sijoitus', NULL, 'Kulkuväylän hahmottamista helpottaa samansuuntainen yhtenäinen valaisinlinja.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (438, 1, 'WC-/peseytymistila, opaste, julkinen tila', NULL, 'Julkisissa tiloissa liikkumisesteisen WC merkitään ISA-opasteella ja kuvalla, josta selviää kummalla puolella wc-istuinta on vapaa tila pyörätuolia varten.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (272, 2, 'Autopaikka, opaste', NULL, 'Liikkumisesteisten autopaikat pitää merkitä kansainvälisellä ISA-tunnuksella.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (176, 2, 'Kulkuväylä, opastava, pääovi', NULL, 'Kulkuväylän, joka ohjaa pääovelle, on oltava esteetön ja helposti havaittava.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (217, 3, 'Kulkuväylä, pinta, kuvio, opastava', NULL, 'Opastava kuvio esim. pitkittäinen pyöristetty kohokuvio, jonka korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (718, 3, 'Opaste, korkeus lattiasta', 'Opasteen sijoituskorkeus (C), myös koho- ja syvennyskuvio-opasteet.', 'Arvo C on välillä 1400 ... 1600 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (717, 3, 'Opaste, materiaali', NULL, 'Pintamateriaali kiiltämätön ja heijastamaton, lasipintaisessa opasteessa heijastamaton lasi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (728, 3, 'Opaste, sijoitus, ovi', NULL, 'Huonetilan opasteet sijoitetaan oven aukeamispuolelle törmäysvaaran välttämiseksi.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (720, 3, 'Opaste, teksti', NULL, 'Isoilla kirjaimilla kirjoitetaan alkukirjaimet, lyhyet sanat sekä koho- ja syvennyskirjaimet. Pienillä kirjaimilla kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (719, 3, 'Opaste, teksti, kirjaintyyppi', NULL, 'Suurehko ja selvä, esim. Helvetica Medium.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (721, 3, 'Opaste, teksti, kirjaintyyppi, pienet kirjaimet', NULL, 'Pienellä kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (704, 3, 'Opaste, teksti, korkeus', 'Tekstin korkeus (C) opasteissa, jotka luetaan läheltä.', 'Arvo A on vähintään 15 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (705, 3, 'Opaste, teksti, korkeus, sijainti-tai suuntakilpi', 'Tekstin korkeus (A) sijaintia ja suuntaa osoittavissa opasteissa, jotka luetaan muutaman metrin etäisyydeltä.', 'Arvo C on välillä 25 ... 40 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (722, 3, 'Opaste, väri, kontrasti', NULL, 'Tekstin ja symbolin väri- tai tummuuskontrasti taustaa vasten. Parhaiten erottuu musta teksti valkoisella pohjalla.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (819, 3, 'Valaistus, opaste, ulkopuolelta', NULL, 'Opasteen valaisu helpottaa sen löytymistä. Häikäisemätön valo, josta ei muodostu häiritseviä varjoja.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (731, 4, 'Opaste, sijoitus, kartat', NULL, 'Rakennuksessa suunnistautumista opastavat kohokartat sijoitetaan hyvin näkyville, esimerkiksi ulko-oven viereen.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (725, 4, 'Opaste, teksti, kohokirjain, korkeus', 'Kirjaimen korkeus pinnasta (A) ja kirjaimen korkeus (C). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo A on vähintään 1 mm. Arvo C on välillä 15 ... 40 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (716, 4, 'Opaste, teksti, korkeus, nimikilpi', 'Tekstin korkeus (C) nimikilvissä, jotka luetaan 1..3 m etäisyydeltä.', 'Arvo C on välillä 70 ... 100 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (726, 4, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', NULL, 'Pyörätuolin käyttäjän pistekirjoitusopasteen korkeus lattiasta on 1100 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (818, 4, 'Valaistus, opaste, sisäpuolelta', NULL, 'Sisältäpäin valaistussa opasteessa vaalea teksti tummalla pohjalla häikäisyn estämiseksi.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (724, 5, 'Opaste, kieli', NULL, 'Opasteisiin soveltuu selkokieli, jota ymmärtävät kaikki. Erityisesti sitä tarvitsevat kehitys- ja monivammaiset sekä esim. viittomakieltä äidinkielenään käyttävät.', '1,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (729, 5, 'Opaste, merkinantolaite', NULL, 'Äänimerkkien lisäksi esim. ovipuhelimiin ja merkinantolaitteisiin suositellaan myös visuaalisia ja taktuaalisia eli tuntoaistiin perustuvia merkkejä.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (730, 5, 'Opaste, rakennuksen opastusjärjestelmä', NULL, 'Hälytysäänen ohella myös visuaalinen viesti ja synteettinen puheviesti.', '1,3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1179, 5, 'Opaste, teksti, kohokirjain, poikkileikkaus', NULL, 'Kohokirjaimen poikkileikkaus on kolmioimainen, jonka yläsärmää pitkin sormea voi liuuttaa. Yläsärmä ei saa olla terävä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1043, 5, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', 'Opasteen korkeus (C) lattiasta.', 'Arvo C on välillä 1300 ... 1400 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1180, 5, 'Opaste, teksti, syvennyskirjain, korkeus', NULL, 'Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta. Kirjaimen syvyys tai korkeus pinnasta 1 mm ja kirjaimen korkeus 30-60 mm. Kirjaimen korkeus 15-40 mm (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1181, 5, 'Opaste, teksti, syvennyskirjain, leveys', 'Kirjaimen leveys eli syvennysuran leveys (A). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo C on välillä 6 ... 10 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (393, 1, 'Käsijohde, korkeus lattiasta', NULL, 'Käsijohteen sopiva korkeus on 900 mm. Jos asuintalon portaan kaiteen korkeus on enintään 1000 mm ja jos kaiteen yläpinta soveltuu johteeksi, ei tarvita erillistä käsijohdetta.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (386, 1, 'Käsijohde, luiskat ja portaat, julkinen tila', NULL, 'Luiskat ja portaat varustetaan molemmin puolin yhtenäisillä käsijohteilla. Leveissä portaissa suositellaan portaan keskellä käsijohdetta 2400 mm:n välein.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1222, 1, 'Käsijohde, pituus, julkinen tila', 'Käsijohde ulotetaan luiskien ja portaiden alkamis- ja päättymiskohtien ohi. Käsijohteen ulokkeen pituus (A).', 'Arvo A on vähintään 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1217, 1, 'Luiska, pituuskaltevuus', NULL, 'Portaan kaltevuuden mukainen, yli yhden askelman pituinen ns. lastenvaunuluiska ei ole turvallinen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1223, 1, 'Luiska/portaat, käsijohde', NULL, 'Käsijohde tai kädensija asennetaan kaikkiin portaisiin ja luiskiin, julkisissa tiloissa molemmin puolin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1224, 1, 'Luiska/portaat,käsijohde, pituus', NULL, 'Portaassa ja luiskassa käsijohde on asennettava koko pituudelle. Johteiden tulee jatkua yhtenäisinä myös välitasanteiden osuudella.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1299, 1, 'Portaat, kerrostaso, kate', NULL, 'Kerrostasojen välisen portaan on oltava katettu.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (332, 1, 'Portaat, mitoitus, julkiset sisätilat', 'Auloissa ja muissa sisäisen liikenteen tiloissa toimivan portaan mitoitus etenemän (A) ja nousun (C) avulla.', 'Arvo A on vähintään 300 mm. Arvo C on enintään 160 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (843, 1, 'Portaat, sijoitus', NULL, 'Portaat eivät saa aiheuttaa putoamis- tai törmäysvaaraa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1087, 2, 'Käsijohde, luiskat ja portaat, korkeus lattiasta, julkinen tila', NULL, 'Kahden päällekkäisen käsijohteen sopivat korkeudet ovat noin 900 ja 700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (391, 2, 'Käsijohde, materiaali', NULL, 'Materiaali ei saa aiheuttaa allergiaa. Allergiaa aiheuttavat esim. nikkeli ja kromi.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (388, 2, 'Käsijohde, pyöreä', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Pyöreän käsijohteen suositeltava halkaisija (C).', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (389, 2, 'Käsijohde, suorakaide', 'Käsijohteen etäisyys seinästä  eli vapaa sormitila (A). Suorakaiteen muotoisen käsijohteen suositeltava leveys (C), jotta siitä saa hyvän otteen. Käsijohteen kulmat pitää olla pyöristetyt.', 'Arvo A on vähintään 45 mm. Arvo C on välillä 25 ... 30 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1218, 2, 'Luiska, tasoero', 'Luiskaa ei suositella ainoaksi kulkuväyläksi yli metrin tasoerolle (C).', 'Arvo C on enintään 1000 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (697, 2, 'Pinta, materiaali', NULL, 'Käytettävien materiaalien päästöluokka on oltava M1. Heijastamattomat mattapinnat soveltuvat hyvin seiniin ja kattoihin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (383, 2, 'Portaat, askelmat', NULL, 'Portaissa ei tulisi olla avonaisia tai yksittäisiä askelmia eikä ulkonevia askelmien etureunoja. Portaat, joissa on vähintään kolme nousua, on helpompi havaita.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (330, 2, 'Portaat, mitoitus, askelmat', 'Portaan nousun ja etenemän suhde määrää portaiden helppokulkuisuuden.', 'Mitoituksessa voidaan käyttää seuraavaa kaavaa: 2 * nousu + etenemä = 630 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1129, 2, 'Portaat, mitoitus, paarikuljetus', 'Paarien tilantarve kantajien liikkumavarat mukaanlukien esitettynä leveyden (A) ja pituuden (B) avulla.', 'Arvo A on vähintään 600 mm. Arvo B on vähintään 2400 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (380, 2, 'Portaat, väri', NULL, 'Portaan väri- tai tummuuskontrasti seinän tai lattian kanssa auttaa portaiden erottamista seinä- tai lattiapinnasta. Porrasaskelmien etureunojen merkintä helpottaa askelmien erottamista.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (823, 2, 'Valaistus, häikäisy', NULL, 'Valaisimet eivät saa aiheuttaa suoraa eikä heijastumishäikäisyä. Suositusten mukaisen valomäärän ylittäminen aiheuttaa häikäisyongelmia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1041, 2, 'Valaistus, kulkuväylä ulkona', NULL, 'Yleisvalaistusvoimakkuus kulkuväylällä ulkona on 5-30 lx.  Risteyksien, portaiden ja luiskien kohdalla valaitusvoimakkuus on 20-50 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (213, 3, 'Kulkuväylä, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton. Ulkona esim. asfaltti, betoni, kivituhka ja laatta.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1208, 3, 'Kulkuväylä, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (736, 3, 'Kytkin/säädin, etäisyys alasjohtavasta portaasta tai luiskasta', 'Kytkimen tai säätimene etäisyys alasjohtavasta portaasta tai luiskasta (A).', 'Arvo A on vähintään 700 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (835, 3, 'Käsijohde, materiaali', NULL, 'Käsijohteen on oltava luistamatonta materiaalia, jotta siitä saa pitävän otteen.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (390, 3, 'Käsijohde, suorakaide, ympärysmitta', 'Suorakaiteen muotoisen profiilin kokonaisympärysmitta (C). Pyöristetyn profiilin leveys noin 25 - 30 mm.', 'Arvo C on välillä 120 ... 180 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (392, 3, 'Käsijohde, väri', NULL, 'Väri- tai tummuuskontrasti taustaa vasten auttaa käsijohteen havaitsemista.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (381, 3, 'Portaat, liukueste', NULL, 'Liukuesteet porrasaskelmien etureunoissa ja niiden väriero portaan kanssa lisäävät portaan turvallisuutta.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1234, 3, 'Portaat, mitoitus, helppokulkuinen porras', 'Portaiden mitoitus henkilölle, jolla on jäykät nilkat. Mitoitus esitetty portaan etenemän (A) ja nousun (B) avulla.', 'Arvo A on vähintään 390 mm. Arvo C on enintään 120 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (384, 3, 'Portaat, tyyppi', NULL, 'Kierreporrasta ei saa käyttää.', '3,7,8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1128, 3, 'Portaat, välitasanne', NULL, 'Välitasanteita tulee olla vähintään yksi kerrosta kohti.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (824, 3, 'Valaistus, kohdevalaistus, korostus', NULL, 'Kohdevalaistuksella korostetaan yksityiskohtaa esim. opastetta ja varoitetaan kompastumis- ja törmäämisvaarasta esim. aukeavasta ovesta tai tasoerosta.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (956, 3, 'Valaistus, luiska, portaat, kohdevalaistus', 'Luiskan ja portaiden alun kohdevalaistus auttaa luiskan ja portaan havaitsemista.', 'Valaistusvoimakkuus portaissa ja luiskissa 20 - 50 lx. Kriittisissä kohteissa, kuten tasoerojen yhteydessä, valaistusvoimakkuus 400 - 500 lx (2. lähde).', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (834, 4, 'Valaistus, kulkuväylä, pinta, häikäisy', NULL, 'Kulkuväylän pinta ei saa heijastaa keino- eikä päivänvaloa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (228, 4, 'Valaistus, kulkuväylä, sijoitus', NULL, 'Kulkuväylän hahmottamista helpottaa samansuuntainen yhtenäinen valaisinlinja.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (385, 1, 'Kaide', NULL, 'Kaide tulee rakentaa, kun putoamiskorkeus ylittää 500 mm ja putoamisen tai harhaanastumisen vaara on olemassa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1220, 1, 'Kaide, korkeus lattiasta', 'Kaiteen korkeus (A), silloin kun putoamiskorkeus on 500-3000 mm. Kaiteen korkeus (B), silloin kun putoamiskorkeus on 3000-6000 mm. Kaiteen korkeus (C), silloin kun putoamiskorkeus on yli 6000 mm. Asunnon parvekkeen /terassin kaidekorkeus (B).', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 1000 mm. Arvo C on vähintään 1200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (207, 1, 'Kulkuväylä, korkeus', 'Kulkuväylän vapaa korkeus (A). Vertailuarvona rakennuksen kiinteän ulokkeen esim. parvekkeen alla oleva vapaa korkeus (B).', 'Arvo A on vähintään 2100 mm. Arvo B on vähintään 2200 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1211, 1, 'Kulkuväylä, mitoitus, julkinen tila', NULL, 'Kulkuväylillä kääntymistilaa ja tiloissa liikkumista mitoittaa sekä ulko- että sisäkäyttöön soveltuvan pyörätuolin pyörähdysympyrä, jonka halkaisija on 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (179, 1, 'Kulkuväylä, turvallisuus', NULL, 'Ajoneuvoliikenne tontilla ei saisi ristetä jalankulkuliikenteen kanssa.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (211, 1, 'Kulkuväylä, törmäysvaara', NULL, 'Kulkuväylällä ei saa olla törmäysvaaraa aiheuttavia rakenteita. Rakenteen törmäysvaara estetään esim. kaiteella ja siitä varoitetaan värillä tai materiaalilla.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1134, 1, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys porrassyöksyn yläreunasta.', 'Arvo A on vähintään 400 mm. Arvo B on vähintään 1500 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1219, 1, 'Liikkumisen tilantarve, oven etäisyys portaasta tai luiskasta', 'Uloskäytävän kulkureitillä olevan oven eteen ja taakse varataan tasanne.Tasanteen pituus A.', 'Arvo A on vähintään 800 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (237, 2, 'Kulkuväylä, istutukset', NULL, 'Kulkuväylän varrella olevien istutusten on oltava myrkyttömiä ja allergiaa aiheuttamattomia. Kasvit on sijoitettava kulkuväylän ulkopuolelle.', '20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (948, 2, 'Kulkuväylä, kalusteet, pollari, väri', NULL, 'Väri- ja tummuuskontrasti varoittaa törmäysvaarasta.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (176, 2, 'Kulkuväylä, opastava, pääovi', NULL, 'Kulkuväylän, joka ohjaa pääovelle, on oltava esteetön ja helposti havaittava.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (224, 2, 'Kulkuväylä, reunakiven korkeus', 'Ajoväylän ja kulkuväylän erottavan reunakiven korkeus (C).', 'Arvo C on välillä 30 ... 40 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (914, 2, 'Kulkuväylä, ulkona, pituuskaltevuus', 'Jalankulkutien suositeltava pituuskaltevuus (C). Pitkillä etäisyyksillä  2. lähde suosittelee pituuskaltevuudeksi enintään 2,5 %.', 'Arvo C on enintään 5 %.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (197, 2, 'Kulkuväylä, ulkona, sivukaltevuus', 'Kevytliikenteen väylän sivukaltevuus (C). Suurempi kaltevuus aiheuttaa pyörätuolia ohjattaessa jatkuvia korjausliikkeitä.', 'Arvo C on enintään 2 %.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1052, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kainalosauvojen käyttäjän tilantarve leveyssuunnassa (A). Vertailuarvona (B) tilantarve kyynärsauvojen kanssa.', 'Arvo A on vähintään 850 mm. Arvo B on vähintään 950 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1088, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Itsenäisen pyörätuolin käyttäjän tilantarve (A) leveyssuunnassa.', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1112, 2, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Rollaattorin käyttäjän tilantarve (A) leveyssuunnassa ja pituussuunnassa (B). Vertailuarvona tilantarve leveyssuunnassa (C) 2. lähteestä.', 'Arvo A on vähintään 600 mm, mutta arvoksi suositellaan vähintään 800 mm. Arvo B on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1100 mm. Arvo C on vähintään 850 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (191, 2, 'Liikkumisen tilantarve, kääntyminen 180 astetta, rollaattori', 'Käytävän leveys (A).', 'Arvo A on vähintään 1000 mm, mutta arvoksi suositellaan vähintään 1100 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (250, 2, 'Liikkumisen tilantarve, kääntyminen 360 astetta, rollaattori', 'Rollaattorin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1300 mm. Arvo B on vähintään 1200 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1216, 2, 'Liikkumisen tilantarve, oven etäisyys alasjohtavasta portaasta tai luiskasta', 'Oven karmin etäisyys alasjohtavasta portaasta tai luiskasta. Portaan sivun suuntaisen oven (A) etäisyys ja vastapäisen oven (B) etäisyys portaasta tai luiskasta.', 'Arvo A on vähintään 700 mm. Arvo B on vähintään 2000 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1046, 2, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 700 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1041, 2, 'Valaistus, kulkuväylä ulkona', NULL, 'Yleisvalaistusvoimakkuus kulkuväylällä ulkona on 5-30 lx.  Risteyksien, portaiden ja luiskien kohdalla valaitusvoimakkuus on 20-50 lx.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (233, 3, 'Kalusteet, istumakorkeus', NULL, 'Istumakorkeus on tavallisesti 450 mm. Normaalia korkeampi istumakorkeus (500...550 mm), on sopiva jäykkäpolvisille ja -lonkkaisille. Lapsille ja lyhytkasvuiselle henkilölle sopiva istumakorkeus on 300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1124, 3, 'Kalusteet, turvallisuus', NULL, 'Päänkorkeudella olevat kalusteet esim. naulakot suunnitellaan ja sijoitetaan siten, ettei näkövammainen ole vaarassa lyödä niihin päätään.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1016, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Käsien- ja ylävartalon toimintarajoitteinen henkilö ulottuu helposti hartialinjansa korkeudelle eli noin 1300-1500 mm lattiasta ja kurottamalla 1700 mm:iin.', '16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1017, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', 'Ulottumiskorkeus (A).', 'Epävarmasti kävelevä ja rollaattoria käyttävän henkilön ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1186, 3, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Naisen ulottumiskorkeus lattiasta on enintään 1600-1800 mm. Miehen ulottumiskorkeus lattiasta on enintään 1750-1900 mm. Vanhuksen ulottumiskorkeus lattiasta on enintään 1500-1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (232, 3, 'Kulkuväylä, kalusteet, sijoitus', NULL, 'Tontilla olevat kalusteet, kuten valaisinpylväät, opasteet, roskalaatikot, pyörätelineet ja penkit on sijoitettava kulkuväylän ulkopuolelle.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (172, 3, 'Kulkuväylä, koordinaatisto', NULL, 'Kulkuväylien on oltava selkeässä koordinaatistossa ja yhtenäisiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (213, 3, 'Kulkuväylä, pinta', NULL, 'Kova, tasainen, kiiltämätön ja märkänäkin luistamaton. Ulkona esim. asfaltti, betoni, kivituhka ja laatta.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1208, 3, 'Kulkuväylä, pinta, kitka', NULL, 'Vierekkäisten pintojen kitkan on oltava lähes samanlainen liukastumisen ja kompastumisen välttämiseksi.', '3,7,8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (215, 3, 'Kulkuväylä, pinta, korkeusero', 'Kohoumien ja kuoppien korkeusero (C) pinnasta.', 'Arvo C on enintään 5 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (216, 3, 'Kulkuväylä, pinta, kuvio', NULL, 'Materiaalikontrasti ja kuviot auttaa suunnistautumista. Tummat kuviot vaalealla yksivärisellä pohjalla ovat selkeimpiä.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (217, 3, 'Kulkuväylä, pinta, kuvio, opastava', NULL, 'Opastava kuvio esim. pitkittäinen pyöristetty kohokuvio, jonka korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (218, 3, 'Kulkuväylä, pinta, kuvio, varoittava', NULL, 'Väri-, tummuus- ja materiaaliero ympäröivään pinnoitteeseen. Alkamiskohta 1200 mm ennen vaaraa, esim. ennen ajoväylää. Pintakuvio esim. pallokalotin muotoiset nystyrät,joiden korkeus on 5 mm ja leveys 25 mm. Kuvioiden väli k/k 100 mm.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (214, 3, 'Kulkuväylä, pinta, saumojen leveys', 'Sauman leveys (C).', 'Arvo C on enintään 5 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (895, 3, 'Kulkuväylä, pinta, väri', NULL, 'Kulkuväylän tai yksityiskohdan (esim. jalkalistan) väri- ja tummuuskontrasti auttaa suunnistautumista ja varoittaa vaarasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (171, 3, 'Kulkuväylä, pituus', NULL, 'Helppokäyttöinen, vähäisellä käsivoimalla toimiva yksiotesekoitin. Sekoittimessa veden lämpötilaa säädetään termostaatilla, painetta pressostaatilla ja lämmönsäädön rajoittimella estetään liian kuuman veden tahaton käyttö.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (915, 3, 'Kulkuväylä, pituuskaltevuus lepotasoilla ja kulkuväylän kääntyessä', 'Kaltevuusprosentti (C).Käännöskohdissa tasanne on vaakasuorassa ( 2. lähde).', 'Arvo C on enintään 2 %.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (177, 3, 'Kulkuväylä, päättyminen', NULL, 'Kulkureitti ei saa johtaa umpikujaan.', '3,7,11,14,15,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (219, 3, 'Kulkuväylä, sadevesikaivo, kansi', NULL, 'Kaivon kansi, jossa on pitkänomaiset raot (esim.10x20 mm), tulisi asettaa niin, että raon pidempi sivu (20mm) on kohtisuorasti liikenteen pääkulkusuuuntaan.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (221, 3, 'Kulkuväylä, sadevesikaivo, reunan korkeus', 'Kannen reunan korkeusero (C) ympäröivän pinnan kanssa.', 'Arvo C on enintään 5 mm.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (239, 3, 'Kulkuväylä, tasoero', NULL, 'Kulkuväylän tasoeroihin suunnitellaan luiska ja loivat portaat, jotka soveltuvat hyvin askelrytmiin. Keppien tai sauvojen kanssa kulkevan on usein turvallisempaa kulkea loivia portaita kuin luiskaa pitkin.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (238, 3, 'Kulkuväylä, ulkona', NULL, 'Kulkuväylä on pidettävä puhtaana; vesilammikot ja lumi eivät saa haitata liikkumista. Irtohiekka kovalla pinnalla aiheuttaa liukastumisriskin.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1051, 3, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Kävelykepin käyttäjän tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 650 mm, mutta arvoksi suositellaan vähintään 750 mm.', '8');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (940, 3, 'Liikkumisen tilantarve, automaattiovi aukeaa kohti, ulkopyörätuoli', 'Tilantarve ulko-oven oven edessä  (A), silloin kun tasanteen syvyys on mitta (B). Vieressä alas johtava porras tai luiska. Jos oven edessä on alasjohtava porras, tarvitaan tilaa enemmän kuin 2000 mm.Oviaukon vapaa leveys 900 mm.', 'Arvo A on vähintään 2000 mm. Arvo B on vähintään 2700 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (865, 3, 'Liikkumisen tilantarve, avustajan kanssa', 'Tilantarve avustajan kanssa (A). Vertailuarvona liikkumisen tilantarve (B), silloin kun avustettava tukee kaiteeseen.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 1300 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (192, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, kulman avartaminen', NULL, 'Kulkuväylän (leveys  900 mm) kääntymiskulmaa avarretaan niin, että kulman mitat ovat 1150 x 1150 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (251, 3, 'Liikkumisen tilantarve, kulkuväylä kääntyy 90 astetta, leveys', 'Kulkuväylän leveys (A) ja (B) kulkuväylän kääntyessä.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (890, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 350 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1044, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila (C).', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 300 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1113, 3, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Rollaattorin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B). Oviaukon vapaa leveys 750 mm. Oven sulkemiseen tarvittava tila oven takana (C).', 'Arvo A on vähintään 1100 mm, mutta arvoksi suositellaan vähintään 1200 mm. Arvo B on vähintään 500 mm. Arvo C on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1047, 3, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Oven edessä (A) tarvittava tila.', 'Arvo A on vähintään 800 mm.', '11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1092, 3, 'Mitoitus, katsekorkeus', NULL, 'Pyörätuolissa istuvan naisen katsekorkeus on 1150 mm:n korkeudella ja miehen katsekorkeus on 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1183, 3, 'Mitoitus, katsekorkeus', 'Seisovan henkilön katsekorkeus (C).', 'Arvo C on välillä 1400 ... 1700 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (718, 3, 'Opaste, korkeus lattiasta', 'Opasteen sijoituskorkeus (C), myös koho- ja syvennyskuvio-opasteet.', 'Arvo C on välillä 1400 ... 1600 mm.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (717, 3, 'Opaste, materiaali', NULL, 'Pintamateriaali kiiltämätön ja heijastamaton, lasipintaisessa opasteessa heijastamaton lasi.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (720, 3, 'Opaste, teksti', NULL, 'Isoilla kirjaimilla kirjoitetaan alkukirjaimet, lyhyet sanat sekä koho- ja syvennyskirjaimet. Pienillä kirjaimilla kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (719, 3, 'Opaste, teksti, kirjaintyyppi', NULL, 'Suurehko ja selvä, esim. Helvetica Medium.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (721, 3, 'Opaste, teksti, kirjaintyyppi, pienet kirjaimet', NULL, 'Pienellä kirjoitetaan pitkät sanat ja tekstit.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (704, 3, 'Opaste, teksti, korkeus', 'Tekstin korkeus (C) opasteissa, jotka luetaan läheltä.', 'Arvo A on vähintään 15 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (705, 3, 'Opaste, teksti, korkeus, sijainti-tai suuntakilpi', 'Tekstin korkeus (A) sijaintia ja suuntaa osoittavissa opasteissa, jotka luetaan muutaman metrin etäisyydeltä.', 'Arvo C on välillä 25 ... 40 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (722, 3, 'Opaste, väri, kontrasti', NULL, 'Tekstin ja symbolin väri- tai tummuuskontrasti taustaa vasten. Parhaiten erottuu musta teksti valkoisella pohjalla.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (231, 3, 'Pihakalusteet', NULL, 'Kalusteen on oltava tukeva ja paikallaan pysyvä ja siinä ei saa olla teräviä särmiä ja kulmia. Materiaali ei saa aiheuttaa allergiaa.', '3,7,8,20');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1068, 3, 'Pihakalusteet, penkki, käsinojat', NULL, 'Penkissä on oltava etureunaan ulottuvat käsinojat, joista saa tukea istuutuessa ja noustaessa ylös.', '8,11,14,15,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (943, 3, 'Pihakalusteet, penkkien välinen etäisyys', 'Penkkejä tulisi olla tasaisin välein (C) kulkuväylän varrella. Pihalla penkkejä sijoitetaan etenkin sisäänkäynnin lähelle.', 'Arvo C on enintään 25 m.', '8,11');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (819, 3, 'Valaistus, opaste, ulkopuolelta', NULL, 'Opasteen valaisu helpottaa sen löytymistä. Häikäisemätön valo, josta ei muodostu häiritseviä varjoja.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (664, 3, 'Yhteistilat, pyykinkuivaus', NULL, 'Pesulassa on joko kuivauskaappi tai -rumpu tai pesulan yhteydessä on erillinen kuivaushuone. Mahdollisuus pyykin kuivaamiseen ulkona.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (667, 3, 'Yhteistilat, pyykinkuivaus, telineet, alusta', NULL, 'Pyykinkuivaus- ja tomutustelineet sijoitetaan alustalle, joka on vaakasuora ja kovapintainen.', '3,7,8,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (706, 4, 'Kalusteet, turvallisuus', NULL, 'Kalusteissa ei saa olla teräviä kulmia, ulkonemia eikä helposti irtoavia osia.', '26');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (552, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Henkilölle, jolla on heikentynyt liikkumis- ja koordinointikyky ja kumartumis- ja tasapainovaikeuksia, ulottumiskorkeus lattiasta on enintään 350- 500 mm.', '8,11,16');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1015, 4, 'Kalusteet, ulottumiskorkeus lattiasta, alaraja', NULL, 'Pyörätuolissa istuva mies ulottuu helposti etualaviistoon 400 mm:n korkeudelle lattiasta, kurottamalla enintään 250 mm:n korkeudelle. Pyörätuolissa istuva nainen ulottuu helposti etualaviistoon 450 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (557, 4, 'Kalusteet, ulottumiskorkeus lattiasta, yläraja', NULL, 'Pyörätuolissa istuva henkilö ulottuu helposti yläviistoon 1100 mm:n korkeudelle lattiasta ja kurottamalla, mikäli kädet toimivat normaalisti, enintään 1350 mm:n korkeudelle.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (236, 4, 'Kulkuväylä, kalusteet, kalustevyöhyke', NULL, 'Kalustevyöhyke tulisi erottaa kulkuväylästä poikkeavalla pintamateriaalilla ja/tai -värityksellä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (950, 4, 'Kulkuväylä, kalusteet, pollari, etäisyys toisistaan', 'Pollarit sijoitetaan tasaisin välein (A) hahmottamisen helpottamiseksi ja törmäysvaaran vähentämiseksi.', 'Arvo A on vähintään 900 mm.', '3,7,11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (235, 4, 'Kulkuväylä, kalusteet, pollari, korkeus', 'Esteen korkeus (A). Este ulottuu mieluummin vyötärön kuin polven korkeudelle.', 'Arvo A on vähintään 1000 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (894, 4, 'Kulkuväylä, rakenteiden alareunan korkeus', NULL, 'Kulkuväylälle rajoittuvan rakenteen esim. ulokkeen alareuna ei saa olla liian korkealla, jotta näkövammainen pystyisi sen havaitsemaan. Korkeus maasta tai lattiasta noin 300 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (222, 4, 'Kulkuväylä, reuna', NULL, 'Suunnistautumista helpottaa kulkuväylän rajoittuminen selvästi poikkeavan tuntuiseen pintaan.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (223, 4, 'Kulkuväylä, reuna, kaide', NULL, 'Kulkuväylän reunalla kulkuesteaitana voi olla esim. avokaide, jossa on vaakajohteet sekä 900 mm:n (käsijohde) että 100 mm:n korkeudella.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1212, 4, 'Kulkuväylä, reuna, korkeus', NULL, 'Turvallisessa kulkuväylässä on korotetun reunuksen lisäksi kaide tai istutuksia. Tai reunus on riittävän korkea (noin 600 mm), ettei siitä aiheudu kompastumisvaaraa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (180, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Valkoisen kepin käyttäjän tilantarve leveysuunnassa (A). Henkilön kulkuleveys on 600 mm ja lisäksi tarvitaan 150 mm tilaa kepille molemmilla puolilla.Tilantarve opaskoiran kanssa (B).', 'Arvo A on vähintään 900 mm, mutta arvoksi suositellaan vähintään 1000 mm. Arvo B on vähintään 1100 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1050, 4, 'Liikkumisen tilantarve, apuvälineen kanssa', 'Avustetun pyörätuolin käyttäjän tilantarve leveys- ja pituussuunnassa (A ja B).', 'Arvo A on vähintään 800 mm, mutta arvoksi suositellaan vähintään 900 mm. Arvo B on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (194, 4, 'Liikkumisen tilantarve, kalusteiden ohitus', 'Tilantarve kalusteen vieressä (A). Vertailuarvona tilantarve (B), silloin kun kaluste on matala.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 800 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (833, 4, 'Liikkumisen tilantarve, kulkuväylä kääntyy, yhteenlaskettu leveys', 'Risteävien kulkuväylien a ja b yhteen laskettu leveys (A). Kulkusuunnan kääntyessä 90 astetta, a:n ja b:n leveys vähintään 900 mm.', 'Arvo A on vähintään 2300 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (908, 4, 'Liikkumisen tilantarve, kääntyminen 180 astetta, ulkopyörätuoli', 'Käytävän leveys (A). Suositusmitta mahdollistaa esteettömän kääntymisen.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 2000 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (903, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, pyörätuoli avustettu', 'Avustetun pyörätuolin pyörähdysympyrän halkaisija (A). Suositusmitta mahdollistaa esteettömän kääntymisen. Vertailuarvona pyörähdysympyrä (B) 2. lähteestä.', 'Arvo A on vähintään 1500 mm, mutta arvoksi suositellaan vähintään 1800 mm. Arvo B on vähintään 1750 mm.', '15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (186, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkopyörätuoli', 'Ulkopyörätuolin pyörähdysympyrän halkaisija (A).', 'Arvo A on vähintään 1800 mm, mutta arvoksi suositellaan vähintään 2300 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (901, 4, 'Liikkumisen tilantarve, kääntyminen 360 astetta, ulkosähköpyörätuoli', 'Pyörähdysympyrän halkaisijan (A) vertailuarvona halkaisija (B) 2. lähteestä.', 'Arvo A on vähintään 2300 mm. Arvo B on vähintään 2500 mm.', '14');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (941, 4, 'Liikkumisen tilantarve, liukuovi', 'Tilantarve liukuoven edessä (A) ja avaamispuolella oven vieressä (B). Tila ei riitä pyörätuolilla kääntymiseen ja oven sulkemiseen, vaan lisätilaa tarvitaan oven vierestä.', 'Arvo A on vähintään 1200 mm. Arvo B on vähintään 200 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (874, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti', 'Pyörätuolin tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 400 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1076, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta edestä, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1700 mm. Arvo B on vähintään 650 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1077, 4, 'Liikkumisen tilantarve, ovi aukeaa kohti, tulosuunta sivusta, ulkopyörätuoli', 'Tilantarve oven edessä (A) silloin kun oven vieressä on tilaa mitta (B).', 'Arvo A on vähintään 1500 mm. Arvo B on vähintään 850 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (873, 4, 'Liikkumisen tilantarve, ovi aukeaa poispäin', 'Pyörätuolin tilantarve oven edessä (A) ja oven vieressä (B).', 'Arvo A on vähintään 1200 mm, mutta arvoksi suositellaan vähintään 1400 mm. Arvo B on vähintään 300 mm, mutta arvoksi suositellaan vähintään 450 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (189, 4, 'Liikkumisen tilantarve, pyörätuoli muun henkilöliikenteen rinnalla', 'Kulkuväylän leveys (A) silloin kun pyörätuolin vieressä kulkee toinen henkilö. Vertailuarvona  tilantarve (B) 2. lähteestä.', 'Arvo A on vähintään 1400 mm. Arvo B on vähintään 1500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (190, 4, 'Liikkumisen tilantarve, pyörätuolien kohdatessa', 'Kulkuväylän leveyden (A) vertailuarvona leveys (B) 2. lähteestä.', 'Arvo A on vähintään 1600 mm. Arvo B on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (907, 4, 'Liikkumisen tilantarve, ulkosähköpyörätuolien kohdatessa', 'Tilantarve esitetty kulkuväylän leveyden (A) avulla.', 'Arvo A on vähintään 1800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1093, 4, 'Mitoitus, ulottuminen korkeussuunnassa', 'Korkeus lattiasta (C), johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta. Mitat henkilölle jolla on hyvä käsivarsien liikkuvuus. Kurottumalla ulottuvuus lisääntyy 200 -300 mm.', 'Arvo C on välillä 700 ... 1200 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1184, 4, 'Mitoitus, ulottuminen sivusuunnassa', 'Etäisyys sivusuunnassa (C) pyörätuolin reunasta mitattuna, johon pyörätuolissa istuva henkilö ulottuu kumartumatta ja kurkottamatta.', 'Arvo C on enintään 500 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (731, 4, 'Opaste, sijoitus, kartat', NULL, 'Rakennuksessa suunnistautumista opastavat kohokartat sijoitetaan hyvin näkyville, esimerkiksi ulko-oven viereen.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (725, 4, 'Opaste, teksti, kohokirjain, korkeus', 'Kirjaimen korkeus pinnasta (A) ja kirjaimen korkeus (C). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo A on vähintään 1 mm. Arvo C on välillä 15 ... 40 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (716, 4, 'Opaste, teksti, korkeus, nimikilpi', 'Tekstin korkeus (C) nimikilvissä, jotka luetaan 1..3 m etäisyydeltä.', 'Arvo C on välillä 70 ... 100 mm.', '7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (726, 4, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', NULL, 'Pyörätuolin käyttäjän pistekirjoitusopasteen korkeus lattiasta on 1100 mm.', '3,7,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (234, 4, 'Pihakalusteet, penkki, tila pyörätuolille ja rollaattorille', 'Penkin vieressä tarvittavan tilan leveys (A) pyörätuolille ja rollaattorille, silloin kun henkilö istuu penkillä.', 'Arvo A on vähintään 900 mm.', '11,14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (947, 4, 'Pihakalusteet, pöytä, korkeus', 'Pöydän korkeus (C) maanpinnasta.', 'Arvo C on välillä 750 ... 800 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (946, 4, 'Pihakalusteet, pöytä, polvitilan mitoitus', 'Mitat on esitetty leveyden (A), syvyyden (B) ja korkeuden  (C) avulla.', 'Arvo A on vähintään 900 mm. Arvo B on vähintään 600 mm. Arvo C on vähintään 670 mm.', '14,15');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (834, 4, 'Valaistus, kulkuväylä, pinta, häikäisy', NULL, 'Kulkuväylän pinta ei saa heijastaa keino- eikä päivänvaloa.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (228, 4, 'Valaistus, kulkuväylä, sijoitus', NULL, 'Kulkuväylän hahmottamista helpottaa samansuuntainen yhtenäinen valaisinlinja.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (818, 4, 'Valaistus, opaste, sisäpuolelta', NULL, 'Sisältäpäin valaistussa opasteessa vaalea teksti tummalla pohjalla häikäisyn estämiseksi.', '3,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (665, 4, 'Yhteistilat, pyykinkuivaus, telineet', NULL, 'Pyykinkuivaus- ja tomutustelineet asennetaan eri korkeuksille, niin että niihin ulottuu myös lyhytkasvuinen henkilö ja pyörätuolin käyttäjä. Pyykin ripustaminen kuivumaan mahdollista myös avustajan kanssa.', '3,7,8,11,14,15,16,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (766, 5, 'Kalusteet, väri', NULL, 'Kalusteiden väri- tai tummuuskontrasti, siten että jokainen pinta, kaluste tai yksityiskohta erottuu ympäröivistä pinnoista tai taustasta.', '7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1089, 5, 'Liikkumisen tilantarve, opaskoiran kanssa', 'Opaskoiraa käyttävän henkilön tilantarve leveyssuunnassa (A).', 'Arvo A on vähintään 1100 mm.', '3');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (724, 5, 'Opaste, kieli', NULL, 'Opasteisiin soveltuu selkokieli, jota ymmärtävät kaikki. Erityisesti sitä tarvitsevat kehitys- ja monivammaiset sekä esim. viittomakieltä äidinkielenään käyttävät.', '1,7,25');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (729, 5, 'Opaste, merkinantolaite', NULL, 'Äänimerkkien lisäksi esim. ovipuhelimiin ja merkinantolaitteisiin suositellaan myös visuaalisia ja taktuaalisia eli tuntoaistiin perustuvia merkkejä.', '1,3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1179, 5, 'Opaste, teksti, kohokirjain, poikkileikkaus', NULL, 'Kohokirjaimen poikkileikkaus on kolmioimainen, jonka yläsärmää pitkin sormea voi liuuttaa. Yläsärmä ei saa olla terävä.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1043, 5, 'Opaste, teksti, pistekirjoitus, korkeus lattiasta', 'Opasteen korkeus (C) lattiasta.', 'Arvo C on välillä 1300 ... 1400 mm.', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1180, 5, 'Opaste, teksti, syvennyskirjain, korkeus', NULL, 'Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta. Kirjaimen syvyys tai korkeus pinnasta 1 mm ja kirjaimen korkeus 30-60 mm. Kirjaimen korkeus 15-40 mm (2. lähde).', '3,7');
INSERT INTO guidelines (id, guidelineType, name, description1, description2, profiles) VALUES (1181, 5, 'Opaste, teksti, syvennyskirjain, leveys', 'Kirjaimen leveys eli syvennysuran leveys (A). Näkövammainen lukee tunnusteluetäisyydellä olevissa opasteissa koho-tai syvennyskuviosta.', 'Arvo C on välillä 6 ... 10 mm.', '3,7');